/*INFOBOX (the things that contain polls and room banners and other things.)*/

.infobox {
    border: 1px solid silver;
    border-radius: 5px;
    background: transparent;
    color: white;
}

.infobox div button {
    border: 1px solid silver;
    background: transparent;
    color: white;
    border-radius: 5px;
    cursor: crosshair;
}

/*CHAT TEXT AND BACKGROUND*/

.dark .chat-log{
