    color: white;
}

.userlist-minimized .userlist-count:hover, 
.userlist-maximized .userlist-count:hover {
    background: #282828 !important;
    transition: 0.3s;
}

/*PM BOX*/

.challenge {
    border: none;
    background: darkorange;
    color: var(--white1);
}

.dark .pm-buttonbar button {
    color: white;
    border: none;
    background: transparent !important;
    cursor: crosshair;
    font-weight: bold;
    transition: 0.3s;
}

.dark .pm-buttonbar button:hover {
    color: red;
    border: none;
    background: transparent !important;
    cursor: crosshair;
    font-weight: bold;
    transition: 0.3s;
}

button[name="acceptChallenge"], button[name="rejectChallenge"], button[name="makeChallenge"], button[name="dismissChallenge"] {
    color: white;
    border: none;
    background: transparent;
    cursor: crosshair;
    transition: 0.3s;
}

button[name="acceptChallenge"]:hover, button[name="rejectChallenge"]:hover, button[name="makeChallenge"]:hover, button[name="dismissChallenge"]:hover {
    color: red;
    border: none;
    background: transparent;
    cursor: crosshair;
    transition: 0.3s;
}

.pm-window h3 .minimizebutton, .pm-window h3 .closebutton {
    color: grey !important;
    cursor: crosshair;
    transition: 0.3s;
}

.pm-window h3 .minimizebutton:hover, .pm-window h3 .closebutton:hover {
    color: red !important;
    text-shadow: 0px 0px 7px red;
    cursor: crosshair;
    transition: 0.3s;
}

/*(↓stolen from DunDealStylish's Smoking Black/Pyrotoz's theme)*/

.pm-log em{
        color: white;
}
 
.pm-window h3, .pm-window.focused h3{
        color: white;
    background: linear-gradient(#111 10%, black 90%);
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
        border: none;
    cursor: crosshair;
    transition: 0.3s;
}

.pm-window h3.pm-notifying {
    color: black;
    background: linear-gradient(tan 10%, #998366 90%);
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
        border: none;
    cursor: crosshair;
}

.pm-window h3.pm-notifying:hover {
    color: black;
    background: linear-gradient(tan 10%, #998366 90%);
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
        border: none;
    cursor: crosshair;
    text-shadow: 0px 0px 7px black;
}

.pm-window h3:hover, .pm-window.focused h3:hover {
        color: grey;
    background: linear-gradient(#111 10%, black 90%);
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
    border: none;
    cursor: crosshair;
    text-shadow: none;
    transition: 0.3s;
}

.dark .pm-log{
        background: #000000bd;
        border: none;
}
 
.dark .pm-log-add{
        border: none;
    background: #202020;
}

