    color: red;
    border: none;
    background: transparent !important;
    cursor: crosshair;
    font-weight: bold;
    transition: 0.3s;
}

.ps-popup button {
    color: white;
    border: none !important;
    background: transparent;
    cursor: crosshair;
    font-weight: bold;
    transition: 0.3s;
    box-shadow: none !important;
}

.ps-popup button:hover {
    color: red !important;
    border: none;
    background: transparent;
    cursor: crosshair;
    font-weight: bold;
    transition: 0.3s;
}

.dark .folderButtonOpen.folderButtonOver, .dark .folderButton {
    background: transparent !important;
}

.ps-popup a.button, .ps-popup a.button:hover, ps-popup a.button:focus, ps-popup a.button:active  {
    border: 1px solid darkgreen;
    background: transparent;
    box-shadow: none;
    cursor: crosshair;
    margin-top: 3px;
}

.ps-popup a.roomtab.button.cur {
    border: 1px solid blue;
    background: transparent;
    box-shadow: none;
    cursor: crosshair;
}

button.closebutton {
    cursor: not-allowed ;
    transition: 0.3s;
    color: silver;
    border: none !important;
}

button.closebutton:hover {
    cursor: not-allowed ;
    transition: 0.3s;
    color: red;
    text-shadow: 0px 0px 6px red;
}

