    transition: 0.3s;
}

label.checkbox:hover {
    color: white;
    background: #000000c2 !important;
    border: 1px solid darkgreen;
    font-size: 14px;
    box-shadow: 0px 0px 13px darkgreen;
    transition: 0.3s;
}

