.statbar
  {
    background-image: url(http://i.imgur.com/r8456dy.png);
    background-size: 162px 44px;
  }

  .statbar .hpbar
  {
    position: relative;
    border: 1px solid #777777;
    background: #161616;
    padding: 1px;
    height: 8px;
    margin: 0 0 0 0;
    width: 151px;
    border-radius: 0px !important;
    transform: skewx(-20deg);
  }

  .status
  {
    transform: skewx(20deg);
  }

  .statbar .hpbar .hptext
  {
    position: absolute;
    background: #777777;
    color: #EEEEEE;
    text-shadow: #000000 0 1px 0;
    font-size: 9px;
    width: 32px;
    height: 12px;
    top: -1px;
    text-align: center;
  }

  .statbar .hpbar .hp
  {
    height: 4px;
    border-top: 2px solid #46E930;
    background: #46E930;
    border-bottom: 2px solid #46E930;
    border-right: 1px solid #46E930;
    border-radius: 0px !important;
  }

  .statbar .hpbar .hp-yellow
  {
    border-top-color: #f5d538;
    background-color: #f5d538;
    border-bottom-color: #f5d538;
    border-right-color: #f5d538;
  }

  .statbar .hpbar .hp-red
  {
    border-top-color: #ee4928;
    background-color: #ee4928;
    border-bottom-color: #ee4928;
    border-right-color: #ee4928;
  }

  .statbar .hpbar .prevhp
  {
    background: #3a3a3a;
    height: 8px;
    border-radius: 0px !important;
  }

  .statbar .hpbar .prevhp-yellow
  {
    background-color: #3a3a3a;
  }

  .statbar .hpbar .prevhp-red
  {
    background-color: #3a3a3a;
  }

  .lstatbar .hpbar .hptext
  {
    left: -32px;
  }

