style.css:
@-moz-document url-prefix("https://www.eldarya.") {
body {background-color: #000;}
#season-switcher {background-color: #111;}
#menu-bar {background: rgba(0, 0, 0, 0.35) !important;}

.page-main-container:not(.mall-main-container):not(.appearance-interface-container), #container .episode-main-container, #container .library-main-container {
    background-image: url("https://www.eldarya.es/static/img/home/treasure-hunt/bg-temple.jpg") !important;
}

.home-interface-container #right-bg, .appearance-interface-container #right-bg, #layout-2col #layout-2col-right, .attic-container #right-bg {
    background: #222;
}

#layout-2col #layout-2col-right {
    background-color: rgba(34, 34, 34, .9);
}

#marketplace-sell-form {
    line-height: 1em;
    z-index: 2;
    background-color: #111;
    color: #fff;
}

.home-interface-container #right-bg::before, .appearance-interface-container #right-bg::before, #layout-2col-left::before, .attic-container #right-bg::before {
    filter: brightness(0.13);
}

#currencies .currency {
    background-color: #222;
    box-shadow: 0 0 6px rgba(255, 255, 255, 0.5);
}

.flavr-container.new-layout-popup .crush-choice {
    background: #000;
}

.section-title {
    color: #eee;
}

/* Episodio */
.carousel-container .episode .episode-info, .library-main-container .picture-box .episode-info {
    background-color: rgba(34, 34, 34, 0.6);
    box-shadow: 0 0 8px 0 rgba(255, 255, 255, 0.56);
    color: #eee;
    line-height: 1em;
}

.season-s2 .carousel-container .episode .episode-info .pictures.completed {
    color: #fff;
}
/* Botones y test color general */
.season-s1 .carousel-container .episode .episode-info .pictures, 
.season-s1 .library-main-container .picture-box .counter:not(.completed) {
    filter: hue-rotate(90deg);
}

.season-s2 .carousel-container .episode .episode-info .pictures, 
.season-s2 .library-main-container .picture-box .counter:not(.completed) {
    filter: hue-rotate(340deg);
}

.season-s1 .carousel-container .episode .episode-info .summary {
    filter: hue-rotate(90deg);
}

.season-s2 .carousel-container .episode .episode-info .summary {
    filter: hue-rotate(340deg);
}

.season-s1 .carousel-container .episode .episode-info .summary:hover {
    filter: hue-rotate(90deg);
}

.season-s2 .carousel-container .episode .episode-info .summary:hover {
    filter: hue-rotate(340deg);
}

.season-s1 .fancybox-wrap .fancybox-close, 
.season-s1 .fancybox-wrap .nav-box-next, 
.season-s1 .fancybox-wrap .nav-box-prev {
    filter: hue-rotate(90deg);
}

.season-s2 .fancybox-wrap .fancybox-close, 
.season-s2 .fancybox-wrap .nav-box-next, 
.season-s2 .fancybox-wrap .nav-box-prev {
    filter: hue-rotate(340deg);
}

.season-s1 .flavr-button, 
.season-s1 .nl-button:not(.gold-button):not(.maana-button):not(button), 
.season-s1 #pet-return, 
.season-s1 .eggs-interface #select-layout, 
.season-s1 #player-level-bar {
    filter: hue-rotate(90deg);
}

.season-s2 .flavr-button, 
.season-s2 .nl-button:not(.gold-button):not(.maana-button):not(button), 
.season-s2 #pet-return, 
.season-s2 .eggs-interface #select-layout, 
.season-s2 #player-level-bar {
    filter: hue-rotate(340deg);
}


.season-s1 .flavr-button:not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s1 .flavr-button.close:not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s1 .nl-button:not(.gold-button):not(.maana-button):not(button):not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s1 #pet-return:hover {
    filter: brightness(1.05) contrast(1.1) hue-rotate(90deg);
}

.season-s2 .flavr-button:not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s2 .flavr-button.close:not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s2 .nl-button:not(.gold-button):not(.maana-button):not(button):not(.disabled):not(.gray):not([disabled]):not(.inactive):not(.active):hover, 
.season-s2 #pet-return:hover {
    filter: brightness(1.05) contrast(1.1) hue-rotate(340deg);
}

/* Forzar botones [<button>] */

.season-s2 .replay-free-button, .season-s2 .replay-narrative-button {
    filter: hue-rotate(340deg);
}

.season-s2 .replay-free-button:hover, .season-s2 .replay-narrative-button:hover {
	filter: brightness(1.05) contrast(1.1) hue-rotate(340deg) !important;
}

.season-s1 .pet-action-button, .season-s2 #cancel-button, #avatar-actions button, #wear-outfit {
    filter: hue-rotate(90deg);
}

.season-s2 .pet-action-button, .season-s2 #cancel-button, #avatar-actions button, #wear-outfit {
    filter: hue-rotate(340deg);
}

.season-s1 .pet-action-button:hover, .season-s2 #cancel-button:hover {
    filter: brightness(1.05) contrast(1.1) hue-rotate(90deg) !important;
}

.season-s2 .pet-action-button:hover, .season-s2 #cancel-button:hover {
    filter: brightness(1.05) contrast(1.1) hue-rotate(340deg) !important;
}

.seasons-s2 #explore-button {
    filter: hue-rotate(340deg) !important;
}


/* Profile */
#player-info-name-change {
    line-height: 20px;
    padding-top: 3px;
}

#player-stats {
    background-color: rgba(34, 34, 34, .7);
	box-shadow: 0 0 10px 3px rgba(150, 150, 150, 0.3);
    color: #fff;
    line-height: 1em;
}

/* Pet */

#pet-resources {line-height: 1em;}

#right-container-inner #title-container p {
    color: #fff;
}

#map-container .map-location:not(.active) {
    filter: invert(.85);
}

#map-container .map-location-tooltip .tooltip-bg {
    background-image: url("https://1.bp.blogspot.com/-TrjswN1LvIw/YB82Hm3VAcI/AAAAAAAAAdY/i9eibpvoYYEXlEFC9djugmUO9MRpbQSFQCLcBGAsYHQ/s841/right-container.png");
}

#map-container .map-location-tooltip .map-location-infos {
    background-color: rgba(34, 34, 34, 0.7);
}

#map-container .map-location-tooltip .map-location-name {
    color: #fff;
}

.pet-interface-container #treasure-hunt-interface .lens {
    border: 2px solid rgba(34, 34, 34, 0.7);
}

#map-container .map-location-tooltip .map-location-history {
    background: #222;
    color: #fff;
}

#map-container .map-location-tooltip .energy-required span, #map-container .map-location-tooltip .time-to-explore span {
    color: #fff;
}

#name-container h3, #name-container h4 {
    color: #fff;
}

#pet-infos {
    background: rgba(34, 34, 34, 0.7);
}

#pet-infos .pet-active-days, #pet-infos #luck-text {
    color: #fff;
}

.season-s1 #pet-infos .retractor {filter: hue-rotate(90deg);}
.season-s2 #pet-infos .retractor {filter: hue-rotate(340deg);}


#pet-image {max-height: 590px;}  /* SV UPDATE */

#pending-map-location-data-outer {
    height: 450px;
} /* SV UPDATE */

#result-glow {
    width: 380px !important;
    height: 380px !important;
    background-size: 400px 400px !important;
} /* SV UPDATE */

#treasure-hunt-result-overlay {
    height: 450px;
} /* SV UPDATE */

.flavr-container.use-potion-popup .flavr-outer .flavr-content .flavr-message #potion-selectors .potion-selector.active {
    box-shadow: 0 0 8px rgba(255, 255, 255, 0.56);
}

.season-s1 .resource-count {
    filter: hue-rotate(90deg);
}

.season-s2 .resource-count {
    filter: hue-rotate(340deg);
}

.eggs-interface h2, #incubator #incubator-text #incubator-text-inner p {
    color: #fff !important;
}

#incubator #incubator-text {
    background: url("https://1.bp.blogspot.com/-HtDbR9TuqsU/YB-MtZkqQ_I/AAAAAAAAAeU/rB6pvmjoAWY-AOFBToSb8kZCqt8xv9b3ACLcBGAsYHQ/s316/bg-incubator-text.png");
    
}

.eggs-interface .egg-container {
    background-color: rgba(0, 0, 0, 0.7);
}

.egg-container .egg-item {
    background-color: rgba(0, 0, 0, 0.8);
}

.eggs-interface #search-eggs input {
    background-color: #222;
    color: #fff;
}

.egg-item h3, .egg-item h4, .egg-item p {
