}

.season-s1 .egg-item p em, .season-s1 .egg-container .egg-count, .season-s1 #incubator #incubator-count {
    filter: hue-rotate(90deg);
}

.season-s2 .egg-item p em, .season-s2 .egg-container .egg-count, .season-s2 #incubator #incubator-count {
    filter: hue-rotate(340deg);
}

/* Inventario */
.season-s1 #inventory-menu {filter: hue-rotate(90deg);}
.season-s2 #inventory-menu {filter: hue-rotate(90deg);}

.section-subtitle {color: #fff;}
.inventory-item {background-color: rgba(0, 0, 0, .6);}

.inventory-item:hover {background-color: #444;}

.inventory-quantity {
    background-color: rgba(34, 34, 34, .3);
    color: #fff;
}

/* Biblioteca */
.npc-description h3, .npc-description p, .left-content h1, .left-content em, .left-content p, .diaries-library-container .header .diaries-info {
    color: #fff;
}

.season-s1 .fancybox-wrap .fancybox-skin .tL, 
.season-s1 .fancybox-wrap .fancybox-skin .tR, 
.season-s1 .fancybox-wrap .fancybox-skin .bL, 
.season-s1 .fancybox-wrap .fancybox-skin .bR {
    filter: hue-rotate(90deg);
}


.season-s2 .fancybox-wrap .fancybox-skin .tL, 
.season-s2 .fancybox-wrap .fancybox-skin .tR, 
.season-s2 .fancybox-wrap .fancybox-skin .bL, 
.season-s2 .fancybox-wrap .fancybox-skin .bR {
    filter: hue-rotate(340deg);
}


.pet-description .right-content {background-color: #222;}
.pet-description .right-content ul {color: #fff;}

.carousel-pagination .page-icon::before {
    filter: hue-rotate(90deg);
}

.library-main-container .picture-box .episode-info, .pet-description .right-content, .diaries-library-container .header .diaries-info {
    line-height: 1em;
}

.left-content-second h2 {
    color: #ddd;
    border-bottom: 1px solid #ddd;
}

.baby-info ul li, .adult-info ul li {
    color: #ddd;
}

/* Armario */

.season-s1 .action.action-picto {filter: hue-rotate(90deg);}
.season-s2 .action.action-picto {filter: hue-rotate(340deg);}

.attic-description {
    color: #fff;
}
#avatar-picto-actions {
    position: relative;
    z-index: 2;
}
#wardrobe-menu, #appearance-items {
    position: relative;
    z-index: 1;
}

#wardrobe-menu, #attic-menu {
    filter: hue-rotate(90deg);
}

/* Armario / Tiendas */
.appearance-category, .appearance-item, .appearance-item-group, .mall-category, .mall-product, .mall-variation, .mall-variation.mall-variation-background:not(.selected) {
    background: #111;
}

.appearance-category-name, .mall-category-title, .mall-variations-owned, .mall-product-maana-price, .mall-product-gold-price, .mall-product-purropass-price {
    color: #ddd;
}

.appearance-category, .mall-category-icon {
    filter: hue-rotate(90deg);
}

.appearance-category:hover *, .mall-category:hover div, .mall-variation:hover div, .mall-variation.mall-variation-background.selected div, .mall-product:hover div:not(.tradable-mark) {
    color: #000 !important;
}
.mall-stock-limit, .mall-event-tip, .mall-blackFriday-tip {
    color: #fff !important;
}

.appearance-category:hover, .appearance-item:hover, .appearance-item-group:hover, .mall-category:hover, .mall-product:hover, .mall-variation:hover, .mall-variation.mall-variation-background:hover {
    background-color: #fae1ff;
}
.appearance-items-info {
    background-color: #111;
}

.appearance-items-info .appearance-item-info-name, .appearance-items-info .appearance-item-info-rarity {
    color: #fff !important;
}

/* Minijuegos */
#minigames-list .section-subtitle, #malls-list li h2 {
    background-color: rgba(34, 34, 34, .7);
}

#purrotroc-desc, .need-label {
    line-height: 1em;
}

.flavr-container.new-layout-popup.minigames-rules h2 {
    color: #aaa;
}

/* Tiendas */
#mall-container {
    line-height: 1em;
}

.mall-variation.selected div, .mall-product.selected div {
    color: #000;
}

#mall-menu {
    filter: hue-rotate(90deg);
}

#mall-productDetail-info {
    background-color: #111;
}

#mall-productDetail-info .mall-productDetail-name {
    color: #fff;
}

/* Mercado */
.marketplace-abstract, .pagination, .marketplace-assistance {
    line-height: 1em;
}

.marketplace-assistance, .pagination {
    color: #fff;
}

.season-s1 .pagination {
    filter: hue-rotate(90deg);
}

.season-s2 .pagination {
    filter: hue-rotate(340deg);
}

.marketplace-main-container .page {
    color: #777;
    background-color: rgba(20, 20, 20, .5);
    border: 1px solid #777;
}

.marketplace-abstract .abstract-icon, .marketplace-abstract .abstract-icon > img {
    width: 80px;
    height: 80px;
}

.marketplace-search-items {
    line-height: 1em;
    color: #fff;
}

.marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item {
    height: 215px;
}

#marketplace-itemDetail {
    display: flex;
	bottom: -90px;
	position: absolute;
    min-height: 0;
    z-index: 5;
}

.marketplace-misc-info {
    line-height: 1em;
    color: #fff;
}

#marketplace-zoom {
    top: 70px;
}

.marketplace-itemDetail-name {
    display: none;
}

.marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item {
    background-color: rgba(0, 0, 0, .8);
}

.marketplace-abstract .abstract-icon {
    background-color: #000;
}

.marketplace-search-item:hover, .marketplace-search-item.selected, .marketplace-search-item:hover .abstract-icon, .marketplace-search-item.selected .abstract-icon, .marketplace-itemsForSale-item:hover, .marketplace-itemsForSale-item.selected, .marketplace-itemsForSale-item:hover .abstract-icon, .marketplace-itemsForSale-item.selected .abstract-icon {
    background-color: #350530;
}

.marketplace-abstract .abstract-name, .marketplace-abstract .abstract-type, .abstract-currentPrice, .marketplace-error-message {
    color: #fff;
}

.marketplace-itemsForSale-items li.marketplace-itemsForSale-item {
    height: 125px;
}

/* Pop up */ 
.flavr-container.new-layout-popup .flavr-outer, .fancybox-wrap:not(.no-nl-style) .fancybox-inner {
    background-image: url("https://1.bp.blogspot.com/-DMpURzAflrI/YB89gtWIN_I/AAAAAAAAAd0/fvWBs4oqeYQixy58Am2kn73ZYBpVNZQoACLcBGAsYHQ/s1770/bg-popup-1.png");
}

.flavr-container.new-layout-popup .flavr-outer::before {
    filter: hue-rotate(90deg);
}

.flavr-container .flavr-outer .flavr-content .flavr-message h1 {
    color: #fff;
}

.flavr-container.use-potion-popup .flavr-outer .flavr-content .flavr-message p {
    color: #fff;
}

.flavr-container.feed-pet-popup .flavr-outer .flavr-content .flavr-message p.food-count-text {
    color: #fff;
}

.flavr-container.feed-pet-popup .flavr-outer .flavr-content .flavr-message p {
    color: #fff;
}

.flavr-container.feed-pet-popup .flavr-outer .flavr-content .flavr-message p.stock {
    color: #fff;
}

.flavr-container.new-layout-popup .flavr-fixer .flavr-outer .flavr-content .flavr-message {
    color: #fff;
}

.stock-btn {
    filter: hue-rotate(90deg);
}

.flavr-container.feed-pet-popup .flavr-outer .flavr-content .flavr-message #food-count {
    filter: hue-rotate(90deg);
}

/* Foro */
#forum {
    line-height: 1em;
}

 .pun.breadcrumb, .pun.header, .pun td {
    background-color: rgba(255, 255, 255, 0.7);
}

.pun .inbox, .pun.footbox {
    background-color: rgba(255, 255, 255, 0.8);
}

.icon_offset_new {
    background-color: #fff;
}

.pun .poll_bar {
    background-color: #666;
	height: 12px;
	border-radius: 10px 0 0 10px;
	top: 2px;
	position: relative;
}

.pun.poll_bar_text {
    position: absolute;
	background-color: #666;
	height: 12px;
	display: inline-block;
	margin-top: 2px;
	line-height: 0.9em;
	padding: 0 4px;
	border-radius: 0 10px 10px 0;
	color: #fff;
}

.pun.sondage_result {
    color: #fff;
}

/* Cuenta */
#account-personal-bloc {
    background-color: #222;
    line-height: 1em;
}

#account-left::before {
    filter: brightness(0.14);
}

#account-form-personal .form-row {
    color: #fff;
}

#account-section .form-row > label {
    color: #ddd
}

#account-section input[type="text"] {
    color: #fff;
}

#account-right {
    background-color: rgba(34, 34, 34, 0.9);
}

#messenging-subscription {
    background-color: rgba(255,255,255, .7);
    padding: 15px;
    border-radius: 13px;
}

.account-social-label {
    color: #fff;
}

#player-dropdown-menu #player-infos-container {
    background-color: #111;
}

#player-dropdown-menu, #player-dropdown-menu a {
    color: #fff;
}

#player-dropdown-menu #player-infos-container hr {
    border-bottom: 2px solid rgba(200, 200, 200, .4);
}

/* Mensajeria */
.messaging-main-container, #messaging-conversation, #contact-friends {
    line-height: 1em;
}

.contact-main-container .friend {
    background-color: rgba(10, 10, 10, 0.7)
}

#messaging-conversation-content-bloc, .messaging-warning-message, #messageDetails #infoban, #messaging-answer-content-bloc {
    background: #111;
}

[data-type="0"] #messaging-conversation-content-bloc.group-member {
    background: #222;
}

.messaging-discussion-content, .messaging-warning-message, #messageDetails #infoban, #messaging-answer-content-bloc textarea, .messaging-conversation-content {
    color: #ddd;
}

#messaging-answer-content-bloc textarea {
    border: none;
}

.messaging-conversation-author-pseudo {
    color: rgb(231, 143, 255);
}
/*[[extra]]*/
}

