@-moz-document domain(animeonline.cc) {
body 
{
    background: #353535!important
}

.header, .carou-in, .side-box, .owl-prev, .owl-next, .grid-select, .short, .full-comms, .rels, .sub-text, .fcols-wr, .fsect {
    background-color: #444;
}

body {
    font: 15px 'PT Sans', Arial, Helvetica, sans-serif;
