body {
    font: 15px 'PT Sans', Arial, Helvetica, sans-serif;
    line-height: normal;
    padding: 0;
    margin: 0;
    color: #fff;
    background: #8e8e8e;
    height: 100%;
    width: 100%;
    font-weight: 400;
}

.comm-two {
    color: #cacaca;
    line-height: 20px;
    padding-bottom: 0;
    font-size: 14px;
}

.usp-name {
    margin: -20px -20px 10px -20px;
    padding: 20px;
    border-bottom: 1px solid #f0f0f0;
    background-color: #464646;
}

.usp-meta li:nth-child(2n+1) {
    background-color: #464646;
}

.full-text {
    color: #b1b1b1;
    font-size: 15px;
    line-height: 1.6;
}

.berrors {
    background: #484848;
    color: #ffffff;
    margin: 0 0 20px 0;
    padding: 10px 20px;
}

.search-box button {
    position: absolute;
    right: 0px;
    top: 0px;
    z-index: 10;
    width: 40px;
    padding: 0 !important;
    background-color: #464646 !important;
    color: #fff !important;
}

.fplayer .tabs-sel span {
    display: inline-block;
    padding: 0 15px;
    height: 40px;
    line-height: 40px;
    cursor: pointer;
    font-weight: 700;
    margin-right: -4px;
    background-color: #464646;
}

.cm-item:first-child, .side-bt, .fplayer .tabs-sel span.current, .btn-menu, .to-graph, .to-review {
    background-color: #292929;
    color: #e8e8e8;
}

.cm-item.active, .grid-select div:hover, .grid-select div.current, .error-btn, .error-btn a, .to-view {
    background-color: #464646;
    color: #fff;
}

.button, .pagi-load a, .up-second li a, .usp-edit a, .qq-upload-button, button:not(.color-btn):not([class*=fr]), html input[type="button"], input[type="reset"], input[type="submit"], .meta-fav a, .usp-btn a {
    padding: 0 30px;
    display: inline-block;
    height: 40px;
    line-height: 40px;
    border-radius: 0px;
    cursor: pointer;
    background-color: #292929;
    color: #ececec;
    text-align: center;
    font-weight: 700;
    font-size: 15px;
}

/* ЛОГОТИП*/

.logo
{
    /*display:none*/
}

/* ЛОГОТИП*/

.cm-item {
    display: block;
    height: 40px;
    line-height: 40px;
    padding: 0 15px;
    font-size: 15px;
    float: right;
    background-color: #464646;
}

#gotop {
    position: fixed;
    width: 40px;
    height: 40px;
    line-height: 36px;
    right: 10px;
    bottom: 10px;
    cursor: pointer;
    font-size: 20px;
    z-index: 9998;
    display: none;
    opacity: 0.7;
    background-color: #424242;
    color: #FFF;
    border-radius: 50%;
    text-align: center;
}

.owl-prev, .owl-next {
    width: 40px;
    height: 40px;
    position: absolute;
    top: 85px;
    z-index: 200;
    text-align: center;
    line-height: 40px;
    color: #fff;
    font-size: 18px;
    border-radius: 50%;
}

.ui-dialog-titlebar {
    padding: 0 60px 0 20px;
    background-color: #464646;
    height: 50px;
    line-height: 50px;
}

.obnov-item {
    margin-bottom: 2px;
    background-color: #464646;

}

select {
    height: 40px;
    border: 1px solid #272727;
    background-color: #464646;
}

/* ПРОФИЛЬ */
.us-prof {
    border: 0px solid #ebeef3;
    background-color: #464646;
    margin-bottom: 20px;
    box-shadow: 0 1px 4px rgba(0,0,0,0.2);
}

.usp-left {
    width: 240px;
    padding: 20px;
    background-color: #464646;
    color: #fff;
    text-align: center;
    position: relative;
}

.usp-activ {
    margin: 10px -20px 0 -20px;
    background-color: #292929;
    color: #fff;
}

.usp-activ > div + div {
    border-left: 3px solid #386382;
}

.l-menu {
    position: absolute;
    right: 0px;
    top: 100%;
    width: 200px;
    background-color: #353535;
    padding: 10px 0;
    box-shadow: 0 5px 11px 0 rgba(0,0,0,0.1);
    display: none
}

.logo {
    width: 220px!important;
    height: 70px!important;
    padding-right: 220px;
    background-image: url('data:image/png;base64,')!important;
}
.br-link {
    display: none;
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: #000 url(https://animeonline.cc/templates/vser-light/images/bg.jpg) center 0 no-repeat;
}
.search-box input, .search-box input:focus {
    width: 100%;
    padding: 0 60px 0 15px;
    background-color: #ececec;
    box-shadow: none;
}
    
.ui-dialog {
    background-color: #353535;
    }
  
.ui-dialog .ui-dialog-buttonpane {
    border-top: 1px solid #464646;
    background-color: #464646;
	}
