style.css:
@-moz-document regexp("https://www.speedrun.com/[_a-zA-Z0-9]*/auditlog") {
/* Better Audit Log */

/*
 * REGEX
 *
 * https:\/\/www\.speedrun\.com\/[_a-zA-Z0-9]*\/auditlog
 */

#resultListing div.mt-4 {
	border: #ddd;
	border-radius: 0.5em;
	border-style: solid;
	border-width: 0.1em;
	flex-grow: 1;
	margin-left: 0.7em;
	padding-left: 1rem;
	padding-top: 0.4em;
	position: relative;
	width: 22em;
}

/* Entry header (Date, Responsible user, etc.) */
.mt-4 > p, .mt-4 > h2 {
	color: #ddd !important;
	margin-bottom: 0em;
}

/* Center align categories in the "edit game" settings */
a.nav-item.nav-link {
	text-align: center !important;
}
}

@-moz-document regexp("https:\\/\\/www\\.speedrun\\.com\\/.+") {
/* Better Leaderboards */

/*
 * REGEX
 *
 * https:\/\/www\.speedrun\.com\/.+
 */

/* Left align leaderboard text and player names */
#leaderboarddiv > table > tbody > tr > td,
#leaderboarddiv > table > tbody > tr > th {
	text-align: left;
}

/* Shift rank numbers to the right a bit */
table.reverse-padding-sides.reverse-padding-bottom {
	margin-left: -5px;
	width: calc((100% + 2rem) - 1rem) !important;
}

/* Make the category list extend across the whole screen */
.nav-link {
	flex-grow: 1;
}

.nav-tabs {
	text-align: center;
}

/*
 * NOTE: This the following requires that you use the rounded tabs extension
 * found in `js/round_tabs.js`, otherwise this will result in a blocky page
 * with no rounded corners.
 */

/* Make the leaderboard and category select merge together nicer */
.nav-tabs, .nav-link, .panel.panel-tabbed {
	border-top-left-radius: unset !important;
	border-top-right-radius: unset !important;
}

.first {
	border-top-left-radius: .25rem !important;
}

.last {
	border-top-right-radius: .25rem !important;
}
}

@-moz-document regexp("https:\\/\\/www\\.speedrun\\.com\\/.+") {
/* Better Profile Headers */

/*
 * REGEX
 *
 * https:\/\/www\.speedrun\.com\/.+
 */

/* Make the sliders less massive */
span.float-right.right {
	font-size: .8rem !important;
}

.switch {
	width: 26px !important;
	height: 14px !important;
}

.switch .slider::before, .switch .slider::after {
	width: 12px !important;
	height: 12px !important;
	bottom: 1px !important;
}

.switch input:checked+.slider:before {
	transform: translateX(11px) !important;
}

/* Bigger options in user profiles */
a.usermenu-item {
	font-size: 14px !important;
}
}

@-moz-document regexp("https:\\/\\/www\\.speedrun\\.com\\/.+") {
/* Big Videos */

/*
 * REGEX
 *
 * https:\/\/www\.speedrun\.com\/.+
 */

/* NOTE: This REQUIRES `css/widescreen.css` to work properly. */

/* 
 * The height of the video player, you may need to adjust this based on your
 * screen size.
 */
:root {
	--video-height: 87vh;
}

/* Video height */
iframe.youtube, iframe.twitch {
	height: var(--video-height) !important;
	width: 100% !important;
}

/* Workaround to get videos in forum posts back to a normal size */
.forumpost p > iframe {
	width: 770px !important;
	height: 433px !important;
}
}

@-moz-document url("https://www.speedrun.com/games") {
/* Fixed Game Height */

/*
 * URL
 *
 * https://www.speedrun.com/games
 */

div.col-auto.mt-4.listcell.center {
	height: 265px;
}
}

@-moz-document url-prefix("https://www.speedrun.com") {
/* No Footer */

/*
 * URL
 *
 * https://www.speedrun.com
 */

footer {
	display: none;
}
}

@-moz-document url-prefix("https://www.speedrun.com") {
/* No New Font */

/*
 * URL
 *
 * https://www.speedrun.com
 */

/* Fix "Latest Runs" font */
.widget-title {
	text-transform: unset !important;
	font-weight: unset !important;
	font-family: sans-serif !important;
}

/* Remove "Latest Runs" header background */
body.dark .widget-header {
	background-color: unset !important;
}

/* Line break after "Latest Runs" */
body.dark .widget-header {
	padding: 0;
	padding-top: 1rem;
	border-bottom: 1px solid rgba(255,255,255,.13) !important;
	padding-bottom: .5rem !important;
	margin-bottom: 1rem !important
}

.widget-container {
	border-top: 0px !important;
	padding-left: 1rem;
	padding-right: 1rem;
}
}

@-moz-document url("https://www.speedrun.com/runsawaitingverification") {
/* No Rejected Runs */

/*
 * URL
 *
 * https://www.speedrun.com/runsawaitingverification
 */

.panel:last-of-type {
	display: none;
}
}

@-moz-document url-prefix("https://www.speedrun.com") {
/* No Sidebar */

/*
 * URL
 *
 * https://www.speedrun.com
 */

/* Remove the sidebar */
#widget.col-auto {
	display: none;
}

#centerwidget {
	width: 100% !important;
}

#leaderboarddiv, .old-leaderboard {
	max-width: 100% !important;
}
}

@-moz-document url-prefix("https://www.speedrun.com") {
/*
 * URL
 *
 * https://www.speedrun.com
 */

/* Edit this value to change how wide the pages get */
:root {
	--page-width: 85%;
}

/* Make the homepage/leaderboards/etc. wider. */
@media (min-width: 1600px) {
	.container {
		max-width: var(--page-width);
	}
}
}
