style.css:
@-moz-document url-prefix("https://panorama.pub/") {
/*** Panorama DarKompact v1.2.3 (27.02.21) ***/


/*[[tinylogo]]*/
/*[[background]]*/

/* tiny navbar */
.head-flex {
	display: flex;
	height: 0px !important;
}
.navbar {
	padding: 0.57rem 0rem 0.43rem 0rem;
	margin-left: 125px; }

/* connected quote block */
.navbar-nav.mr-auto.mt-2.mt-lg-0 .nav-item:nth-child(1) {display: none; }
.the-row.quote { border-bottom: 1px solid #0096A8; }

/* smaller logo */
img.logo {
	width: 150px;
	filter: contrast(1.05) brightness(4) grayscale();
	transition: 250ms; }
img.logo:hover {filter: contrast(0.9) brightness(4) grayscale() drop-shadow(0 0 2px #ddd); }

/* wider pages */
.the-row > .inner {
	max-width: 85%;
}

/* dimmer (blur doesn't work in Firefox if backdrop is not enabled in about:config) */
#main-dimmer {
	background-color: #333D;
	backdrop-filter: blur(2px)
}

body {
	color: #EEE;
	background-attachment: fixed;
	background-size: cover;
}

/* custom links colours */
.entry.big {color: #DDD;}
a .entry, h3{ transition: 250ms; }
p a:hover, form a:hover {
	color: #B8976C;
	text-decoration: auto;
	border-bottom-color: inherit;
}
p a, form a {
	color: #1fabc1;
	border-bottom: 1px solid #1fabc1;
}
a.entry:hover h3, p a:hover {
	background-color: #0000;
	color: #DFAC6A;
}
.entry.small:hover .wrapper, .entry.medium:hover .wrapper, .entry.medium:hover .wrapper h3 {color: #D7A77A; }
.meta {
	border-bottom: 1px solid #2C3237;
	color: #CCC;
}

/* centered rounded category titles */
.category-title {
	padding: 6px 20px 4px;
	border: 2px solid #88663b;
	background-color: #0005;
	margin: 20px 80px 0px;
	display: block;
	border-radius: 50px;
	text-align: center;
	box-shadow: 0px 0px 0px 4px #0005;
	transition: 259ms;
}
.category-title:hover {
	border: 2px solid #DFAC6A;
	color: #DFAC6A !important;
}

/* custom buttons colours */
.meta .categories {
	margin-left: 0px;
	border-left: none;
}
.btn {
	color: #999;
	margin: 2px;
}
.btn:hover {
	color: #FFF;
	text-decoration: none;
	background-color: #fff1;
}
.btn-page-selected, .btn-page-selected:hover {
	color: #FFF;
}
.btn-secondary {
	color: #CCC !important;
	background-color: #6c757d;
	border-color: #6c757d;
}

/* custom form-control colours */
.form-control {
	color: #999 !important;
	background-color: #222;
	border: 2px solid #666;
}
.form-control:focus {
	color: #6c757d;
	background-color: #222;
	border-color: #18b0b0;
}
.form-control:disabled, .form-control[readonly] {
	background-color: #222;
	border: 2px solid #333;
}

/* news swiper arrows fix */
.swiper-button-next {
	padding: 11rem 2rem;
	top: 6%;
	right: 0px;
	background-image: linear-gradient(90deg,#0000,#3333 50%);
	transition: 250ms; }
.swiper-button-prev {
	padding: 11rem 2rem;
	top: 6%;
	left: 0px;
	background-image: linear-gradient(270deg,#0000,#3333 50%);
	transition: 250ms;
}
.swiper-button-next:hover, .swiper-button-prev:hover {filter: brightness(3); }

/* alerts and disclaimers */
.disclaimer {
	color: #465059;
	border-top: 1px solid #333;
}
.alert-light {
	color: #999;
	background-color: #212529;
	border: 2px solid #373F46;
}
	
/* donations table */
.table {color: #ccc; }
.table thead th {
	border-top: none;
	border-bottom: 2px solid #ccc;
}
.table td, .table th {
	border-top: 1px solid #2C3237;
	border-bottom: 1px solid #2C3237;
}
.table-hover tbody tr:hover {
	color: #fff;
	background-color: #2C3237;
	border: 1px transparent;
	transition: 250ms;
}
.card {
	background-color: #333;
}

/* other stuff */
.border-top {
	border-top: 1px solid #333 !important;
}
.page-contents h1 {
	border-bottom: 2px solid #2C3237;
}
.ad-container.bottom {
	border: none;
}
.entry-contents > .text > p:first-child {
	color: #666;
}

}

