/* контейнер блоков (говнокод, потому что спасибо мылору
 за этот отвратительный макет чёрного цвета 00х гг. XXI века) */
body {
	--block-width: 24%;
	overflow: hidden;
	height: 100vh;
	scrollbar-width: none;
}
.i-pull-left:nth-child(1) {
	float: inherit !important;
	width: 100% !important;
	height: 256px !important;
	display: inline-flex !important;
	margin: 40px 13.4% 0 !important;
	justify-content: center !important;
}

/* удаление остаточного мусора */
.slidotype,
.tabs-content, .tabs, .new-tgb-wrap,
.news-main.i-link-no-deco::before,
.search__sample, .searchContainer { display: none; }

/* фикс очень странного бага с рандомным смещением блоков */
.grid__lcol { position: relative !important; }



/* почта */
.mailbox-container {
	width: var(--block-width);
	margin: 0 2px;
	display: inline-block !important; }
.account:hover .account__email::after, .account:hover .account__name::after { background: none !important; }
.footer::before {
	margin-bottom: 0;
	border: none;
	display: none; }
.account__name {
	font-weight: 600;
	margin-left: -1px; }
.resplash-btn_secondary { display: none; }

/* почта (если вход не выполнен) */
.password-input-container.hide { display: block !important; }
.button { display: none !important; }
.second-button.hide {
	display: block !important;
	height: 40px !important;
	margin-bottom: 12px !important;
}
.saveauth-wrapper {
	position: absolute !important;
	margin-top: -50px;
	margin-left: 130px;
	width: 180px; }
.restore {
	position: absolute !important;
	margin-top: 38px; }
.error.svelte-1eyrl7y.svelte-1eyrl7y { display: none;
}
.resplash-btn_vk-connect {
	position: absolute;
	margin-top: 0px !important;
	font-size: 0px !important;
	width: 32px !important;
	margin-left: 215px;
	box-shadow: 0px 0px 0px 2px #fff; }
.vk-icon { margin-right: 0px !important;
}

/* погода */
.weather__header { padding: 12px 20px 12px !important; }
.weather__block_base { padding: 4px 8px 18px 20px !important; }
.weather__block_extented { padding: 10px 0 12px !important; }
.weather.svelte-qrnlut {
	width: var(--block-width);
	margin: 0 2px;
	display: inline-block !important;
	position: relative;
	bottom: 129px;
	height: 178px;
}

/* курсы валют */
.rates.rates_three {
	width: var(--block-width);
	margin: 0px 2px;
	margin-left: calc(-24% - 6px);
	display: inline-flex !important;
	position: relative;
	bottom: 9px;
	padding: 50.5px 0; }
.rates.rates_three::before {
	content: "Курсы валют";
	position: absolute;
	margin-top: -39px;
	margin-left: 20px;
	font-weight: 700;
	color: #333; }
.rate {
	padding: 0px 16px;
	margin-top: -4px;
}


/* гороскоп */
.horoscope.i-link-no-deco {
	width: var(--block-width);
	height: 288px;
	margin: 0 2px;
	padding: 13px 20px 23px;
	position: relative;
	bottom: 224px;
	display: inline-flex !important;
	overflow-y: scroll;
	scrollbar-width: thin; }
.desc.i-link-no-deco { height: auto; }
.icon-wrapper.svelte-123mkzr { margin: 2px 2px 0px -4px; }
.title.svelte-123mkzr {
	font-weight: 700;
	margin-bottom: 2px; }
.title.svelte-123mkzr:hover { text-decoration: underline; }






