style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
.ps-room .battle-controls {
    top: 420px !important;
}

.battle {
    padding-top: 50px
}

.background {
	position: absolute;
	top: -80px;
}

.trainer strong {
    color: white;
    font-weight: bold;
    text-shadow: 0px 0px 4px #000;
    font-size: 9px;
}

.rightbar {
    background: #f000;
    width: 300px;
    height: 80px;
    position: absolute;
    top: -50px;
    left: 340px;
    border-left-color: transparent
}

.rightbar .trainer {
    position: absolute;
    top: 25px;
    right: -180px
}

.rightbar .teamicons {
    position: relative;
    top: -112px;
    right: -70px
}

.rightbar .trainersprite::after {
    content: attr(title);
    font-size: 10px;
    color: grey;
    background: black;
    border-radius: 5px;
    padding: 3px;
    font-weight: bold;
    position: relative;
    top: -35px;
    right: 0px;
}

.leftbar {
    background: #f000;
    width: 300px;
    height: 80px;
    position: absolute;
    top: -50px;
    left: 0px;
    border-right-color: transparent
}

.leftbar .trainer {
    position: absolute;
    top: 25px;
    right: 180px
}

.leftbar .teamicons {
    position: relative;
    top: -112px;
    right: -112px
}

.turn {
    background: url(https://i.imgur.com/eP5GXto.png); /*‚ÜêI made that :)*/
    width: 126px;
    height: 23px;
    padding: 0px;
    padding-top: 25px;
    border: none;
    text-align: center;
    font-size: 13px;
    color: white;
    opacity: 1 !important;
    margin: -50px 148px;
}

.weather em {
    font-size: 11px;
    text-shadow: 1px 1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, -1px -1px 0 #fff, 0px 0px 7px black;
    font-weight: bold;
    margin: 0px 0px;
    position: relative;
    top: 90px;
    right: -115px
}

.weather
{
    top: -50px !important;
    background-size: 120%
}
}
