style.css:
@-moz-document url-prefix("https://ent.iledefrance.fr/") {
.navbar {
    background: #2c2c2c;
    height: 74px;
}
body section.main {
    background: #2c2c2c;
}
body {
    font-size: 16px;
    line-height: 21px;
    color: #cdcfd4;
}

.widgets .news {
    background-color: #444444;
}
.qwant, .qwant-junior {
    background-color: #444444;
}
.widget {
    max-width: 100% !important;
    background-color: #444444;
}
.notes-widget {
    background-color: #444444;
}
.notification {
    box-shadow: 0 1px 3px rgb(0 0 0 / 10%);
    background-color: #444444;
}

.my-apps, nav.horizontal .my-apps {
    margin: 0px auto;
    background-color: #444444;
}
.more {
    background: #4f4f4f;
}

popover popover-content {
    background: #444444;
}
html, body {
    background: #2c2c2c;
}

}

@-moz-document url-prefix("https://ent.iledefrance.fr/zimbra/") {
nav.vertical a.classic-link, .app-menu a.classic-link {
    background: #444444;
    transition: background 250ms ease;
    border-radius: 2px;
}
.progress-bar {
    background: #444444;
    border: 1px solid #ddd;
    font-size: 11px;
}
.flat-button, button.flat-button, input[type=submit].flat-button, input[type=button].cancel, button.cancel, a.button.cancel, .drop-down-button label, .drop-down-button.hidden label {
    border: none;
    box-shadow: none;
    color: #FF8500;
    background: #444444;
}
article.mail, article.write-mail {
    padding: 15px 30px;
    background-color: #444444;
}
body {
    font-size: 16px;
    line-height: 21px;
    color: #cdcdcd;
}
html, body {
    background: #2c2c2c !important;
}
section.main-col article.vertical-spacing .escape_cke {
    color: #efefef;
}


[data-drop-down] ul {
    background: #343434;
    color: #d9d9d9;
    font-size: 1.2em;
    padding: 1px;
}
.drop-down .display-more {
    background: #343434;
    text-align: center;
    list-style-type: none;
}
html, body {
    background: #2c2c2c;
}


}

@-moz-document url-prefix("https://ent.iledefrance.fr/zimbra/preferences") {
body {
    overflow-x: hidden;
    font-size: 16px;
    line-height: 21px;
    color: #e2e2e2;
}
article {
    background: #444444;
    margin-bottom: 15px;
}
}

@-moz-document url-prefix("https://ent.iledefrance.fr/welcome") {
.bookmarked-apps .icons-view.apps {
    background-color: #2c2c2c;
}
.icons-view .element legend a:first-child span, .icons-view explorer legend a:first-child span {
    line-height: 1px;
    color: #e5e5e5;
    font-size: 16px;
}

}

@-moz-document url-prefix("https://ent.iledefrance.fr/community") {
html, body {
    background: #2c2c2c;
}
table, ul.list-view {
    background: #444444;
}
article {
    background: #444444;
}

}

@-moz-document url-prefix("https://ent.iledefrance.fr/actualites") {
article {
    background: #444444;
}
.actualites .info-preview .degrade {
    background: transparent;
}
.tabs.heading-tabs {
    background: #444444;
    border-radius: 2px;
}
.side-col.cell {
    border-radius: 2px;
    background: #2c2c2c;
}
nav.vertical.stamps article, nav.vertical.half-stamps article {
    border-radius: 2px;
    background: #444444;
    line-height: 20px;
    font-size: 12px;
}
section.lightbox>div.content, section.tiny-lightbox>div.content {
    border-radius: 5px;
    width: 720px;
    background: #444444;
}
}

@-moz-document url-prefix("https://ent.iledefrance.fr/userbook/") {
article {
    background: #444444;

}
input[type=email] {
    color: #cdcfd4;
    background: transparent;
    border-bottom: 1px solid #d6d9e0;
}
input[type=text], input[type=password], input[type=search], input[type=tel], input[type=email] {
    color: #d6d9e0;
    background: transparent;
    border-bottom: 1px solid #d6d9e0;
}
.tabs.heading-tabs {
    background: #444444;
    border-radius: 2px;
}
.responsive-table table thead tr {
    font-weight: bold;
    color: #e2e2e2;
    border-top: 1px solid #c9ced5;
}
.responsive-table .fixed-head {
    margin-bottom: 0;
    background: #2c2c2c;
}
input[type=email] {
    color: #e2e2e2;
    background: transparent;
    border-bottom: 1px solid #d6d9e0;
}
}

@-moz-document url-prefix("https://ent.iledefrance.fr/auth/") {
body {
    font-size: 16px;
    line-height: 21px;
    color: #cccccc;
}

.panel.login .welcome-text, .panel.activation .welcome-text {
    background-color: #444444;
}

.panel.login .welcome-text+form, .panel.activation .welcome-text+form {
    background-color: #444444;
}

}
