style.css:
@-moz-document domain("sms24.me") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #18191a;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #18191a;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #007bff;
        }

body {
	background: #111;
	color: #d1d5d9;
}
.bg-white {
	background: #111 !important;
}
.card {
	background: #111;
	border-color: rgba(255, 255, 255, .125);
}
.text-secondary, .text-muted {
    color: #9caab7!important;
}
.bd-callout {
	border-color: #222;
}
.bg-light {
    background-color: #18191a!important;
}
.text-dark {
    color: #c4e1ff!important;
}
.form-control {
    color: #b9dcff !important;
    background-color: #111 !important;
    border-color: #4e5053;
}
.custom-select {
	filter: invert(.935) hue-rotate(180deg);
}
option {
	background: #111;
	color: #d1d5d9;
}
.dropdown-menu {
	background: #111;
	color: #d1d5d9;
	border-color: rgba(255, 255, 255, .15);
}
.dropdown-item, .btn {
	color: #d1d5d9;
}
.dropdown-item:focus, .dropdown-item:hover {
	background: #18191a;
	color: #e6e8eb;
}
.page-link:hover {
	background: #292c2f;
	border-color: #3e4042;
}
.page-link {
	background: #111;
	border-color: #3e4042;
}
.btn-light {
	background: #18191a;
	border-color: #18191a;
	color: #d1d5d9;
}
.btn-light:not(:disabled):not(.disabled).active, .btn-light:not(:disabled):not(.disabled):active, .show>.btn-light.dropdown-toggle {
    color: #d1d5d9;
    background-color: #3a3c3e;
    border-color: #33393f;
}
.btn-light:hover {
    color: #d1d5d9;
    background-color: #22262a;
    border-color: #3a3c3e;
}
.jumbotron {
	background: #292c2f;
}
.callout {
	background: #18191a;
	border-color: #292c2f;
}
.callout:hover {
	background: #292c2f;
}
.flag-icon {
	border-color: #18191a;
}
.preloader {
	background: #111;
}
}
