style.css:
@-moz-document domain("lolnada.org") {
    :root {
        --main-background: #1d1f21;
        --secondary-background: #282a2e;
        --secondary-background-rgba: rgba(40, 42, 46, 0.9);
        --secondary-background-highlighted: #1d1d21;
        --border: #111111;
        --text: #c5c8c6;
        --link: #81a2be;
        --link-hover: #5f89ac;
    }

    /* Base */
    body {
        background-color: var(--main-background);
        color: var(--text);
    }
    a {
        color: var(--link);
    }
    a:hover {
        color: var(--link-hover);
    }

    /* Modals */
    #modal, #alert_div, #options_div {
        background-color: var(--secondary-background);
        border: var(--border);
    }

    /* Barra de navegación */
    nav {
        background: var(--secondary-background-rgba);
    }
    .boardlist a {
        border-color: transparent;
        color: var(--link);
    }
    .boardlist a.sel {
        border-color: var(--link);
    }
    .boardlist a:hover {
        border-color: var(--link-hover);
        color: var(--link-hover);
    }
    .nnp {
        background-color: var(--link-hover);
        font-weight: bold;
    }
    .pages a.selected {
        border-color: var(--link);
    }
    .pages a:hover {
        border-color: var(--link-hover);
    }
    #navm /* Versión móvil */ {
        border: none;
        color: var(--link);
    }

    /* Tablones */
    header h1 /* Nombre del tablón */ {
        color: var(--link-hover);
    }
    header .subtitle /* Descripción del tablón */ {
        color: var(--text);
    }
    .blotter /* Aviso */ {
        background-color: var(--secondary-background);
        color: var(--text);
    }
    .reply /* Respuesta al hilo */ {
        background-color: var(--secondary-background);
        border-color: var(--secondary-background);
    }
    .post.reply .intro /* Encabezado de posts en versión móvil  */ {
        background-color: var(--secondary-background);
    }
    .post.reply.highlighted /* Respuesta al hilo */ {
        background-color: var(--secondary-background-highlighted);
        border-color: var(--border);
    }
    .post.reply.highlighted .intro /* Respuesta al hilo */ {
        background-color: var(--secondary-background-highlighted);
    }
    .intro .name, /* Nombre de usuario */
    .name .tu, /* (Tú) */
    .trip, /* Tripcode de usuario */
    .intro time, /* Marca de tiempo */
    .poster_id, /* Identificador de usuario */
    .intro .inl, .intro.qr, /* Íconos */
    .post .body span.toolong a /* Post muy largo */ {
        color: var(--text);
    }
    #xf, /* Nuevo hilo */
    .post_no, /* Número de post */
    .mentioned a, /* Respuestas al post */
    .body .tu /* (Tú) */ {
        color: var(--link);
    }
    #xf:hover, /* Nuevo hilo */
    .intro a.post_no:hover, .post_no:hover, /* Número de post */
    .intro .inl:hover, .intro.qr:hover, /* Íconos */
    .mentioned a:hover, /* Respuestas al post */
    .post .body span.toolong a:hover /* Post muy largo */ {
        color: var(--link-hover);
    }
    .fileinfo a, /* Nombre del archivo adjunto */
    .post .body a /* Enlaces dentro de los posts */ {
        color: var(--link);
    }
    .fileinfo a:hover, /* Nombre del archivo adjunto */
    .post .body a:hover /* Enlaces dentro de los posts */ {
        color: var(--link-hover);
    }
    hr {
        border-color: var(--secondary-background);
    }

    /* Formularios */
    form table tr th,
    input[type="text"],
    input[type="password"],
    textarea,
    .sel-arc,
    select {
        background-color: var(--secondary-background);
        border: 2px solid var(--main-background);
        color: var(--text);
    }
    #rr {
        background-color: var(--main-background);
        border-color: var(--border);
        color: var(--text);
    }
    #hnd {
        background-color: var(--secondary-background);
        color: var(--text);
    }

    /* Catálogo */
    .threads li a {
        color: var(--text);
    }
    .threads .thread {
        background-color: var(--secondary-background);
        border-color: var(--secondary-background);
    }

    /* Ban */
    div.ban {
        background-color: var(--main-background);
        border-color: var(--secondary-background);
    }

    /* Botones */
    .button, input[type="submit"], input[type="button"], /* Botón de entrada de datos */
    .subtitle p a, /* Reglas y preguntas frecuentes */
    .nuevas_resp, /* Nueva actividad */
    .image_id a, /* Google, IQDB */
    span.omitted a, /* Click para mostrar */
    .volarr a, a#mqr, #updater a, /* Botones al fondo del hilo */
    .enlinea, /* Usuarios en línea */
    .bajar, .subir /* Botones de bajar y subir */ {
        background-color: var(--text);
        border-color: var(--text);
        color: var(--secondary-background);
    }
    .button:hover, input[type="submit"]:hover, input[type="button"]:hover, /* Botón de entrada de datos */
    .subtitle p a:hover, /* Reglas y preguntas frecuentes */
    .nuevas_resp:hover, /* Nueva actividad */
    .image_id a:hover, /* Google, IQDB */
    span.omitted a:hover, /* Click para mostrar más */
    .dadoroll, /* Dados */
    .volarr a:hover, a#mqr:hover, #updater a:hover, /* Botones al fondo del hilo */
    .enlinea, /* Usuarios en línea */
    .links a:hover,  /* Footer */
    .bajar:hover, .subir:hover /* Botones de bajar y subir */ {
        background-color: var(--link-hover);
        border-color: var(--link-hover);
        color: white;
    }
    input[type="submit"] {
        border-color: var(--text);
    }
    input[type="submit"]:hover {
        border-color: var(--link-hover);
    }

    /* Hilos populares */
    .ca li a {
        background-color: var(--secondary-background);
        box-shadow: none;
    }
    .ca li .det {
        background-color: var(--secondary-background);
    }
    .ca li a .det {
        color: var(--text);
    }

    /* Hilos recientes */
    .la li {
        background-color: var(--secondary-background);
        border: 2px solid var(--main-background);
    }
    .la li a {
        color: var(--text);
    }

    /* Footer */
    footer {
        color: var(--text);
    }

    /* Reglas y preguntas frecuentes */
    .wbg {
        background-color: var(--secondary-background);
    }
}
