style.css:
@-moz-document regexp("(http|https):\\/\\/(|www\\.|www1\\.)flightrising\\.com.*") {
/*GENERAL STUFF */

/*dark highlighting and inputs + firefox fixes */
::selection, *::selection {
    filter: none !important;
    background: /*[[selectbg]]*/;
    color: /*[[selectcolor]]*/;
}
/*dark select boxes*/
.common-filter select, .scry-id, .common-dialog .common-dialog-inset {
    background-color: #15191e;
    color: #edf1f9;
    border: #121522;
}
.common-column-content select, select, .dragon-picker-filter-field input, .dragon-picker-filter-field select, .morphology-picker-filter-field input, .morphology-picker-filter-field select {
    background-color: #15191e;
    color: #edf1f9;
    border: 1px solid #121522;
    background-image: url('https://www1.flightrising.com/static/layout/common/select-menu.png');
    background-position: right center;
    background-repeat: no-repeat;
    padding-right: 18px;
    -moz-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}
#coli-dragonlist-controls select, #item-chooser-controls #quantity {
    background-color: #233946;
    border-color: #14252f;
}
#item-chooser-controls #quantity {
    width: 100%;
}
/* dark empty checkboxes, bright blue with black checkmarks when selected */
.common-checkbox {
    background: #15191e;
    border: 1px solid #546d86;
    color: #dae0e7;
}
.common-checkbox:hover {
    background: #263646;
    border: 1px solid #6a859f;
}
.common-checkbox[data-selected="1"] {
    border-color:#4df0f0;
    background-color:#4df0f0;
    color: #000;
}
.common-checkbox[data-selected="1"]::after {
    filter: brightness(0);
}
/*change text stroke to black*/
.common-text-stroke-white {
    text-shadow: #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px, #000 0px 0px 2px;
}
/*fix column styling*/
div.contentcontainer {
    display: grid;
    grid-template-columns: 200px auto auto;
}
.contentcontainer div.leftcolumn,
#content div.leftcolumn {
    background-color: #15191e !important;
}
#content div.leftcolumn div.content {
    background: none !important;
}
.contentcontainer > div[style*="clear"], .contentcontainer > div.clear {
    order: 3;
    width: 0px;
}
body > .copybar {
    display: block;
    margin: auto;
}
/*[[leftcol]]*/
/*fix poor contrast of textareas*/
textarea#message.input_reg, form#new input[name="subject"], #attachments input, .input_reg, div#xroad-metadata textarea, #trademsgform textarea, 
    #clan_bio,
    textarea#message, textarea#suggestion {
    background-color: #36404a  !important; /*#3e4952*/
    color: #dae0e7 !important;
    font-family: Verdana, Geneva, sans-serif;
    font-size: 13px;
    line-height: 1.5em;
    border: 1px solid #0b151c !important;
}
/* lower brightness of separators */
img[src*="/images/layout/graydot.gif"], img[src*="/static/layout/graydot.gif"], img.divider {
    opacity: 0.2;
}
/*pagination fixing*/
.common-pagination-pages {
    display: flex;
    justify-content: center;
    align-items: center;
}
.common-pagination .common-pagination-page {
    margin: 0 2px;
}
/*fix issue w/ buttons being over alerts*/
.statdiv span #alerticon, #trade-chat span, #messicon, #frenicon, span.pending-avatar-msg-count {
	z-index: 2;
	position: relative;
}
    
/*add min height to pagination buttons to prevent content shift if arrow images need to load*/
.common-pagination .common-pagination-arrow-previous, .common-js-pagination .common-pagination-arrow-previous,
.common-pagination .common-pagination-arrow-next, .common-js-pagination .common-pagination-arrow-next {
    box-sizing: border-box;
    min-height: 32px;
}

/*fix dev tracker*/
#dev-tracker-content .dev-tracker-search-result-message-fade {
    font-weight: bold;
    text-transform: uppercase;
    color: #00f1ff;
    background: linear-gradient(to bottom, rgba(243,236,228,0) 0%,rgb(19, 23, 27) 80%);
}
    
    /*use filters for some images to make them match main style bc im too lazy to edit and rehost images*/
    img[src="/static/layout/game-database/button-recent-comments.png"],
    img[src="/static/layout/archaeology/button-dig-sites.png"],
    img[src="/static/layout/archaeology/button-muster-resources.png"],
    img[src="/static/layout/archaeology/button-assist-a-friend.png"],
    img[src="/static/layout/archaeology/button-share-with-colleagues.png"], 
    img[src="/static/layout/archaeology/button-donate-to-the-guild.png"] {
        filter: hue-rotate(190deg) saturate(270%) contrast(150%);
    }
    img[src="/static/layout/archaeology/button-muster-resources-disabled.png"] {
        filter: hue-rotate(190deg) saturate(150%) contrast(180%);
    }

/*fix speech bubble issue caused by main style*/
.common-npc-dialog[data-tail="left"]::before {
    border-color: transparent #060d11 transparent transparent !important;
}
.common-npc-dialog[data-tail="left"]::after {
	border-color: transparent #060d11 transparent transparent !important;
}
    /*speech bubble strong*/
    .common-npc-dialog strong {
        color: #70d2ff;
    }

/*wrap long usernames in forum posts*/
a.post-author-username, .post-author-username, .editor-author-username {
    word-wrap: break-word;
    max-width: 90%;
    line-height: 100%;
    text-shadow: -2px -2px 0px rgba(21, 25, 30, .41), -2px -1px 0px rgba(21, 25, 30, .90), -2px 0px 0px rgba(21, 25, 30, .99), -2px 1px 0px rgba(21, 25, 30, .89), -2px 2px 0px rgba(21, 25, 30, .42), -1px -2px 0px rgba(21, 25, 30, .89), -1px -1px 0px rgb(21, 25, 30), -1px 0px 0px rgb(21, 25, 30), -1px 1px 0px rgb(21, 25, 30), -1px 2px 0px rgba(21, 25, 30, .91), 0px -2px 0px rgb(21, 25, 30), 0px -1px 0px rgb(21, 25, 30), 0px 0px 0px rgb(21, 25, 30), 0px 1px 0px rgb(21, 25, 30), 0px 2px 0px rgba(21, 25, 30, .99), 1px -2px 0px rgba(21, 25, 30, .87), 1px -1px 0px rgb(21, 25, 30), 1px 0px 0px rgb(21, 25, 30), 1px 1px 0px rgb(21, 25, 30), 1px 2px 0px rgba(21, 25, 30, .83), 2px -2px 0px rgba(21, 25, 30, .35), 2px -1px 0px rgba(21, 25, 30, .87), 2px 0px 0px rgba(21, 25, 30, .98), 2px 1px 0px rgba(21, 25, 30, .89), 2px 2px 0px rgba(21, 25, 30, .41) !important;
}

    div.post-author:not([style*="background-image"]) {
        background: linear-gradient(to bottom, #202931 0%, #2d363e 50%) !important;
    }
    
    /*fix blockquotes in multiple areas of site*/
    #topic-preview-text div.bbcode_quote_body,
    #topic-preview-text div.bbcode_code_body,
    .post-text div.bbcode_quote_body,
    .post-text div.bbcode_code_body,
    #dragon-profile-bio div.bbcode_quote_body,
    #dragon-profile-bio div.bbcode_code_body,
    #clan-profile-page div.bbcode_quote_body,
    #clan-profile-page div.bbcode_code_body {
        background-color: #2d363e !important;
    }
    #topic-preview-text div.bbcode_quote,
    .post-text div.bbcode_quote,
    #dragon-profile-bio div.bbcode_quote,
    #clan-profile-page div.bbcode_quote {
        border-color: #22262a  !important;
    }
    #topic-preview-text a.bbcode_url,
    .post-text a.bbcode_url,
    #clan-profile-page a.bbcode_url {
        color: #7eceed !important;
    }

/* makes choosing dragon vista background dark when no vista is selected */
.choose-vista-preview,
#rep_box div[style*="background-color:#ccc"] {
    background-color: #22262a !important;
    border-color: #0b151c;
}
.choose-vista-preview:not([style*="url"]) {
    background: linear-gradient(to bottom, #202931 0%, #2d363e 50%) !important;
}

/*more obvious disabled buttons*/
#nonewoffer, button:disabled, *[type=button]:disabled, #notrade.mb_button {
    opacity: 0.45;
}

/*lucky streak label fix*/
#lucky-streak,
.archaeology-dig-site-index-row-notes-badge,
.archaeology-dig-plot-complete {
    color: #000 !important;
}
/*misc label fixes*/
    .content-header-sub,
    #archaeology-dig-timer-time,
    #archaeology-dig-timer-text,
    #archaeology-xp-meter-label,
    #archaeology-xp-meter-amount,
    .archaeology-dig-site-index-row-description,
    .archaeology-dig-site-index-row-focus-toggle-text,
    #gather_turns,
    #gather_success,
    .gather_itemcount
    {
        color: #70d2ff;
    }
    #archaeology-dig-timer-header,
    .archaeology-supply,
    #gather_turn_count,
    .common-bubble-subject,
    #rep_box div[style*="background-color:#ccc"] {
        color: #dae0e7;
    }
    .content-header-sub,
    .baldwin-timer-header{
        color: #70d2ff !important;
    }
/*crossroads*/
    #btrade {
        width: 150px;
    }
    /*trade receipts*/
    table#tradereceipts thead {
        font-weight: bold;
        background: rgba(112, 210, 255, .15);
    }
    table#tradereceipts thead td {
        padding-top: 10px;
    }
    table#tradereceipts tbody tr:nth-child(odd) {
        background: rgba(218, 224, 231, .1);
    }
/*gathering + archaeology fixes*/
    .gather_selection_box,
    .archaeology-dig-site-index-row-details,
    .archaeology-supply{
        background-color: #364650;
        border-color: #121522;
    }
    
    #gather_success_bg {
        filter: brightness(30%) contrast(160%) sepia(100%) hue-rotate(150deg);
        box-shadow: 0px 0px 15px #fff, 0px 0px 10px #fff, 0px 0px 5px #fff;
        border-radius: 20px;
        overflow: hidden;
        left: 145px;
    }
    .gatherblurb {
        margin-top: 30px;
    }
    
    #trading-post-content .common-hub-item-button-image,
    #tradetype > a > img {
        opacity: 0.75;
        transition: opacity 0.3s;
    }
    #trading-post-content a:hover .common-hub-item-button-image,
    #trading-post-content a:focus .common-hub-item-button-image,
    #tradetype > a:focus > img,
    #tradetype > a:hover > img {
        opacity: 1;
    }
    
    .gather_container .lair-page-dragon-energy-meter-meter-full {
    background: linear-gradient(to right, #235e96 0%, #80efbd 100%);
    border: 1px solid #3dd6ef;
}

/* full width gathering buttons */
.gatherbutton input {
    width: 95%;
}

/*fixing btns*/
.beigebutton.thingbutton {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
}
/*baldwin*/
    #baldwin #speech-bubble-button-tray input,
    #baldwin #baldwin-collect-button {
        width: 95%;
    }

/* make the orange alerts blue on old pages */
span#alert div[style*="background-image:url(images/layout/alert_box_bg.png)"] {
    background-image: url(https://i.imgur.com/fkzzcPi.png) !important;
}

/*dark scry share widget*/
.scry-image .scry-image-rear {
    background: none;
    position: relative;
    z-index: 2;
}
.scry-image .scry-buttons {
    z-index: 3;
}
.scry-image .scry-image-rear::before {    
    content: "";
    background-image: url('https://www1.flightrising.com//static/layout/scryer/bg_scrywidget.png');
    background-size: cover;
    position: absolute;
    top: 0px;
    right: 0px;
    bottom: 0px;
    left: 0px;
    opacity: 0.75;
    z-index: -1;
    filter: invert(100%) sepia(100%) hue-rotate(190deg) saturate(390%) brightness(70%);
    pointer-events: none;
}
.scry-image .scry-name, .forum-map-widget-frame .forum-map-widget-button-text, .settings-hub-radio-control[data-selected="0"] {
    color: #78c6f0 !important;
}
/*dark map poi share widget*/
.forum-map-widget-frame {
    background: linear-gradient(to bottom, #161f31 0%, #212b40 100%);
    border: 1px solid #5b6682;
}
.forum-map-widget-frame .forum-map-widget-button {
    background: linear-gradient(to bottom, #192031 0%, #192031 100%);
    border: 1px solid #5d6476;
}

