/*[[stickymenu]]*/

/*~~end optional stuff *~~/

/*nesting ground dialog fix*/
#dialog div[style*="background-color:#dedacf;"] {
    background-color: #364650 !important;
    color: #fff !important;
}
    div[style="position:relative; width:92px; margin-left:auto; margin-right:auto; margin-top:10px;"],
    input[onclick="showNewbies()"] {
        width: 360px !important;
    }
div#dialog.ui-dialog-content.ui-widget-content div[style*="background-color:#dedacf;"] span {
    color: #dae0e7 !important;
}

/*morphology improvement - fixed height and width*/
.morphology-picker-dragon img {
        width: 100%;
        height: 100%;
        border-radius: 3px;
    }
    .morphology-picker-dragon {
        padding: 2px;
        width: 90px;
        height: 90px;
        box-sizing: border-box;
    }
    .morphology-picker-dragon:nth-child(6n+1) {
        padding-left: 0;
    }
    .morphology-picker-dragon:nth-child(6n+6) {
        padding-right: 0;
    }
#bottom-banner {
    margin: 0 auto;
    padding: 10px 0;
}
#bottom-banner, #bottom-banner-center.content {
    max-width: 950px;
    background: #264774 !important;
}
    
