@-moz-document regexp("(http|https):\\/\\/(|www\\.)flightrising\\.com/main\\.php\\?p=settings.*") {
    /*settings page*/
    #super-container div[style="width:700px; margin-left:auto; margin-right:auto; position:relative; height:900px; font-size:12px;"] {
        display: grid;
        grid-template-columns: 1fr 1fr;
        height: auto !important;
        min-height: 900px;
        padding-bottom: 10px;
    }
    #super-container div[style="width:700px; margin-left:auto; margin-right:auto; position:relative; height:900px; font-size:12px;"] > img[src*="graydot"] {
        display: none !important;
    }
    #super-container div[style="width:700px; margin-left:auto; margin-right:auto; position:relative; height:900px; font-size:12px;"] > *,
    #super-container div[style="position:absolute; width:280px; top:0px; left:20px;"] > *,
    #super-container div[style="position:absolute; width:375px; top:0px; right:0px;"] > *,
    #remove_referral_option > * {
        position: static !important;
    }
    #super-container div[style="position:absolute; width:280px; top:0px; left:20px;"],
    #super-container div[style="position:absolute; width:375px; top:0px; right:0px;"] {
        display: grid;
        grid-template-columns: auto auto;
        column-gap: 10px;
        row-gap: 3em;
        width: auto !important;
    }
    #super-container div[style="position:absolute; width:375px; top:0px; right:0px;"] {
        box-sizing: border-box;
        margin-left: 10px;
        border-left: 1px solid #000;
    }
    #super-container div[style="position:absolute; width:280px; top:0px; left:20px;"] > div:nth-child(even) {
        text-align: right;
    }
}
