        text-shadow:  -2px -2px 0px rgba(21, 25, 30, .41), -2px -1px 0px rgba(21, 25, 30, .90), -2px 0px 0px rgba(21, 25, 30, .99), -2px 1px 0px rgba(21, 25, 30, .89), -2px 2px 0px rgba(21, 25, 30, .42), -1px -2px 0px rgba(21, 25, 30, .89), -1px -1px 0px rgb(21, 25, 30), -1px 0px 0px rgb(21, 25, 30), -1px 1px 0px rgb(21, 25, 30), -1px 2px 0px rgba(21, 25, 30, .91), 0px -2px 0px rgb(21, 25, 30), 0px -1px 0px rgb(21, 25, 30), 0px 0px 0px rgb(21, 25, 30), 0px 1px 0px rgb(21, 25, 30), 0px 2px 0px rgba(21, 25, 30, .99), 1px -2px 0px rgba(21, 25, 30, .87), 1px -1px 0px rgb(21, 25, 30), 1px 0px 0px rgb(21, 25, 30), 1px 1px 0px rgb(21, 25, 30), 1px 2px 0px rgba(21, 25, 30, .83), 2px -2px 0px rgba(21, 25, 30, .35), 2px -1px 0px rgba(21, 25, 30, .87), 2px 0px 0px rgba(21, 25, 30, .98), 2px 1px 0px rgba(21, 25, 30, .89), 2px 2px 0px rgba(21, 25, 30, .41) !important;
        max-width: 95%;
        display: inline-block;
        overflow: hidden;
        text-overflow: ellipsis;
    }
    #msg-show {
        display: grid;
        grid-template-columns: 1fr 1fr;
        padding-bottom: 0;
    }
    #msg-items {
        margin-left: 0;
    }
    #msg-items p {
        margin-bottom: 5px;
    }
    #msg-items #items {
        margin-bottom: 15px;
    }
    #msg-show span#msg-avatar,
    #msg-preview #body span:first-child {
        background: linear-gradient(to bottom, #202931 0%, #2d363e 50%) !important;
        grid-row: 1 / 3;
    }
    #msg-editor > div[style*="margin-bottom:5px; position:relative; height:20px;"] {
        height: auto !important;
    }
    #msg-preview #header {
        padding-left: 5px;
        box-sizing: border-box;
        height: auto;
    }
    #msg-preview #header + #body {
        border-top: none;
        box-sizing: border-box;
        display: grid;
        grid-template-columns: 130px auto;
        overflow: hidden;
    }
    #msg-preview #body span:first-child {
        margin-top: 0;
        padding-top: 15px;
        box-sizing:border-box;
    }
    #msg-preview #body #preview-header {
        width: 100%;
        margin: 0;
    }
    #msg-preview-text {
        width: 100%;
    }
    #msg-preview #body span:last-child {
        background-image: none;
        border-left: 1px solid #0b151c;
        box-sizing: border-box;
        min-height: 125px;
        width: 100%;
        overflow: hidden;
    }
    #msg-preview-text {
        color: #dae0e7 !important;
        font-size: 13px;
        word-wrap: break-word;
    }
    #msg-preview #closediv {
        width: 700px;
        margin: 20px auto;
    }
    button#preclose {
        width: 100%;
        padding: 10px 5px;
    }

/*byebye bottom & nav ad banners*/
#bottom-banner, #skybanner {
    display: none;
}
