/* ==UserStyle==
@name           Dark My Anime List
@namespace      github.com/alcooly/CSS-styles
@version        2.2.0
@description    inspired by Youtube colors scheme
@author         alcooly (github.com/alcooly)
==/UserStyle== */

@-moz-document domain("myanimelist.net") {
/* ----------- */
/*  MAIN PAGE  */
/* ----------- */

/* side bg */
body:not(.ownlist){
    background-color: #202020;
}

/* "MyAnimeList" header */
.page-common #headerSmall{
    background-color: #202020;
}
/* header ad */
img[src="https://cdn.myanimelist.net/images/event/app_202101/mal_app_minib_440k.gif"]{
    display: none !important;
}
/* profile button */
.header-menu-unit.header-profile.js-header-menu-unit.link-bg.pl8.pr8{
    background: #202020;
}
/* profile button name */
.page-common .header-profile-link{
    color: white;
}

/* search filter */
select#topSearchValue{
    background-color: #121212 !important;
    color: white !important;
}
/* search filter border*/
.page-common .inputtext{
    border: 2px solid #303030;
}
/* search filter outside-box border radius */
.page-common #searchBar.searchBar .form-select-outer{
    border-radius: 5px 0 0 5px !important;
}
/* search filter inside-box border radius */
.page-common #searchBar.searchBar #topSearchValue{
    border-radius: 5px 0 0 5px !important;
}
/* input search */
input#topSearchText{
    background-color: #121212;
    border-top: 2px solid #303030 !important;
    border-bottom: 2px solid #303030 !important;
    border-left: 0px !important;
    height: 12px !important;
}
/* user-input string search bar */
.page-common #topSearchText{
    /* couldnt find div for string "Search Anime, Manga, and more..." */
    color: white !important;
}
/* search button */
input#topSearchButon{
    background-color: #121212 !important;
    border: 2px solid #303030 !important;
    height: 20px !important;
}

/* "My Panel" header */
.page-common .h1{
    background-color: #202020;
    border: 1px solid #2f2f2f;
}
/* "My Panel" header strings */
h1.h1, a.header-right{
    color: white !important;
}

/* main content bg */
div#content{
    background-color: #181818 !important;
}

/* borders of main page (yes, all of them) */
.page-common #content,
body.index .widget .widget-header,
.page-common .borderClass,
body.index .left-column,
body.index .widget.friend_list_updates .item, body.index .widget.clubs .item,
.news-list .news-unit,
body.index .widget.my_list_updates .item,
body.index .widget.reviews .item{
    border-color: #2b2b2b;
}

/* h strings (sub-titles: announcements, watched topics, anime/manga suggestions || seasonal anime || latest updated eps) */
body.index .widget .widget-header,
body.index .widget .index_h2_seo a,
body.index .widget .index_h2_seo{
    color: white;
}
/* p strings (content - only a couple aren't caught) */
p{
    color: #9caaaa !important;
}

/* cards bg */
ul.widget-slide.js-widget-slide{
    background-color: transparent; /* = main content bg */
}

/* seasonal anime */
.widget-slide-block .widget-slide .btn-anime{
    border: 1px solid #303030;
}

/* latest updated eps footer */
span.external-link{
    background-color: #121212 !important;
    height: 15px !important;
}

/* featured articles': string || time sent */
.text,
.spaceit.lightLink.pb0{
    /* obs.: time sent must be important else preliminary tag will overwrite */
    color: #9caaaa !important;
}
/* featured articles' "preliminary" tag */
.spaceit.pb0{
    color: #eeb438;
}

/* anime recomendations "if you liked" / "then you might like" */
.spaceit_pad{
    color: white;
}
/* anime recomendations resume */
.spaceit{
    color: #9caaaa;
}

/* "My Statistics" numbers */
td{
    color: #9caaaa;
}

/* right side widgets' headers */
.ranking-header.side_header_margin{
    background-color: black;
    border-bottom: 5px solid black;
    margin-bottom: 0px;
}

/* right side widgets' bg
--------OVERWRITTEN--------
ul{
    background-color: #2b2b2b;
}
*/

/* strings "Top Airing Anime", "Top Upcoming Anime" and "Most Popular Anime" */
h2.h2_side{
    color: white;
}

/* "add" boxes */
a.Lightbox_AddEdit.button_add,
a.Lightbox_AddEdit.button_edit.ranking-digest.fl-r.plantowatch,
a.Lightbox_AddEdit.button_edit.watching,
a.Lightbox_AddEdit.button_edit.completed,
a.Lightbox_AddEdit.button_edit.on-hold,
a.Lightbox_AddEdit.button_edit.dropped{
    /* all add; ptw on widget; cw, cmp, hold, drop */
    background-color: black !important;
    border-bottom: 1px solid black !important;
    color: #eeb438 !important;
}

/* accessed link */
.page-common a, .page-common a:visited{
    color: #3c66c7;
}

/* footer bg (invisible) */
.page-common #contentWrapper{
    background-color: #202020;
}

/* -------------- */
/*  PROFILE PAGE  */
/* -------------- */

/* below pfp buttons */
a#comment,
span#message,
span#request{
    /* obs.: weird line on right side of request */
    background-color: #121212;
    border: 1px solid #303030;
}

/* user profile li (widgets on left side) */
.user-profile .user-status li,
.user-profile .user-status li:nth-of-type(even){
    background-color: #121212;
    color: white;
    width: 214px; /* makes the border-left visible */
}
/* li border */
.user-profile .user-status.border-top{
    border: 1px solid #303030;
    padding: 0px !important;
}

/* many strings */
h4,.user-profile-about,
.user-friends.pt4.pb12,
.user-profile-sns{
    color: #9caaaa;
}

/* borders */
.page-common .content-container .container-left,
.user-profile h4{
    border-color: #303030;
}

/* "statistics" and stats below */
.page-common .content-container .container-right h2,
.page-common .content-container .container-right h5{
    color: #9caaaa;
    border-color: #303030;
}

/* anime/manga stats bar */
.profile .user-statistics .stats-graph{
    background-color: #2b2d2f; /* if empty */
    border-radius: 19px; /* will not erase any % */
}

/* anime/manga stats status/data */
ul.stats-status.fl-l,
ul.stats-data.fl-r{
    background-color: transparent;
}

/* a couple numbers (statistics) */
.di-tc.al.pl8.fs12.fw-b,
.di-tc.ar.pr8.fs12.fw-b,
span.di-ib.fl-r.lh10,
span.di-ib.fl-r{
    color: #9caaaa;
}

/* last updates bar */
.profile .statistics-updates .data .graph{
    border-color: #303030;
    border-radius: 4px;
    background-color: #2b2d2f;
}

/* favorites lists */
p.favorites-list,
ul.favorites-list.characters{
    background-color: transparent;
}

/* comment area */
textarea.textarea{
    background-color: #121212;
    border-color: #303030 !important;
}

/* removes recaptcha icon */
.grecaptcha-badge{
    display: none !important;
}

/* -------------------- */
/*  ANIME/MANGA SEARCH  */
/* -------------------- */

/* strings above search bar */
.page-common #horiznav_nav ul li{
    background-color: #181818;
}
.page-common #horiznav_nav ul li a{
    background-color: #181818;
    color: #9caaaa;
}

/* search bar */
input#q,
input.inputButton.notActive{
    background-color: #121212 !important;
    border-color: #303030 !important;
    color: #9caaaa !important;
}

/* strings */
span,
.breadcrumb,
.normal_header.pt24.mb0{
    color: #9caaaa;
}

/* borders */
.page-common .normal_header,
.genre-list-col,
.genre-list.al,
.page-common #horiznav_nav{
    border-color: #303030 !important;
}

/* hover over links */
.anime-manga-search .genre-link .genre-list .genre-name-link:hover{
    background-color: transparent;
    color: lightblue;
}

/* ---------------- */
/*  TOP ANIME/MANGA */
/* ---------------- */

/* header string */
h2.fs18.fw-b.ff-avenir.mt24.top-rank-header2{
    color: #9caaaa;
}

/* list header */
td.rank,
td.title,
td.score,
td.your-score,
td.status{
    border-color: black !important;
}

/* list */
td.rank.ac,
td.title.al.va-t.word-break,
td.score.ac.fs14,
td.your-score.ac.fs14,
td.status.ac{
    background-color: #181818 !important;
    border-left: 1px solid transparent;
    border-bottom: 1px solid #303030;
}

/* status */
a.Lightbox_AddEdit.btn-addEdit-large.btn-anime-watch-status.js-anime-watch-status.watching,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.completed,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantoread,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantowatch{
    border-color: #303030;
    background-color: #121212;
    color: #9caaaa;
}

/* --------------- */
/*  SEASONAL ANIME */
/* --------------- */

/* navigation header */
.navi-seasonal{
    background-color: #181818;
}
ul{
    background-color: #181818;
}
.horiznav_nav{
    border-color: #303030 !important;
}

/* navigation header buttons */
.navi-seasonal .horiznav_nav ul li a,
.navi-seasonal .horiznav_nav ul li a.navtab.horiznav_active{
    background-color: #121212 !important;
    border-color: transparent !important;
    color: #9caaaa;
}
/* "Schedule" and "Archive" buttons */
.navi-seasonal .horiznav_nav ul li a.navtab:hover{
    transition: none !important;
    border-color: transparent;
}
/* removes ~border-bottom */
.navi-seasonal.fixed:after{
    display: none;
}

/* "Jump to" buttons */
select#filterByType,
input.inputtext.mr4{
    background-color: #121212;
    color: #9caaaa;
}

/* filter buttons */
.navi-seasonal .horiznav-nav-seasonal .btn-seasonal .btn-type{
    background-color: #121212;
    color: #9caaaa;
}

/* filter border */
.navi-seasonal .horiznav-nav-seasonal{
    border-color: #303030;
}

/* adds padding */
p.fs11.ar.fn-grey2.pt8.pr4{
    padding: 0 0 10px 0;
}
/* adds border */
.navi-seasonal.js-navi-seasonal.fixed{
    border-bottom: 5px solid #303030;
}

/* card border */
.seasonal-anime-list .seasonal-anime{
    border-color: #303030;
}
/* card backgrounds */
.seasonal-anime-list .seasonal-anime .prodsrc,
.seasonal-anime-list .seasonal-anime .genres,
.seasonal-anime-list .seasonal-anime .information .info,
.seasonal-anime-list .seasonal-anime .information .scormem{
    background-color: #181818;
}
/* card bg small fix */
.seasonal-anime-list .seasonal-anime .image{
    background-color: transparent;
}
/* strings */
span.source{
    color: #9caaaa !important;
}
/* genre buttons */
span.genre{
    background-color: #181818 !important;
    border-color: #303030 !important;
}
/* borders small fix */
.seasonal-anime-list .seasonal-anime .genres,
.seasonal-anime-list .seasonal-anime .information{
    border-color: #303030 !important;
}

/* status button */
a.Lightbox_AddEdit.button_edit.btn-anime-watch-status.js-anime-watch-status.plantowatch{
    background-color: black;
    color: #eeb438;
    border-color: black;
}

/* ------------ */
/*  MANGA STORE */
/* ------------ */

/* DISCLAIMER: it is not available for me */

.manga-store-error-404_description{
    background-color: transparent;
    border-color: #303030;
    color: #2e51a2;
}

/* --------------------- */
/*  ANIME EPISODE VIDEOS */
/* --------------------- */

/* header border */
.video-menu-category{
    border-color: #303030;
}

/* "Most Popular" button */
.watch .video-menu-category .btn-category{
    background-color: #121212;
}

/* status button */
a.Lightbox_AddEdit.button_edit.fl-r.ml4.plantowatch{
    background-color: black;
    color: #eeb438;
}
/* status button - add */
a.Lightbox_AddEdit.button_add.fl-r.ml4{
    border-color: black;
}

/* -------------------- */
/*  ANIME/MANGA REVIEWS */
/* -------------------- */

/* review border */
.spaceit.textReadability.word-break.pt8.mt8,
.page-common .borderDark{
    border-color: #303030 !important;
}

/* review icons (anime & pfp) */
.page-common div.picSurround img{
    background-color: #303030;
    border-color: #303030;
}

/* --------------------------- */
/*  ANIME/MANGA RECOMENDATIONS */
/* --------------------------- */

/* strings */
.spaceit_pad,
a.header-right{
    color: #9caaaa !important;
}

/* page number */
.page-common .bgColor1{
    background-color: #181818;
}

/* --------------------------- */
/*  2021 ANIME/MANGA CHALLENGE */
/* --------------------------- */

/* header string */
.h1{
    color: white !important;
}

/* strings */
.page-forum .forum_locheader,
input.button.show_button,
.fl-r.pb4{
    color: #9caaaa !important;
}

/* backgrounds */
.page-forum .forum_boardrow1,
.page-forum .forum_boardrow2,
.page-common .quotetext, .page-common .codetext,
.page-forum .forum_boardrowspacer{
    background-color: #181818 !important;
}

/* buttons backgrounds */
a.btn-rect-grey1.pl4.ml8,
.page-common .btn-forum,
input.button.show_button{
    background-color: #121212 !important;
}

/* borders */
a.btn-rect-grey1.pl4.ml8,
.page-common .btn-forum,
.quotetext,
td.forum_boardrow2,
td.forum_boardrow1,
input.button.show_button,
.page-forum .sig,
.page-common .quotetext, .page-common .codetext{
    border-color: #303030 !important;
}

/* ------------------ */
/*  COMMUNITY>>FORUNS */
/* ------------------ */

/* backgrounds */
.forum .forum-board:nth-of-type(even){
    background-color: #181818;
}

/* borders */
.forum .forum-board{
    border-color: #303030;
}

/* search bar */
.forum .forum-search-side .forum-search-select,
.forum .forum-search-side .forum-search-input-text,
.forum .forum-search-side .forum-search-input-submit.notActive{
    background-color: #181818;
    color: #9caaaa;
    border-color: #303030;
}
/* left search bar part */
.forum .forum-search-side .forum-search-select{
    border-right: none;
    border-radius: 10px 0 0 10px;
}
/* right search bar part */
.forum .forum-search-side .forum-search-input-submit.notActive{
    border-radius: 0 10px 10px 0;
}

/* filter buttons */
a.btn-rect-grey1.pl4.mr8,
a.btn-rect-grey1.pl4{
    background-color: #121212;
    border-color: #303030;
}

/* side block borders */
.forum .forum-side-block .header,
.forum .forum-side-block .forum-side-list .forum-post{
    border-color: #303030;
}
}/*moz-doc*/
