/****************************************
  ==== BODY
****************************************/
body {
    background-color: #161616;
    --text: #BFBFBF;
    color: #BFBFBF;
}

/****************************************
  ==== MESSAGES
****************************************/
div.message-error {
    background: #fd4b23;
}

div.message-warning {
    background: #fdaf23;
}

div.message-ok {
    background: #27c314;
}

/****************************************
==== sidebar
****************************************/

div.App-module_sidebar__3Kfdm {
    --background: #303030;
}

/****************************************
==== elements p1
*****************************************/

div.Analyse-module_resultsContainer__1QNn5 {
    --background: #404040;
    
}
div.ui.styled.accordion{
    background: #404040;
}

table.ui.table {
    background: #404040;
    color: #DFDFDF;
}

table.ui.table thead th {
    background: #303030;
    color: #BFBFBF;
}

table.ui.table td.negative, table.ui.table tr.negative{ /*Where you did baad*/
    background: #AA0000 !important;
    color: #FFFFFF !important;
}

table.ui.table td.positive, table.ui.table tr.positive{ /*Where you did baad*/
    background: #00AA00 !important;
    color: #FFFFFF !important;
}

div.ui.message{
    background: #606060;
    color: #EFEFEF
}

div.ui.warning.message {
    background-color: #303030;
    color: #FF3030;
}

/*******************
    Special stuff for outer pages
*******************/

:root{
    --background: #000 !important;
}





