style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.wefrag.com") {

  body, #body {
     background:#222222 !important;
  }

  .container, #shouts .title {
     background:#333333 !important;
  }

  .header, #shouts .content, .warning, .footer {
     background:#444444 !important;
     color:#AAAAAA !important;
  }

  .container table th {
     background:#222222 !important;
     color:#DDDDDD !important;
  }

  .container table .icon {
     opacity:0.5;
  }

  .container table td.by  {
    background:#333333 !important;
  }
  .container table td.replies  {
    color:#AAAAAA !important;
    background:#444444 !important;
  }
  .container table td.last  {
    color:#AAAAAA !important;
    background:#333333 !important;
  }

  .container table tr.even td {
    background:#222222 !important;
  }

 .actions a {
    opacity:0.7;
 }



}
