@-moz-document domain("studio.youtube.com") {
  ::-webkit-scrollbar {
    background: #1b1d20 !important;
    width: 8px !important;
  }
  ::-webkit-scrollbar-thumb {
    border-radius: 100px !important;
    transition: all 0.6s !important;
    background: #32333b !important;
  }
  ::-webkit-scrollbar-thumb:hover {
    cursor: pointer !important;
    background: #3c3d47 !important;
  }
  ::selection {
    color: #ecefff !important;
    background: #3f4763 !important;
  }
  ::-webkit-scrollbar, ::-webkit-scrollbar-corner, ::-webkit-scrollbar, ::-webkit-scrollbar-corner {
    background: #1b1d20 !important;
  }
  ::-webkit-scrollbar-button, ::-webkit-scrollbar-button {
    display: none !important;
  }
  ::-webkit-scrollbar-thumb, ::-webkit-scrollbar-thumb {
    background: #32333b !important;
  }
  ::-webkit-scrollbar-thumb:not(:active):hover, ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #3c3d47 !important;
  }
  ::-webkit-scrollbar-thumb:active, ::-webkit-scrollbar-thumb:hover:active, ::-webkit-scrollbar-thumb:active, ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
  }
  /* vars */
  :root {
    --header-color: #222;
    --accent-color: #FF2222 !important;
    --primary-color: var(--theme-color) !important;
    --ytve-border-color-focused: var(--theme-color) !important;
