style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.jellyneo.net") {

#mainshell {
  background-color: rgba(255, 255, 255, .2);
  border: 1px solid black;
  border-width: 0 1px 1px 1px;
  text-align: center !important;
  min-width: 900px;
  width: 95% !important;
}

#header, #navbar, #footer {
  margin: 0 auto !important;
  width: 900px;
}

#leftnav {
  border-left: 0 !important;
}

#contentshell {
  width: auto !important;
}

#pageshell, #cont_table, #foot_table {
  width: 100% !important;
}

.content {
  width: 98% !important;
}

h1, h2, h3, h4, h5, .accountrow, .pagetitle, .news_story,
/* .accountrow + h1 + p + div[style], /* news div, which vanished */
#bottom_box
{
  width: 100% !important;
}

.issuelist {
  margin: 0 auto !important;
}

.accountrow + h1 + p + div[style] + p + p {
  text-align: center !important;
}

}
