style.css:
@-moz-document url-prefix("https://www.pixiv.net/novel/bookmark.php"), url-prefix("https://www.pixiv.net/bookmark.php") {
.layout-a .layout-column-1 {
    z-index: 99999;
    overflow: auto;
    position: fixed;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
    display: flex !important;
    background-color: rgba(0, 0, 0, 0.32);}
    .ui-layout-west  {
        -webkit-box-flex: 0;
        -webkit-flex: none;
        flex: none;
        width: auto;
        height: auto;
        margin: auto;
        padding: 0;}
        .area_new  {
            -moz-box-flex: 1;
            flex-grow: 1;
            width: auto;
            height: auto;
            margin-bottom: 0;
            border-radius: 24px;
            border: 0;
            background: rgb(31, 31, 31) none repeat scroll 0% 0%;}

._user-profile-card {display: none;}

.area_title {
    margin: 0;
    padding: 0;
    background-color: transparent;}
#bookmark_toggle_btn, .floleft {
    padding: 12px 24px;
    line-height: 24px;
    font-size: 16px;}
    .f12 {
        color: rgba(255, 255, 255, 0.68) !important;}
    [data-type="cloud"], .book_flat_on {display: none;}
.area_title:before {
    content: "絞り込む";
    display: block;
    width: 100%;
    line-height: 56px;
    text-align: center;
    font-size: 16px;
    font-weight: bold;
    color: rgb(255, 255, 255);
}

.area_bookmark {
    position: relative;
    height: 600px;
    width: 440px;
    margin: 0;
    padding: 0;
    overflow: auto;
    will-change: transform;
    direction: ltr;}
#bookmark_list > ul:nth-child(1){width: 100%;}
    .area_bookmark li {
        display: flex;
        -moz-box-pack: justify;
        justify-content: space-between;
        -moz-box-align: center;
        align-items: center;
        margin: 0;
        transition: background-color 0.2s ease 0s;
        text-decoration: none;
        background-color: transparent;}
        .area_bookmark li > a {
            position:relative;
            display: inline-flex;
            flex-direction: column;
            -moz-box-pack: center;
            justify-content: center;
            flex: 1 1 0%;
            width: 100%;
            margin: auto 0px;
            padding: 12px 24px;
            overflow: hidden;
            font-size:14px;
            line-height: 22px;
            font-weight: bold;
            color: rgba(255, 255, 255, 0.68);
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;}
            .area_bookmark li > a:hover {
                text-decoration: none;
                background-color: rgba(255, 255, 255, 0.12);}
        .area_bookmark li > a > span {
            position:absolute;
            right: 24px;
            display: flex;
            -moz-box-pack: center;
            justify-content: center;
            -moz-box-align: center;
            align-items: center;
            flex: 0 0 auto;
            box-sizing: border-box;
            height: 20px;
            min-width: 20px;
            color: rgb(255, 255, 255);
            font-weight: bold;
            padding: 0px 6px;
            background: rgba(0, 0, 0, 0.32) none repeat scroll 0% 0%;
            border-radius: 10px;
            font-size: 10px;
            line-height: 10px;}
}

@-moz-document url-prefix("https://www.pixiv.net/bookmark.php") {
#bookmark_toggle_btn {display: none;}
}
