style.css:
@-moz-document url-prefix("https://v2.horsereality.com") {
.header-menu {
    background: #6b2f1d;
}.side-nav {
    padding: 10px;
    background: #97381c;
    border: 2px solid #f2a05c;
    border-radius: 0px 0px 75px 75px;
    height: 840px;
}.side-nav ul .subtitle {
    font-size: 20px;
    font-family: Parkavenue, cursive;
    margin-bottom: 5px;
    line-height: 21px;
    font-weight: 700;
    text-transform: uppercase;
    color: #5e281c;
}.side-nav ul a {
    font-size: 18px;
    font-family: Gabriola;
    font-weight: 700;
    text-transform: capitalize;
    color: #5e281c;
    padding: 0;
}.minimap {
    float: left;
    width: 150px;
    height: 150px;
    background-position: 50%;
    background-size: cover;
    border-radius: 70px;
}.main-content {
    background: #d8a683;
    padding: 15px;
}
body {
    margin: 0;
    font-family: Gabriola;
    font-size: 18px;
    font-weight: 400;
    line-height: 1.5;
    color: #4b0000;
    text-align: left;
    background-color: #fff;
}.show-profile .profile-account .tag-npc, .show-profile .profile-account .tag-player, .show-profile .profile-account .tag-trial {
    font-size: 18px;
    font-family: Gabriola;
    color: #4b0000;
    display: block;
    text-align: left;
    font-weight: 400;
    padding-left: 0;
}.footer {
    background: #6b2f1d;
    color: #4b0000;
    padding-top: 10px;
    padding-bottom: 10px;
    vertical-align: middle;
    margin-bottom: 50px;
}.footer ul li a {
    color: #4b0000;
}.show-profile .navbar {
    background-color: #85240d!important;
    border-top: 2px solid #f0954e;
    border-bottom: 2px solid #feed81;
    padding-top: 0;
    padding-bottom: 0;
    min-height: 40px!important;
}.show-profile .profile-account {
    width: 100%;
    padding: 10px;
    background-color: #97381c;
    margin-bottom: 20px;
    border: 2px solid #feed81;
    Border-radius: 25px 25px 25px 25px;
}.show-profile .profile-account h2 {
    font-size: 19px;
    font-family: Gabriola;
    font-weight: 600;
    color: #4b0000;
    margin: 0;
}.show-profile .navbar-avatar .navbar-avatar-container {
    z-index: 1;
    width: 150px;
    height: 150px;
    border: 4px solid #5b2214;
    border-radius: 25px 25px 25px 25px;
    position: absolute;
    background-size: cover;
    top: 165px;
    left: 25px;
}.show-profile .profile-name-and-estate .profile-username {
    font-size: 32px!important;
    font-family: Gabriola;
    font-weight: 700!important;
    color: #c16a6a4d;
    margin-bottom: 5px!important;
}.show-profile .profile-name-and-estate .profile-estate {
    font-size: 20px;
    font-family: Gabriola;
    line-height: 21px;
    font-weight: 400;
    color: #a0606040;
}.show-profile .profile-ranking-container .profile-rank-position {
    font-size: 26px!important;
    font-family: Gabriola;
    text-align: right;
    font-weight: 700!important;
    color: #f9c56c7d;
    margin-bottom: 5px!important;
}.show-profile .profile-ranking-container .profile-rank {
    text-align: left;
    margin-bottom: 0;
    font-size: 18px;
    font-family: Gabriola;
    line-height: 18px;
    font-weight: 400;
    color: #fdf48491;
}body.background.africa {
    background: url(https://i.imgur.com/aIpMBTJ.jpg) no-repeat fixed 0 0/100% auto;
    background-size: cover;
}.show-profile .navbar .nav-link {
    padding: 8px 20px;
    line-height: 24px;
    font-weight: 500;
    color: #e49057;
    font-size: 19px;
    font-family: Gabriola;
}.header-menu nav .main-menu .user-nav>a {
    color: #fad17e;
    font-size: 22px;
    font-family: Gabriola;
}.header-menu nav .main-menu ul li a {
    font-size: 22px;
    font-family: Gabriola;
    color: #f9d17d;
}.header-menu nav .stats {
    text-align: right;
    color: #faae60;
    font-size: 18px;
    font-family: Gabriola;
}.header-menu nav .stats a {
     position: relative; 
	top: -8px;

}.side-nav ul a:hover {
    color: #5e281c;
    text-decoration: none;
}.header-menu nav .main-menu .user-nav>a:hover {
    color: #f9d17d;
}.header-menu nav .main-menu ul li a:not(.btn):hover {
    text-decoration: none;
    color: #e8bd71;
}.header-menu nav .stats a {
    color: #deb26b;
}.header-menu nav .main-menu .user-nav .dropdown-menu {
    left: -135px!important;
    min-width: 12rem!important;
    background: #6b2f1dab;
}.header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-header, .header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item {
    font-size: 16px;
    padding: .5rem 1rem;
    color: #f9d17d;
}.progress-bar {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-pack: center;
    justify-content: center;
    color: #e3b86e;
    text-align: center;
    white-space: nowrap;
    background-color: #6b2f1d;
    transition: width .6s ease;
}p {
    font-family: Garamond;
    font-weight: 400;
    font-size: 20px;
    line-height: 18px;
    color: #4b0000;
}.header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item:hover {
    background: 0 0;
    color: #d4a361;
}.estate-item {
    background-color: #c97245;
    border: 2px solid #6b2f1d;
    border-radius: 15px 15px 15px 15px;
    padding: 10px 15px;
    width: 100%!important;
    height: 100%;
}.estate-item .estate-item-buy button {
    font-size: 13px;
    line-height: 18px;
    font-family: Roboto,sans-serif;
    font-weight: 600;
    border-radius: 2px;
    border: none;
    padding: 3px 8px;
    color: #fc9a4e;
    background-color: #6b2f1d;
}.estate-item .estate-item-buy button:hover {
    background-color: #401c11;
    cursor: pointer;
    transition: all .3s ease 0s;
}.estate-item .estate-item-name {
    font-size: 14px;
    text-align: center;
    font-weight: 700!important;
    color: #6b2f1d;
    margin-bottom: 15px;
}.component.npc {
    display: flex;
    background: #d2835a;
    border: 2px solid #6b2f1d;
    border-radius: 15px 15px 15px 15px;
}.card-item {
    background-color: #d2835a;
     border: 2px solid #6b2f1d;
    border-radius: 15px 15px 15px 15px;
    padding: 10px 15px;
    width: 100%!important;
    height: 100%;
    display: flex;
    flex-direction: column;
}
.card-item-name {
    font-size: 16px;
    font-family: times new roman;
    text-align: center;
    line-height: 1;
    font-weight: 700!important;
    color: #6b2f1d;
    margin-bottom: 15px;
    min-height: 28px;
}.card-item-action button.black {
    background-color: #6b2f1d;
}.card-item-action button {
    width: 100%;
    font-size: 13px;
    line-height: 18px;
    font-family: Roboto,sans-serif;
    font-weight: 600;
    border-radius: 2px;
    border: none;
    padding: 3px 8px;
    color: #f2a05c;
    background-color: #ffa200;
}.card-item-action button {
    width: 100%;
    font-size: 13px;
    line-height: 18px;
    font-family: Roboto,sans-serif;
    font-weight: 600;
    border-radius: 2px;
    border: none;
    padding: 3px 8px;
    color: #d4855c;
    background-color: #6b2f1d;
}.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + .75rem + 2px);
    padding: .375rem .75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #6b2f1d;
    background-color: #d8a683;
    background-clip: padding-box;
    border: 1px solid #6b2f1d;
    border-radius: .25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}.component.frame .wrapper {
    background: #792613;
    padding: 2px;
}.freezer .freezer-table td {
    background-color: #c48967;
    padding: 8px 10px;
}.freezer .freezer-horse-id, .freezer .freezer-tagline {
    height: 18px;
    font-weight: 700;
    color: #6b2f1d;
    font-size: 13px;
}.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #6b2f1d;
    font-family: Garamond;
}.freezer .selected-vial .content {
    background-color: #c48967!important;
}.btn {
    font-size: 13px;
    color: #f2a05c;
}
.btn-danger {
    color: #fb9950;
    background-color: #6b2f1d;
    border-color: #f2a05c;
}.btn.btn-cta, .btn.btn-primary {
    color: #d8a683;
    background-color: #6b2f1d;
    border-color: #f2a05c;
    font-weight: 600;
}.btn.btn-cta:hover, .btn.btn-primary:hover {
    background-color: #6b2f1d;
    border-color: #f2a05c;
}.btn-danger:hover {
    color: #f2a05c;
    background-color: #6b2f1d;
    border-color: #f2a05c;
}.component.frame .wrapper .header .title {
    display: inline-block;
    font-size: 18px;
    color: #f2a05c;
    text-transform: none;
}.freezer .freezer-table:hover td {
    background-color: #a5755a!important;
}.component.table .table thead th {
    background: #c07350;
    border: none;
    padding: 5px 10px;
}.table .thead-dark th {
    color: #792613;
    background-color: #343a40;
    border-color: #454d55;
}.component.table .table tbody tr:nth-of-type(odd) {
    background-color: #e2bea4;
}.component.table .table tbody {
    background-color: #d0b099;
}a {
    color: #6b2f1d;
}.breadcrumb {
    display: -ms-flexbox;
