    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    padding: .75rem 1rem;
    margin-bottom: 1rem;
    list-style: none;
    background-color: #e2bea4;
    border-radius: .25rem;
}.component.table .table {
    background-color: #e2bea4;
    margin: 0;
}.checkbox-label .checkbox-custom {
    position: absolute;
    top: 0;
    left: 0;
    height: 24px;
    width: 24px;
    border-radius: 5px;
    transition: all .3s ease-out;
    -webkit-transition: all .3s ease-out;
    -moz-transition: all .3s ease-out;
    -ms-transition: all .3s ease-out;
    -o-transition: all .3s ease-out;
    border: 2px solid #792613;
    background-color: #e2bea4;
}a:hover {
    color: #6b2f1d;
    text-decoration: none;
}.btn.btn-cta:hover, .btn.btn-primary:hover {
    background-color: #6b2f1d;
    border-color: #e3965a;
}.btn-warning:not(:disabled):not(.disabled).active, .btn-warning:not(:disabled):not(.disabled):active, .show>.btn-warning.dropdown-toggle {
    color: #f19e5b;
    background-color: #5a2c1e;
    border-color: #6b2f1d;
}.shows.show .mine td {
    background-color: #c07350;
    border-bottom: 1px solid #6b2f1d;
}.component.table .table tbody tr:nth-of-type(odd) {
    background-color: #d0b099;
}.component.frame .wrapper .subheader {
    background-color: #bc7a5e;
    color: #6b2f1d;
    font-weight: 600;
    padding: 5px 10px;
}.component.frame .wrapper .content {
    background: #d8a683;
}p a:active, p a:link, p a:visited {
    color: #6b2f1d;
    text-decoration: none;
    font-weight: 600;
}h1 {
    font-size: 26px;
    line-height: 30px;
    font-family: Roboto,sans-serif;
    font-weight: 400;
    margin-bottom: 15px;
    color: #6b2f1d;
}.clubs nav.navbar.navbar {
    background-color: #6b2f1d!important;
}.clubs nav.navbar.navbar .navbar-nav .nav-link {
