    padding: 5px 0;
}.component.comments .comment .header a {
    color: #6b2f1d;
    text-decoration: underline;
}.component.comments .comment .profile-wrapper {
    background-color: #d8a683;
    padding-left: 15px;
    padding-right: 15px;
    min-width: 187px;
}.component.frame .wrapper .header .action .light {
    color: #f2a05c;
    font-weight: 500;
}.component.frame .wrapper .header .action .light>.btn, .component.frame .wrapper .header .action>.btn {
    display: inline;
    padding: 2px 5px 1px;
    font-size: 18px;
    line-height: 12px;
    vertical-align: middle;
}.your-rank-text {
    color: #f2a05c;
    line-height: 21px;
    font-size: 18px;
    font-weight: 700;
    justify-content: end;
}.city .mobile-world-menu a {
    font-size: 18px;
    font-weight: 700;
    text-transform: capitalize;
    color: #6b2f1d;
}.job-item {
    background-color: #d2835a;
    border: 2px solid #6b2f1d;
    border-radius: 15px 15px 15px 15px;
    text-align: center;
    padding: 10px 15px;
    width: 100%!important;
    height: 100%;
}.job-minutes {
    color: #6b2f1d;
    text-transform: uppercase;
    font-size: 14px;
}.job-progress {
    background-color: #d8a683;
}.progress span {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
