    font-size: 13px;
    font-family: times new roman;
}nav .pagination .page-item.active .page-link {
    color: #d0b099;
    font-weight: 700;
    background: #6b2f1d;
}nav .pagination .page-item:not(.disabled) .page-link {
    color: #d8a683;
}
nav .pagination .page-item .page-link {
    background: #792613;
    padding: 5px 10px;
    margin: 0 3px;
    text-align: center;
    border: none;
    border-radius: 3px;
}.alert-success {
    color: #6b2f1d;
    background-color: #e2bea4;
    border-color: #6b2f1d;
}.table td, .table th {
    padding: .75rem;
    vertical-align: top;
    border-top: 1px solid #6b2f1d;
}.shows .table .progress .progress-bar {
    background-color: #792613;
    background: -webkit-gradient(linear,0 0,0 100%,from(#792613),to(#2d0e07));
}.shows .table .progress .progress-text {
    position: absolute;
    width: 100%;
    padding: 2px 0 0;
    text-align: center;
    color: #e2bea4;
}


}

