/* ==UserStyle==
@name           Eldarya - More Darker v1.0
@namespace      USO Archive
@author         MariaLovesQueen
@description    `A little more darker theme`
@version        20210413.12.14
@license        NO-REDISTRIBUTION
@preprocessor   uso 
==/UserStyle== */
@-moz-document domain("eldarya.ru"), domain("eldarya.pl"), domain("eldarya.fr"), domain("eldarya.com.br"), domain("eldarya.com"), domain("eldarya.de"), domain("eldarya.es"), domain("eldarya.fi"), domain("eldarya.hu"), domain("eldarya.it"), regexp("http://(fa1|fa2|www)\\.eldarya\\.(dk|nl|ro|se|uk)\\/.*") {
/*Small Crystal ======================================================================================*/
#header #crystal-images-container {
    height: 0 !important;
}

#top-bar::before {
    background-position: top center !important;
    background-size: 20% 100% !important;
}

#header #crystal-images-inner, #crystal-images-inner > img, #crystal-images-container {
    height: 25px !important;
}

#crystal-images-inner > img {
    padding-left: 79px !important;
}

/* More Darker Stuff for Theme ========================================================================= */
.page-main-container {
    min-height: 927px !important;
}

div[style="max-height: 143px;"], #mall-categories, #mall-products, #mall-productDetail {
    min-height: 360px !important;
}

.section-subtitle, .section-title {
    font-size: 14px !important;
    line-height: 14px !important;
    margin: 5px auto 0px auto !important;
}

.guard-4 {
    font-size: 45px !important;
    line-height: 45px !important;
    margin: 0px auto 10px auto !important;
}

#mall-menu li {
    margin: 1px 1px !important;
    padding: 2px !important;
    width: 20px !important;
    height: 20px !important;
    border-radius: 6px !important;
}

#marketplace-open-help, #marketplace-help-section-close {
    top: -10px !important;
}

.carousel-container, .carousel .page {
    width: 1172px;
}

.diaries-info, #library-npc-crush {
    margin: -56px 0 0 0 !important;
}
    
/* Mall ======================================================================*/

#mall-preview {
    top: -70px !important;
}

#mall-productDetail-info-legacy {
    top: -172px !important;
    }
    
#mall-productDetail-info {
    height: 100px !important;
    }
    
#mall-productDetail-info .mall-productDetail-name {
        min-height: 60px !important;
    }

/* Colors ================================================================================ */
#season-switcher, .eggs-interface .egg-container {
    background-color: rgba(50, 21, 64, .7) !important;
}

.page-main-container:before {
    content: linear-gradient(rgba(50, 21, 64, .7), rgba(50, 21, 64, .7)) !important;
    display: block !important;
    width: 100% !important;
    height: 100% !important;
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
}

#menu-bar {
    background: linear-gradient(rgba(50, 21, 64, 1) 30%, rgba(50, 21, 64, .7) 75%, rgba(50, 21, 64, 0)) !important;
}

#player-dropdown-menu #player-level-bar, #pet-infos {
    background-color: rgba(255, 255, 255, .8) !important;
}

#malls-list li h2, #account-right, #account-left::before, #layout-2col-left:before, #right-bg, #right-bg::before, #background-gradient, #layout-2col #layout-2col-right {
    background: rgba(0, 0, 0, 0) !important;
}

.marketplace-search-items, .marketplace-error-message, .marketplace-assistance, #marketplace-help-section, .diaries-info, .npcs-library-container .header #library-npc-crush, #player-dropdown-menu #player-infos-container *, .eggs-interface h2, #bank-page .product-panel h2, .account-social-label, .lost-transactions-main-container .payment-method-form, #account-section .form-row > label, #right-container-inner #title-container p, #name-container *, .section-subtitle, .section-title {
    color: #fef1ff !important;
}

#rename-pet *, #name-container h3 #rename-pet #confirm-rename.gold .rename-price, #player-dropdown-menu #player-level-bar p, #minigames-list .section-subtitle, #lost-transactions-left-bloc * {
    color: rgb(50, 21, 64) !important
}

#season-switcher::after {
    display: none !important;
}

/* Optional additions =========================================================== */
.marketplace-abstract .abstract-icon, #player-dropdown-menu #player-infos-container {
    background: rgb(50, 21, 64) !important;
}

.marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item, .marketplace-sales-item, #currencies .currency, #pet-infos, #player-stats, #account-personal-bloc {
    background-color: rgba(50, 21, 64, .7) !important;
}

button, .flavr-button, .nl-button {
    background: #934596;
}

#pet-return {
    background: rgb(147, 69, 150) !important;
}

.attic-description, .abstract-currentPrice, .abstract-name, #currencies #currency-legacy, .not-met, #pet-infos > p, #player-stats, #birthday-form, #account-email-input {
    color: #fef1ff !important;
}

.flavr-button.active, .nl-button.active {
    color: rgb(50, 21, 64) !important;
}

.abstract-type {
    color: #934596 !important;
}

.mall-category:after, .mall-product:after, .mall-variation:after, .marketplace-abstract .abstract-content, .library-info h3, button, .flavr-button, .nl-button, #account-form-personal .form-row, #player-dropdown-menu #player-infos-container hr {
    border-color: rgba(0, 0, 0, 0) !important;
    box-shadow: none !important;
}

.menu-story li, #malls-list li, .minimap, .pet-interface-container #treasure-hunt-interface .lens, .pet-resource, .marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item, .marketplace-abstract .abstract-icon, .marketplace-sales-item, .episode-body, .diaryEntryPage, .library-pet, .library-main-container .picture-box .picture-container, .library-npc, .menu-library li, #minigames-list li, #pet-infos, #player-stats, #account-personal-bloc {
    box-shadow: 0 0 15px rgba(9, 191, 198, 0.55), 0 5px 4px 1px rgba(0, 0, 0, 0.1) !important;
    border: 2px solid #fff !important;
}

.menu-story li::before, .menu-story li, #malls-list li, .appearance-category-icon, .appearance-item-icon, .mall-category, .mall-product, .mall-variation, .appearance-category, .appearance-item, .appearance-item-group, .minimap, .pet-interface-container #treasure-hunt-interface .lens, .pet-resource, .marketplace-abstract .abstract-icon, .marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item, .marketplace-sales-item, #inner-grid #home-content-smalls .home-content-small, #inner-grid #home-content-bigs .home-content-big, .episode-body, .diaryEntryPage, .library-pet, .library-main-container .picture-box .picture-container, .library-npc, .menu-library li.current::before, .menu-library li, #minigames-list li, #pet-infos, #player-stats, #account-personal-bloc, .flavr-button, .nl-button, button {
    border-radius: 12px !important;
}

.pet-resource .gauge {
    border-radius: 0 0 8px;
}

#incubator #incubator-text #incubator-text-inner #pet-return::after {
    filter: hue-rotate(85deg) !important;
}

/* Fix Ptoblems ================================================================================================= */
.pet-interface-container > #treasure-hunt-interface > #pending-map-location-data-outer > #pending-map-location-data > #pending-actions {
    display: none;
}

#main-section.treasure-hunt-interface-open > #treasure-hunt-interface > #pending-map-location-data-outer > #pending-map-location-data > #pending-actions {
    display: block !important;
}
}
