style.css:
@-moz-document url-prefix("https://storyfire.com") {
/* Home */
.main-container .top-panel {
    background-color: #fff
}
.main-container .logged-user-top-panel a,
.main-container .logged-user-top-panel button {
    color: #000
}
.searchTerm {
    border: 1px solid #cecccc;
    background: #d0cfcf;
    color: #000;
}
.searchButton {
    border: 1px solid #cecccc;
    background: #d0cfcf;
}

.main-container .top-panel .hamburger .hamburger-line {
    background-color: #000;
}

.main-container .left-menu {
    background-color: #bebfbf
}

.main-container .left-menu .item .icon-block .label {
    color: #242424
}

.main-container .left-menu .social .social-item {
    border-top: 1px solid #cecccc;
}

.main-container .left-menu .social .social-item:hover {
    background-color: #ddd;
}

.main-container .left-menu .item .text {
    color: #242424;
}
.main-container .left-menu .item {
    border-top-color: #cecccc
}
.main-container .left-menu .item:hover {
    background-color: #ddd
}
.main-container.full .left-menu .social .social-item:hover {
    background-color: #ddd
}
.main-container.full .left-menu .social {
    border-top: 1px solid #cecccc
}
.main-container .content-container {
    background-color: #edeff4
}
.carusel-container .centered-container .top-line .title {
    color: #000
}

.carusel-container .centered-container .carousel .slide-item {
    background-color: #d0cfcf
}

.video-carusel.carusel-container .centered-container .slide-item .data div.view-block .title-container .title,
.video-carusel.carusel-container .centered-container .slide-item .data div.view-block .title-container span .title {
    color: #0b0b0b
}

.username-block .user-name {
    color: #222
}

.video-carusel.carusel-container .centered-container .slide-item .data div.view-block .info {
    color: #2d2d2d
}

.creator-carusel.carusel-container .centered-container .slide-item:hover {
    background-color: #ddd
}

.creator-carusel.carusel-container .centered-container .slide-item .link .name {
    color: #242424
}

.subscribe-btn {
    background-color: #fff
}

.searchTerm:focus {
    background: #d0cfcf;
    border: 1px solid #cecccc;
    color: #000;
}
.video-carusel.carusel-container .centered-container .slide-item .link .time {
    background-color: #2f2e2e;
}
.tippy-tooltip.storyfire-theme {
    background-color: rgba(159, 159, 159, 0.9);
}
.main-container .profile-contextual-menu .header h5 {
    color: #2b2b2b
}
.main-container .profile-contextual-menu .item button {
    color: #131313
}

/*video-details*/
.header-line .inline-header .title {
    color: #1e1e1e;
}
.video-details .centered-container .wrapper .episode-container .title {
    color: #000
}
.video-details .centered-container .wrapper .video-container .description {
    color: #202
}
.video-details .centered-container .wrapper .video-container .video-bottom .video-info {
    color: #262626
}
.video-details .centered-container .wrapper .comment-container .title-line .title {
    color: #2b2b2b;
}
.video-details .centered-container .wrapper .episode-container .list .item .title {
    color: #171717;
}
.comment-list .item .item-data .creator .info .user-name {
    color: #3e3d3d
}
.scl-card {
    background-color: #d0cfcf;
}
.scl-card__info-title {
    color: #0b0b0b;
}
.scl-card__info-subscribe,
.scl-card__info-username,
.scl-card__info-view {
    color: #242424;
}
.scl-subscribe__button--unsubscribe {
    background-color: #2f2e2e;
}

/*Social*/
.upload,
input,
select,
textarea {
    background-color: #bbb;
    color: black
}

.upload,
input,
select,
textarea:focus {
    background-color: #b9b1b9;
    color: black
}

.main-container .centered-container.social-feed-page .social-sidebar .section,
.reply-modal-container .social-sidebar .section {
    background-color: #ddd;
}

.main-container .centered-container.social-feed-page h2,
.reply-modal-container h2 {
    color: #242424
}

.main-container .centered-container.social-feed-page .social-sidebar .section h3,
.reply-modal-container .social-sidebar .section h3 {
    color: #000
}
.main-container .content-container {
    color: #111;
}

.main-container .social-container.centered-container .posts-list .post-item,
.main-container .tabbedSearch .posts-list .post-item {
    background-color: #fff;
}
.main-container .social-container.centered-container .posts-list .post-item:hover,
.main-container .tabbedSearch .posts-list .post-item:hover {
    background-color: #ddd;
}

.main-container .social-container.centered-container .posts-list .post-item .description,
.main-container .tabbedSearch .posts-list .post-item .description {
    color: #000
}

.main-container .social-container.centered-container .posts-list .post-item .intro-header .info .bottom-line .user-name,
.main-container .tabbedSearch .posts-list .post-item .intro-header .info .bottom-line .user-name {
    color: #000
}

.social-container.centered-container.post-details .back-header .go-back {
    color: #000
}

/*Socail-details*/
.main-container .social-container.centered-container .posts-list .single-post,
.main-container .tabbedSearch .posts-list .single-post {
    background-color: #fff;
}

.reply-modal-container .posts-list .post-item .intro-header .info .bottom-line .date,
.social-container.centered-container .posts-list .post-item .intro-header .info .bottom-line .date,
.tabbedSearch .posts-list .post-item .intro-header .info .bottom-line .date {
    color: #000
}

.social-container.centered-container.post-details .first-comment {
    background-color: #fff;
}

.social-container.centered-container.post-details .posts-list > .comment-list .item.line-comment {
    background-color: #fff;
}

.comment-list .item .item-data .text {
    color: #000
}

.first-comment .reply-form .comment-input > .textarea-block textarea {
    color: #000
}

.shared-story {
    background-color: #ced6e6;
}

.first-comment .reply-form .button-group .button {
    color: #111;
    border: 1px solid #cecccc;
}

.main-container .centered-container.social-feed-page .social-leaderboard.section .position .creator .link .info,
.reply-modal-container .social-leaderboard.section .position .creator .link .info {
    color: #242020;
}

.social-container.centered-container.post-details .posts-list > .comment-list .item.line-comment .item-data .creator .date {
    color: #000
}

.main-container .centered-container.social-feed-page .social-featured-posts.section .post .post-meta .date,
.reply-modal-container .social-featured-posts.section .post .post-meta .date {
    color: #222;
}

/*User*/
.profile-video .nav button {
    color: #000
}

.profile-header .profile-header-content .profile-bio {
    color: #242424
}

.profile-nav .link-container a,
.profile-nav .link-container a:active,
.profile-nav .link-container a:hover,
.profile-nav .link-container a:link {
    color: #000;
}

.scl-metric-box {
    background: #fff;
}

.scl-metric-box:hover {
    background-color: #ddd
}

.scl-metric-box.small .scl-metric-box__details-metric-stat {
    color: #373737;
}
.profile-nav {
    border-bottom: 1px solid #cecccc;
}

.profile-video .container .slide-item .data div.view-block .title-container .title,
.profile-video .container .slide-item .data div.view-block .title-container span .title {
    color: #171717;
}

.profile-video .container .slide-item .data div.view-block .info {
    color: #242424;
}

.profile-video .container .slide-item .link .time {
    background-color: #2f2e2e;
}

.subscribe-btn .subscribe-btn-action.subscribe {
    color: #000
}

.subscribe-btn {
    color: #000
}

/*Followers and Following*/
.user-lists .container .user-card {
    background-color: #c5c6ca;
}
.user-lists .container .user-card:hover {
    background-color: #888;
}
/*Blaze*/
.main-container .content-container .centered-container.blaze-container .header .text {
    color: #171717
}
.main-container .content-container .centered-container.blaze-container .content .column .list .list-item .text {
    color: #393939;
}
.main-container .content-container .centered-container.blaze-container .content .column .list .sale-list > tbody > tr > td {
    color: #373737;
}
.main-container .content-container .centered-container.blaze-container .content .column .list .sale-list > tbody > tr > td.price button {
    color: #242424
}
.main-container .content-container .centered-container.blaze-container .content .picture .centered .text {
    background-color: #bebfbf;
    color: #111
}


/*Leaderboard*/
.container-fluid .statement-panel .period-switch ul li button,
.main-container .content-container .centered-container.leaderboard .statement-panel .period-switch ul li button {
    color: #000;
}

.blazers .list .item .leader-info .name,
.creators .list .item .leader-info .name,
.readers .list .item .leader-info .name,
.top .list .item .leader-info .name {
    color: #000;
}

.container-fluid .statement-panel .period-switch ul li.active button,
.main-container .content-container .centered-container.leaderboard .statement-panel .period-switch ul li.active button {
    color: #000;
}

/*Wallet*/
.profile-wallet .wallet-header .text {
    color: #202020;
}
.profile-wallet .wallet-header .current-blaze .value .points {
    color: #1c1c1c;
}
.profile-header .profile-header-content .profile-card .settings-link {
    color: #000
}

.profile-wallet .wallet-content .column.blaze .earn-blaze {
    background-color: #bebfbf
}
/*Legal*/
.legal-policies .copyright .c2 {
    color: #060606
}
.legal-policies .top-button .navigate {
    color: #000;
}
}
