body {
    background-color: #08000E!important;
    color: #f9eecf;
    font-family: 'Open Sans' serif;
}


#content {
    display: flex;
    padding: 5em 20px 10px;
    background: #08000E!important;
    min-height: 100%;
}

.logo 
{
     border-radius: 0!important;
    font-size: 1.5em;
    text-transform: uppercase;
    font-weight: 700;
    border-bottom: 31px solid #f9eecf!important;
    background-image: url("https://i.imgur.com/76NAMiv.jpg")!important;
    
}



.bloc 
{
    background-color: #322E2D9E!important;;
    border:1px solid #F9EECF!important;
}

p
{
    
    color: #F9EECF !important;
        font-size: 12px;
}

a
{
    
    color: #F9EECF !important;
        font-size: 12px;
}
a:hover
{
    
    color: #F9EECF !important;
    text-decoration: underline;
        font-size: 12px;
}

.title
{
    color: #F9EECF !important;
    font-family: 'Poiret One', sans-serif!important;
    
}

.bloc .content.links a {
    display: flex;
    align-items: center;
    padding: 8px 16px;
    font-size: .85em;
    color: #F9EECF!important;;
    font-family: 'Poiret One', sans-serif!important;
}


/* Liste des topics */ 

.title.is_sticky 
{
    
    border-bottom:0px solid #F9EECF!important;
    background-color: #322e2d!important;
}

body.forum .topics .topic {
    display: flex;
    flex-wrap: wrap;
    align-items: middle;
    padding: 2px;
    border-top: 1px solid #F9EECF!important;
    position: relative;
    transition: background color 75ms ease-in-out;
}


.content.topics
{
    border:0px solid #F9EECF!important;
    font-family: 'Poiret One', sans-serif!important;
    
}

body.forum .topics .topic .topic-subject span {
    font-weight: 500;
   font-family: 'Poiret One', sans-serif!important;
    overflow: hidden;
}


body.windows ::-webkit-scrollbar {
    width: 16px;
    background-color: #08000E!important;
}

body.windows ::-webkit-scrollbar {
    width: 16px;
    background-color: #08000E!important;
}

.bloc .title.sticky {
    margin-left: 1px;
    margin-right: 1px;
    border: none;
    background-color: #322e2d!important;
     border:1px solid #F9EECF!important;
}


body.windows ::-webkit-scrollbar {
    width: 16px;
    background-color: #08000E!important;
}

body.windows ::-webkit-scrollbar-track {
    background-color: #08000E!important;
}

body.forum.grey .topics .topic .right span, body.forum.grey .topics .topic .topic-subject .topic-nb, body.forum.grey .topics .topic .topic-subject .topic-username {
    color: #F9EECF;
}

.color.green {
    color: #F9EECF!important;
}

/* footer */ 
footer a {
    display: inline-flex;
    align-items: center;
    color: #F9EECF;
    font-family: 'Poiret One', sans-serif!important;
}

/* input */
.input {
    padding: .8em;
    background: #0A0808A3!important;
    border: 0px solid #F9EECF!important;;
    width: 100%;
    color: #F9EECF!important;
    font-family: Roboto,arial,sans-serif;
    font-size: .9em;
    border-radius: 6px;
}


.textarea {
    padding: .8em;
    width: 100%;
    min-height: 120px;
    max-height: 500px;
    background: #0A0808A3!important;
    border: 0px solid #F9EECF!important;;
    color: #000;
    resize: vertical;
    border-radius: 6px;
    font-family: Roboto,arial,sans-serif;
    font-size: .9em;
}

textarea::placeholder {

        color: #F9EECF8F !important;
        font-family: 'Open sans', sans-serif;
    }

    input::placeholder {
        color: #F9EECF !important;
        font-family: 'Poiret One', sans-serif;
    }


/* items */

.format .items .item  {
    display: flex;
    align-items: center;
    margin: 0 5px 0 0;
    padding: 5px;
    font-size: 1em;
    border-radius: 3px;
    color: #F9EECF!important;
    transition: background .3s;
    cursor: pointer;
    min-width: 30px;
    min-height: 30px;
}

.item.onche
{
    color: #F9EECF!important;
    opacity: 1;
}


.format .items .item.onche:before {
    position: absolute;
    background-image: url(https://i.imgur.com/5TySJIw.png)!important;;
    background-repeat: no-repeat;
    background-size: 22px 22px;
    top: 8px;
    left: 4px;
    right: 4px;
    bottom: 4px;
    content: "";
}

.format .items .item.active, .format .items .item:hover {
    background: #322E2D!important;;
}


/* boutons */

body.grey .button.bordered {
    border: 1px solid #F9EECF;
}

.button.filled {
    background: #322E2D9E!important;;
    border: 1px solid #F9EECF!important;;
    color: #F9EECF!important;;
    font-family: 'Poiret One', sans-serif!important;
    border-radius: 3px;
}

.button.filled:active {
    background: #322E2D9E!important;;
    border: 1px solid #F9EECF!important;;
    color: #F9CFCF!important;;
    font-family: 'Poiret One', sans-serif!important;
    border-radius: 3px;
}


#tooltip {
    display: none;
    position: absolute;
    z-index: 4;
    background: #08000E!important;
    color: #F9EECF!important;
    font-size: 13px;
    border-radius: 3px;
    overflow: hidden;
    flex-direction: column;
}
body.grey #tooltip .tabs .tab {
    color: #F9EECF;
}

#tooltip .tabs {
    display: flex;
    background: #08000E!important;
    color: #F9EECF!important;
}

#tooltip .tabs.active {
    display: flex;
    background: #322E2D!important;
    color: #F9EECF!important;
}

#tooltip .tabs .tab.active, #tooltip .tabs .tab:hover {
    background: #322E2D!important;
    color: #F9EECF!important;
}


/* page recherche */

body.grey .bloc .content .tabs .item.active {
    background: #322E2D;
    color: #fff;
}

#context .item {
    padding: 11px 20px;
    font-size: 14px;
    text-align: center;
    background-color: #08000E!important;
    color: #F9EECF!important;
    cursor: pointer;
}


/* interieur topics */

body.windows ::-webkit-scrollbar {
    width: 16px;
    background-color: #322E2D9E!important;
}

body.windows ::-webkit-scrollbar {
    width: 16px;
    background-color: #322E2D9E!important;
}

body.windows ::-webkit-scrollbar-thumb {
    border-radius: 16px;
    background-color: rgba(0,0,0,.4);
    border: 5px solid #08000E!important;
}


body.forum.grey .message {
    background: #262428B8;
}

body.forum .message {
    position: relative;
    display: flex;
    margin: 0 0 20px;
    padding: 11px 0;
    border: 1px solid #F9EECF;
    border-radius: 5px;
    overflow: hidden;
    background: #08000E;
    border-radius: 3px;
    box-shadow: 0 2px 0 rgb(0 0 0 / 5%);
}


body.forum.grey .message .message-right .message-content {
    color: #F9EECF;
    font-family: 'Open Sans' serif;
    font-size: 13px;
}


body.forum.grey .message .message-right .message-top .message-date, body.forum.grey .message .message-right .message-top .message-edited, body.forum.grey .message .message-right .message-top .message-username, body.forum.grey .message .message-right .message-top .right .mdi {
    color: #F9EECF;
    font-family: 'Poiret One', sans-serif!important;
    
}

/* profil */

body.grey .progress-bar .progress-value {
    background: #F9EECF;
}


body.grey .progress .progress-label {
    color: #F9EECF;
}

body.blue #menu .top .username, body.grey #menu .top .username {
    color: #F9EECF;
}

/* avatar */
header .account img {
    width: 40px;
    height: 40px;
    max-width: 40px;
    max-height: 40px;
    border-radius: 50%;
    border: 1px solid #F9EECF;
    background: #39425a;
}


/* logo */
.logo {
    border-radius: 0!important;
    font-size: 1.5em;
    text-transform: uppercase;
    font-weight: 700;
    border-bottom: 31px solid #f9eecf!important;
    background-image: url(https://i.imgur.com/76NAMiv.jpg)!important;
}


header .logo .image {
    height: 4em;
    width: 200px;
    background: url(https://i.imgur.com/PT06poi.png) 100% no-repeat;
    background-position: 50%;
    background-size: auto 26px;
}

}
