@-moz-document domain("roamedit.com") {
	/* 本样式基于 roamedit-andy 修改 */

	/*浅色模式 -默认文字字体*/
	.plugin-outline-wrap .node .node-text {
		font-size: 16px;
	}



	/*底色*/
	body {
		color: #484848;
		background-color: #fff;
		font-size: 15px;
		font-family: dinpro, "PingFang SC", Arial, "\5FAE\8F6F\96C5\9ED1", "\5B8B\4F53", simsun, sans-serif;
	}


	.plugin-outline-wrap {
		background-color: #fff !important;
	}

	body#jShape.theme-night .plugin-outline-wrap {
		background-color: #fff0 !important;
	}


	/* 双方括号链接文字颜色 */
	#detail-wrap .nmarkdown span[linkto] {
		color: var(--info);
		border-bottom: 1px solid #cccccc03;
	}


	.plugin-outline-wrap .node .node-text.node-text-long {
		margin-bottom: 0px;
	}

	/*悬停时样式*/
	.icon-circle.node-btn:hover:before {
		box-shadow: 0 0 0 6px #e9e9e9;
		transition: all .2s ease-in-out;
	}

	#roam-wrap .node.node-foldup:not(.node-top) > .node-btn:before,
	#jShape.theme-night #roam-wrap .node.node-foldup:not(.node-top) > .node-btn:before,
	body#jShape.theme-night #roam-wrap .node-search-result .node.node-foldup > .node-btn:before {
		box-shadow: 0 0 0 6px #e9e9e9;
	}


	/*调整缩进距离*/
	.plugin-outline-wrap .node-child {
		padding-left: 10px;
	}

	/* 统计子节点，排序,标签样式 */
	.label.label-info,
	.label {
		padding: 0px 5px;
		color: #484848;
		font-size: 12px;
		background-color: #f9ebd8;
		margin-left: 5px;
		font-weight: 400;
	}

	/* 调整索引前的图标位置 */
	.suggest p .string-help {
		flex-basis: 0px;
		margin-right: 5px;
		color: var(--suggest-help);
	}

	.suggest p .num,
	.suggest p .help {
		text-align: right;
	}

	/* 双方括号 左右标记 */
	#detail-wrap .nmarkdown .linkto.hover {
		border-bottom: 1px solid #4d70d785;
	}
	.linkto [char=right]::before {
		content: ']]';
		/* display: none; */
	}
	.linkto [char=left]::before {
		content: '[[';
		/* display: none; */
	}

	.theme-orange .navbar-brand:hover {
		color: #484848;
	}

	/*默认文字光滑*/
	html {
		-webkit-font-smoothing: antialiased!important;
	}

	/*版本号样式*/
	.plugin-outline-wrap .node-btn-wrap:not(:empty) > * {
		margin-right: 15px;
		color: #607d8b;
		font-weight: 400;
	}

	.hover-preview {
		margin-left: 0px !important;
		padding: 0px !important;
		margin-right: 0px !important;
	}

	/* 取消大写*/
	h6,
	.h6 {
		text-transform: none !important;
	}
	/* 设置里面的 代码*/
	#jShape .form-group textarea.form-control {
		padding: 5px;
		font-family: dinpro, "PingFang SC", Arial, "\5FAE\8F6F\96C5\9ED1", "\5B8B\4F53", simsun, sans-serif;
	}

	/*已经添加星标*/
	/*.plugin-outline-wrap .node-btn-wrap:not(:empty) .active {
		color: #decf5b !important;
		opacity: 1;
	}*/
	.icon-star:before {
		content: "\f005";
		color: #999;
	}
	.icon-star.active:before {
		content: "\f005";
		color: #ff9800;
	}


	.textarea {
		font-family: inherit;
	}


	/*引用片段*/
	.plg-cmd-search > .node-child > .node,
	.plg-cmd-search > .node-scroll > .node-child > .node {
		background-color: rgba(235, 236, 237, 0);
		padding-top: 0px;
		border-radius: 0px;
		border-right: 2px solid #ffb2e4;
	}




	/*右边侧边栏  - -- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---*/
	/* 无效信息隐藏 */
	#rightsidebar {
		display: none;
	}

	/*外部联接 样式*/
	.plugin-outline-wrap .node .node-text a:before {
		content: '🕸 '
	}
	.plugin-outline-wrap .node .node-text a {
		color: #0a84ff;
	}


	/*窗口*/
	.jwarn {
		box-shadow: rgba(0, 0, 0, 0.1) 0px 4px 6px -1px, rgba(0, 0, 0, 0.06) 0px 2px 4px -1px;
		border-radius: 4px;
		/* width: 625px !important;*/
	}

	/* 隐藏按钮*/
	#help-btn-for-search {
		display: none;
	}


	.plugin-outline-wrap .node.node-topic:not(.node-foldup) > .node-btn:hover:before {
		background-color: #9b8181;
	}

