	#detail-wrap .node[layout=tablemap] .node .node-trigger,
	#detail-wrap .node[layout=tablemap] .node .node-zoomin {
		display: none !important;
	}


	#roam-right-wrap .item-body {
		padding: 0px !important;
	}


	/*头部 - -- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---- ---*/
	.theme-orange .navbar-header {
		background: #ffffff;
	}

