	}

	body#jShape.theme-night .navbar {
		box-shadow: none;
		border-bottom: #ffffff0a 1px solid;
	}




	/* 检索内图标顺序调整 */
	.suggest p .string-main {
		order: 2;
	}




	/*kanban 看板*/
	#detail-wrap .node[layout=kanban]:not(.node-top) > .kanban-wrap,
	#detail-wrap .node-top[layout=kanban]:not(.node-top) > .kanban-wrap {
		padding-left: 0px;
		margin-left: 0px;
	}
	#detail-wrap .node[layout=kanban]:not(.node-top) > .kanban-wrap > .node-child,
	#detail-wrap .node-top[layout=kanban]:not(.node-top) > .kanban-wrap > .node-child {
		padding-left: 0px;
		margin-left: -7px;
	}
	#detail-wrap .node[layout=kanban] > .kanban-wrap > .node-child > .node,
	#detail-wrap .node-top[layout=kanban] > .kanban-wrap > .node-child > .node {
		flex-basis: auto;
		min-width: 200px;
	}

	/*被引用数标签展示*/
	.badge {
		font-size: 14px;
		line-height: 1.1;
		vertical-align: top;
	}

	/*备注样式*/
	.plugin-outline-wrap .node-quote {
