	.h2,
	.h3,
	.h4,
	.h5,
	.h6,
	h1,
	h2,
	h3,
	h4,
	h5,
	h6 {
		text-transform: none !important;
	}

	/*文字居中对齐*/
	.plugin-outline-wrap .node-head {
		align-items: normal;
	}

