style.css:
@-moz-document url-prefix("https://minestrator.com/panel/") {
:root{
    --blue-0: #6f3ef7 !important;
    --blue-1: hsla(0, 0%, 26%, .1);
    --blue-2: hsla(0, 0%, 18%, .15);
    --blue-3: hsla(0, 0%, 0%, .25);
    --minestrator:hsla(164, 0%, 9%, .9); 
    --minestrator-dark: hsl(153, 67%, 44%);
    --text-1: rgba(255, 255, 255, 0.6);
    --text-2: rgba(255, 255, 255, 0.9);
    --text-2-noactive: rgba(255, 255, 255, 0.6);
    
    --light-blue-1:hsla(196, 100%, 14%, .7);
    --light-blue-2: rgba(80, 166, 199, .6);
}

.dark-skin .progress {
	background-color: var(--light-blue-1) !important;
    box-shadow: #0000006e 0px 0px 4px;
}

.dark-skin {
    background-image: url(https://minestrator.com/assets/img/bgblur.webp) !important;
}

.dark-skin .nav-tabs{
    border: 0;
}

.dark-skin .text-dark {
	color: var(--text-1) !important;
}

body.dark-skin {
	color: var(--text-2) !important;
}

.dark-skin .nav-tabs-custom > .nav-tabs > li > a.active {
	background-color: var(--blue-2);
}

.dark-skin .nav-tabs .nav-link{
    color: var(--text-2-noactive);
}

.dark-skin .nav-tabs .nav-link:active{
    color: var(--text-1);
}

.dark-skin .wrapper{
    background-image: url(https://i.imgur.com/IQQnKVZ.jpeg) !important;
}

.layout-top-nav.dark-skin .content-wrapper .content {
    background-color: var(--blue-1);
}

.dark-skin .nav-tabs-custom {
    background-color: var(--blue-2);
}

.dark-skin .nav-tabs-custom > .nav-tabs > li > a.active {
    background-color: var(--blue-2);
    border-bottom: solid var(--minestrator) 1px !important;
}

.nav-tabs-custom {
    border-radius: 5px;
    border: 0px;
}

.dark-skin .nav-tabs-custom > .tab-content {
    background-color: var(--blue-2);
}

.dark-skin .bg-minesr{
    background-color: var(--light-blue-1) !important;
}

.dark-skin .content-header .page-title {
	color: #8a99b5;
	border-color: #899bca;
}

.text-white {
    color: #f3f7ff !important;
}

#sparklines_ram > svg > g > g > path{
    fill: var(--light-blue-2) !important;
}

#sparklines_cpu > svg > g > g > path{
    fill: var(--light-blue-2) !important;
}


.theme-success .btn-success {
     background-color: var(--minestrator-dark) !important;
    border: var(--minestrator);
    color: var(--minestrator)
}

.dark-skin.theme-success .sm-blue li.current > a, .dark-skin.theme-success .sm-blue li.highlighted > a {
	background: var(--blue-1);
	color: #ffffff !important;
    border-bottom: solid var(--minestrator) 1px !important;
    margin-bottom: -1px;
}
.sm-blue a {
	color: var(--text-2);
    border-radius: 5px 5px 0 0;
	background-color: var(--blue-2);
}
.sm-blue a:active {
	color: var(--text-1);
}


.dark-skin .border-minesr{
    border-color: var(--blue-2) !important;
}

.theme-success .btn-danger {
	background-color: hsla(360, 50%, 25%, .32);
	color: hsl(360, 54%, 57%);
    border: 0;
}

.dark-skin .bg-minesr-dark {
	background-color: var(--minestrator-dark) !important;
    color: var(--minestrator) !important;
    border: 0;
}

.dark-skin .form-control {
    border-color: #272e4800;
    color: var(--text-2);
}

.dark-skin .btn-default {
	border-color: #61538c00 !important;
}

iframe{
    border:solid 1px #0000005e;
    border-radius: 5px;
}

#advanced-panel > a {
	background-color: hsl(81, 100%, 50%) !important;
}

.theme-success .alert-primary {
	border-color: var(--minestrator-dark);
	background-color: var(--minestrator-dark) !important;
	color: var(--minestrator);
}


.theme-success .badge-success {
	background-color: var(--light-blue-2);
	color: var(--text-1);
}

.theme-success .badge-primary {
	background-color: var(--light-blue-1);
	color: var(--text-1);
}

.dark-skin .form-control {
	background-color: var(--blue-3);
	color: var(--text-2);
}

.dark-skin .btn-default {
	background-color: var(--blue-3);
	color: var(--text-2);
	border-color: hsla(227, 31%, 20%, .6);
}

.dark-skin .progress {
	background-color: hsla(227, 31%, 20%, .6);
}

.form-control::placeholder {
	color: var(--text-2);
	opacity: 1;
}

.dark-skin .box {
    background-color: var(--blue-2);
    box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.14), 0px 2px 1px -1px rgba(0, 0, 0, 0.12), 0px 1px 3px 0px rgba(0, 0, 0, 0.2);
    border-radius: 5px;
}

.dark-skin .btn-primary {
	background-color: var(--minestrator-dark) !important;
    color: var(--minestrator) !important;
    border: 0;
}

.dark-skin .bg-blackmod {
    background-color: var(--light-blue-1) !important;
}

.dark-skin .text-blackmod {
    color: var(--text-2) !important;
}

.dragula-container > tr {
    background-color: var(--blue-2) !important;
}



.card {
	border: 0;
	box-shadow: 0px 2px 5px 0px rgba(19, 23, 38, 0.05);
	margin-bottom: 20px !important;
}

.dark-skin .card {
	background-color: var(--blue-2);
}

.theme-success .alert-warning {
	border-color: hsla(37, 29%, 41%, 0);
	background-color: hsla(37, 19%, 40%, .5) !important;
	color: hsl(37, 67%, 54%);
}

.dark-skin .list-group-item {
	background-color: var(--blue-2);
	border-top: 1px solid var(--text-2);
	border-bottom: 0;
	border-right: 0;
    border-radius:0;
	border-left: 0;
}

.theme-success .alert-danger {
	border-color: hsl(355, 45%, 30%,0);
	background-color: hsla(355, 45%, 25%, .5) !important;
	color: hsl(354, 76%, 61%);
}

.dark-skin .bg-lightest {
	background-color: var(--blue-2) !important;
}

.dark-skin .bg-light {
	background-color: var(--blue-2) !important;
}

.dark-skin .box .box-footer {
	background-color: var(--blue-2);
	border-color: rgba(255, 255, 255, 0.12);
}

.theme-success .bg-info {
	background-color: hsla(210, 100%, 50%, .2) !important;
	color: #ffffff;
}

.theme-success .bg-info-light {
	background-color: hsla(210, 100%, 86%, .2) !important;
	color: #007eff;
}

.theme-success .bg-success {
	background-color: hsla(279, 85%, 24%, .2) !important;
	color: #ffffff;
}

.theme-success .bg-success-light {
	background-color: hsla(250, 50%, 95%, .2) !important;
	color: #4e0973;
}

.theme-success .bg-warning {
	background-color: hsla(37, 84%, 50%, .2) !important;
	color: #ffffff;
}

.theme-success .bg-warning-light {
	background-color: hsla(35, 74%, 90%, .2) !important;
	color: #ea9715;
}

.theme-success .bg-danger {
	background-color: hsla(355, 96%, 61%, .2) !important;
	color: #ffffff;
}

.theme-success .bg-danger-light {
	background-color: hsla(353, 63%, 95%, .2) !important;
	color: #fb3d4e;
}

.theme-success .btn-warning {
	background-color: hsla(37, 51%, 38%, .5);
	border-color: hsla(37, 84%, 50%, 0);
	color: hsl(37, 67%, 59%);
}

.dark-skin a {
	color: var(--text-1);
}

.dark-skin .table {
	color: var(--text-1);
}

.dark-skin h1, .dark-skin h2, .dark-skin h3, .dark-skin h4, .dark-skin h5, .dark-skin h6 {
	color: var(--text-1);
}
}
