style.css:
@-moz-document url-prefix("https://music.youtube.com") {
/*
* Made by PoDiax
*/

/*
* Colors and others
*/
html, body {
    background: #17202A !important; 
    color: blue !important;
}

/* width */
::-webkit-scrollbar {
    width: 9px !important;
}

/* Track */
::-webkit-scrollbar-track {
    background: #232323 !important;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #239B56 !important;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: #D35400 !important;
}
/*Share popup*/
.ytmusic-popup-container {
    background: #181818 !important;
}

/*Text color*/
.style-scope {color: #85929E !important;}

/*Player page color*/
.ytmusic-player-page{background: #17202A !important;}

/*Nav bar on top of the site*/
div.center-content.style-scope.ytmusic-nav-bar{
    background: #1C2833 !important;
}

/*Nav bar on bottom of the site*/
html:not(.style-scope){--ytmusic-color-black1: #17202A !important;}

/*Search suggestions background and text color*/
.ytmusic-search-suggestions-section{
    background: #1C2833 !important;
    color: #1C2833;
}

/*Search box background and text color*/
.ytmusic-search-box{
    background: #1C2833 !important;
	color: #85929E !important;
}

/*hide behind a nav bar on top*/
#nav-bar-background.ytmusic-app-layout {background: #17202A !important;}

/*Item section renderer (STUCK)*/
.ytmusic-item-section-renderer.stuck{background-color: #1C2833 !important;}

/*Item section renderer (HEADER) Library page*/
#header.ytmusic-item-section-renderer {background-color: #1C2833 !important;}

/*multi page menu renderer*/
.ytd-multi-page-menu-renderer{background-color: #1C2833 !important;}

/*Settings background*/
.ytmusic-settings-page{background-color: #1C2833 !important;}
    
/*Primary style*/
.style-primary{
    color: white !important;
    background: #922B21 !important;
}

/*Set white for popup container
/*
.ytmusic-popup-container{
    background: #fff !important;
}
*/

/*Do not edit this you don't need to*/
.ytmusic-carousel-shelf-renderer{
	text-align: center !important;
}
/*Popup color on right click*/
.style-scope.ytmusic-menu-popup-renderer{background: #1C2833 !important;}

/*New recommendations*/
a#new-recommendations-link.yt-simple-endpoint.style-scope.ytmusic-content-update-chip{
    background: #293e52 !important;
    border: 0px !important;
}

/*Share popup*/
.ytmusic-popup-container {background: #17202a !important;}

/*Share popup*/
div#bar.yt-copy-link-renderer{
    background-color: #17202a !important;
    border: 0px !important;
}
/*Button color(share popup)*/
button#button.style-scope.yt-icon-button{background-color: #23364a;}
yt-button-renderer.style-scope.ytmusic-menu-renderer.style-dark-on-white{background-color: #fff0;}

/**
*Channel page
*/
.gradient-container.ytmusic-visual-header-renderer{background: linear-gradient(360deg, #17202A 0.43%, rgb(252 24 24 / 0%) 100%) !important;}
.yt-button-renderer{background: #23364a;}




/**
*Rounding and formatting
*/

/* Move nav bar 0.4 to the right*/
ytmusic-app-layout > [slot=nav-bar] {width: calc(100.4vw - var(--ytmusic-scrollbar-width))!important;}

yt-button-renderer.style-scope.ytmusic-menu-renderer.style-dark-on-white{
    background-color: #fff0;
    border-radius: 11px;
}
.ytmusic-popup-container{border-radius: 20px !important;}

.image.ytmusic-player-bar {border-radius: 10px !important;}
#background.ytmusic-item-thumbnail-overlay-renderer, #content.ytmusic-item-thumbnail-overlay-renderer {
    border-radius: 14.6px !important;
}
div.center-content.style-scope.ytmusic-nav-bar{
    border-top-right-radius: 15px !important;
	border-top-left-radius: 15px !important;
}

.ytmusic-item-section-renderer.stuck{
	border-bottom-right-radius: 50px !important;
	border-bottom-left-radius: 50px !important;
}
#header.ytmusic-item-section-renderer {
	border-bottom-right-radius: 50px !important;
	border-bottom-left-radius: 50px !important;
}

.yt-simple-endpoint image-wrapper style-scope ytmusic-two-row-item-renderer {
