@-moz-document domain("wolai.com") {
/*-------@暗色主题变量------*/
:root[theme='dark'] {
	--wolaistyleplus-bgcolor:#121212;
	/*笔记背景色*/
	--wolaistyleplus-page-background-image: url(https://s1.imagehub.cc/images/2021/06/20/1624169088777175d239a290912db.jpg);
	/*上面的背景图片库有多种背景可选，也可以自己找图，把背景图片的http://地址复制粘贴到url()括号里↑↑↑↑↑*/
	--wolaistyleplus-page-editer-bgcolor: rgba(3, 32, 90, .5);
	/*磨砂玻璃背景色 rgba(38, 38, 38, .77) 默认颜色值 可根据背景色调节磨砂玻璃的颜色*/
	--wolaistyleplus-page-editer-shadow: 0 4px 16px 0 rgba(116, 117, 118, .37);
	/*磨砂玻璃阴影*/
	--wolaistyleplus-page-editer-backdrop-filter: blur(20px);
	/*磨砂玻璃模糊度*/
	--wolaistyleplus-page-editer-border: 1px solid rgba(171, 171, 171, .3);
	/*磨砂玻璃边框*/
	--wolaistyleplus-glass-border-radius: 9px;
	/*磨砂玻璃菜单和界面圆角半径*/
	--wolaistyleplus-block-bg-border-radius: 20px;
	/*页，引用，行内代码，网址链接块圆角半径*/
	--wolaistyleplus-button-bg-border-radius: 9px;
	/*文章内部媒体附件按钮块圆角半径*/
	--wolaistyleplus-textblock-bg-border-radius: 6px;
	/*文字块，引述，着重，代码片段链接块圆角半径*/
	
	--wolaistyleplus-sidebar-bgcolor: rgb(45, 44, 44);
	/*左边栏背景色*/

	/*----------------------------------------------------------------------*/
	--wolaistyleplus-page-hover-bgcolor: rgba(209, 209, 209, .5);
	/*页标题hover背景色*/
	--wolaistyleplus-page-textlogo-bgcolor: rgba(105, 105, 105, 0);
	/*页提示文字标的背景色*/
	--wolaistyleplus-page-textlogo-color: rgb(255, 255, 255);
	/*页提示文字标色*/
	--wolaistyleplus-page-textlogo-leftline-color: rgba(253, 83, 0,0.8);
	/*页提示文字标左侧竖线颜色*/
	--wolaistyleplus-page-inline-hover-bgcolor: rgba(209, 209, 209, .5);
	/*行内引用页标题hover背景色*/
	--wolaistyleplus-page-embed-hover-bgcolor: rgba(209, 209, 209, .5);
	/*嵌入页引用页标题hover背景色*/
	--wolaistyleplus-page-embed-line-color: rgba(253, 83, 0);
	/*嵌入页引用左侧竖线颜色*/
	--wolaistyleplus-page-hover-textcolor: rgba(237, 236, 236, 1);
	/*行内块引用hover字标文字颜色*/
	--wolaistyleplus-block-inline-bgcolor: rgba(200, 200, 200, 0.1);
	/*行内块引用背景色*/
	--wolaistyleplus-block-inline-hover-bgcolor: rgba(209, 209, 209, .5);
	/*行内块引用hover背景色*/
	--wolaistyleplus-block-inline-hover-textcolor: rgba(229, 229, 229, 1);
	/*行内块引用hover字标文字颜色*/
	--wolaistyleplus-page-bgcolor: rgba(200, 200, 200, 0);
	/*页标题背景色*/
	--wolaistyleplus-page-inline-bgcolor: rgba(200, 200, 200, 0.2);
	/*行内引用页标题背景色*/
	--wolaistyleplus-page-embed-bgcolor: rgba(200, 200, 200, 0.2);
	/*嵌入页引用页标题背景色*/
	--wolaistyleplus-number-notes-bgcolor: rgba(51, 51, 51, .95);
	/*数字标点击之后引用区背景色*/
	--wolaistyleplus-reference-zone-bgcolor: rgba(113, 113, 113, 0.23);
	/*页面下部引用区背景色*/
	--wolaistyleplus-left-Selected-bgcolor: rgba(59, 58, 58, .7);
	/*左侧边栏被选中的页标题背景色 */
	--wolaistyleplus-code-inline-bgcolor: rgba(200, 200, 200, 0.2);
	/*行内代码背景色*/
	--wolaistyleplus-code-inline-note-color: rgba(200, 200, 200, 1);
	/*行内代码符号标颜色*/
	--wolaistyleplus-code-inline-note-bgcolor: rgba(105, 105, 105, 0);
	/*行内代码符号背景色*/
	--wolaistyleplus-layer-line-color: rgba(196, 193, 193, .32);
	/*层级线颜色*/
	--wolaistyleplus-references-symbol-color: rgba(251, 251, 251, .53);
	/*引述两侧符号颜色*/
	--wolai-code-node-bg: rgba(90, 90, 90, 0.4);
	/* 黑色模式下代码片段背景颜色 */
	--wolai-bookmark-hover-bgcolor: #9c9e9f;
	/*黑色模式下网址书签滑动上面时背景颜色 */
	--wolai-editor-bg: rgb(18, 18, 18);
	/* 暗夜模式题图区背景色 */
	--wolai-sidebar-tree-node-color: rgb(156, 158, 159);
	/* 左侧页标题文字颜色 */
	--wolai-sidebar-tree-node-selected-bg: rgba(43, 43, 43, .62);
	/* 左侧页标题文字hover颜色 */
	--wolai-border-color: rgba(200, 200, 200, .33);
	/* 黑色模式虚线框的颜色 */
	--wolai-modal-bg: #3f3f3f;
	/* 黑色模式下骰子菜单背景颜色 */
	--wolai-editor-header-color: rgba(255, 255, 255, 0.9)!important;
	/* 标题颜色 */
	--wolai-caret-color: rgba(255, 255, 255, 0.9) !important;
	/* 其它字体颜色 */
	--wolai-bookmark-title-color: rgba(255, 255, 255, 0.9) !important;
	/* 网址书签字体颜色 */
	--wolaistyleplus-highlight-default-border-color: rgba(204, 204, 204, .78);
	/*着重内容默认颜色边框颜色*/
	--wolaistyleplus-table-border-color: rgb(255, 255, 255, 0.26);
	/*表格边框颜色*/
	--wolaistyleplus-table-list-bgcolor: rgba(136, 136, 136, .2);
	/*表格标题行背景颜色*/
	--wolai-desc-color: #fffefe;
	/*进度条文字*/
	--wolai-float-menu-h1-color: rgba(255, 255, 255, .85);
	/*悬浮目录文字颜色*/
	--wolai-float-menu-h2-color: rgba(255, 255, 255, .85);
	/*悬浮目录文字颜色*/
	--wolai-float-menu-h3-color: rgba(255, 255, 255, .85);
	/*悬浮目录文字颜色*/
	--wolai-float-menu-h4-color: rgba(255, 255, 255, .85);
	/*悬浮目录文字颜色*/
	--wolai-float-menu-h5-color: rgba(255, 255, 255, .85);
	/*悬浮目录文字颜色*/
	--wolai-float-menu-selected-text-color: rgb(255, 253, 253);
	/*悬浮目录被选中的文字颜色*/
	--wolai-float-menu-hover-bg: rgba(198, 196, 196, .5);
	--wolai-float-menu-hover-border-color: rgba(253, 83, 0);
	--wolai-inline-code-color: rgb(244, 101, 5);
	/*行内代码默认颜色*/
--wolaistyleplus-pinglun-inline-bgcolor:rgba(3, 75, 157, 1);
	/*官方行内评论文字背景颜色*/
	--wolaistyleplus-pinglun-hover-bgcolor:rgba(3, 75, 157, .5);
	/*官方行内评论文字hover背景颜色*/
}
}

