	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: var(--wolaistyleplus-text-comments-wave-bgcolor) !important;
	border-radius: var(--wolaistyleplus-text-bg-border-radius);
	padding: 2px 8px 3px 8px;
	text-decoration: underline wavy var(--wolaistyleplus-text-comments-wave-color);
	line-height: 29px;
}
div [style*="background-color:rgba(169, 222, 249, 0.5)"],
div [style*="background-color:rgba(205, 243, 220, 0.5)"],
div [style*="background-color:rgba(255, 193, 153, 0.5)"],
