style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

body{font-family:Helvetica !important}

ol.statuses > li{
line-height:1.3em !important;}

ol.statuses {
font-size:1.4em !important;}

body#profile ol.statuses .latest-status .entry-content{
font-size:1.6em !important;color:#33CCFF !important;}

div#status_update_box{position:absolute !important;top:-10px !important; height:60px !important;padding-left:13px !important;}

p.promotion, a#logo, label.doing, div#currently{display:none !important;}

textarea#status{width:340px !important; height:35px !important; font-size:1.2em !important; color:#aaaaaa!important;padding-bottom:15px !important;}

span#chars_left_notice{position:absolute !important; left:320px !important; top:39px !important; font-size:1em !important; font-family:Helvetica !important;}

.status-btn input.round-btn{position:absolute !important; width:60px !important; background:none #e6e6e6 !important; height:57px !important; left:365px !important; top:17px !important;}

}
