	color: #ddd;
	border-color: rgba(255, 255, 255, .08);
}
.module-contact {
	border-color: #252521;
}
.module-subscribe .button, .module-unsubscribe .button, .module-form .button, .CaptchaContainer .button {
	background: #ccc;
}
.pane--footer .button {
	color: #111;
}
.module-event-calendar_day-container, .module-event-upcoming .module_item-wrap {
	background: #1a1915;
}
.module-event-calendar_legend > div {
	color: #bcbcbc;
}
.module-slideshow--bg-shape {
	filter: invert(.935) hue-rotate(180deg);
	background-color: #d8d8ea
}
.module-slideshow--bg-shape > div {
	filter: inherit;
}
.background--light {
	background: #111;
}
.module-financial-year .module_cover img {
	border-color: #333;
}
.wt-input-btn-group .wt-input-btn-group__input {
	border-color: #ddd6;
}
.wt-input-btn-group .wt-input-btn-group__input:hover+.wt-input-btn-group__btn:not(.wt-input-btn-group__btn--filled) {
    border-color: rgba(221, 221, 221, .5);
}
.wt-input-btn-group .wt-input-btn-group__btn:before {
	background: #ddd;
}
}
