style.css:
@-moz-document domain("zikeke.net") {
body {
	background: #000000c7 !important;
}

/* 播放列表外部环绕的样式 */
.pfromd {

	padding-bottom: 10px;
	background: #383838;
	border-top: 0px;
}
.dramaNumList li {
	width: 55px !important;
}
a {
	color: #000 !important;
}

/*  当前正在播放item*/
.current {
	background: #050505 !important;
	box-shadow: 0 0 10px black;
	color: #f5f4ff;
	border: 1px #f9f9ff !important;
}


/* 播放列表item和顶部目录的样式 */
.h2 a,
.taba-down a {
	color: #e6e6e8 !important;
	border: none !important;
	background: #bdbdbd1a;
}
.h2 a:hover,
.taba-down a:hover {
	box-shadow: 0 0 10px #00ffef;
	background: #47fff6 !important;
	color: black !important;
	transition: 0.2s;
}
.playding {
	display: none;
	/*去掉没有必要的提示*/
}

.footer {
	background: #04040447;
	box-shadow: 0 0 10px #30292aeb;
	/*     color: #f6f6f6; */
}

/* 为了符合颜色设定，把这个白色的播放列表tab给去掉 */
.pfromd li.on,
.pfromd li.g {
	opacity: 0;
}
.pfromd li:hover {
	opacity: 0.5;
}
}
