style.css:
@-moz-document domain("crunchyroll.com") {
/* --------------- 
ESTILOS POR: JAVIER MIZ

Estos estilos dejaran de funcionar cuando Cruncyroll actualice su diseño

Para cambiar el color de fondo, basta con cambiar la variable --background-color-general dentro del :root

--------------- */

/* --------------- GENERAL --------------- */

:root {
    --background-color-general: #1F232D;
}

* {
    border: none !important;
    box-sizing: border-box;
}

.container-shadow {
    box-shadow: none;
}

body,
#footer {
    background-color: var(--background-color-general) !important;
    overflow-x: hidden;
}

.search-form-news .search-field,
#template_container.template-container,
#template_body,
#template_body.old-template-body,
#footer_menu,
.header-search-form .header-searchbox,
.store #store_button,
li.games,
.games #games_button,
.more-replies {
    background-color: rgba(0, 0, 0, 0) !important;
}

#source_manga_showview #main_content .landscape-grid li .wrapper,
.group-card,
#global_base_bubble,
.portrait-bubble,
.portrait-bubble *,
.welcome-crnews-item,
.welcome-news-item,
.welcome-simulcasts li,
.portrait-element,
#source_home .landscape-grid li .queue-wrapper,
#main_content .landscape-element,
.episode-progress-bar,
#source_showview .season-dropdown.open,
#source_showview .season-dropdown,
.guestbook-sort-bar,
.welcome-simulcasts li .info {
    background: var(--background-color-general)!important;
    background-color: var(--background-color-general)!important;
}

.widget-series-bubble-restrictions,
.widget-series-bubble-details h3,
.publisher-title-container .link,
.topbar-nav-trail,
.widget-media-bubble-details h2,
#slidebox h2 a,
ul.news h2 a,
ul.news .news-date-label,
.news-header-right a,
.group-card-name,
a.forum_main-topic-desc-title,
#showmedia_about_info_details span,
.welcome-crnews-title,
.welcome-news-title,
.welcome-latest-videos .name,
.welcome-latest-videos .series,
.welcome-simulcasts .welcome-simulcasts-collection a,
.new-template-body a.block-link,
.old-template-body a.block-link,
a.block-link,
.welcome-countdown-items a,
.showforumtopic-message-user-name a,
.paginator-lite,
ul.newsfeed h2 a,
ul.newsfeed h2 a:hover,
span.series,
input.search-query,
.series-title {
    color: aliceblue;
}

.widget-series-bubble-metadata div,
.widget-series-bubble-metadata div,
.widget-series-bubble-metadata div,
.widget-series-bubble-details,
.widget-media-bubble-restrictions,
.widget-media-bubble-details h4,
.widget-media-bubble-details .created,
.showcrunchynews .tag-header,
.showcrunchynews .top-news,
.showcrunchynews .post-ago,
.showcrunchynews h2,
ul.news-abbr .byline,
ul.news h3 a,
ul.news-top-stories-row li a,
body .landscape-grid .wrapper a:hover .short-desc,
.group-card-headline,
.group-card-details,
.main-tabs a,
.guestbook-spoiler-checkbox,
.welcome-crnews-subtitle .byline,
.welcome-latest-videos li .released,
.welcome-latest-videos .ordernum,
.welcome-recent-link a,
.welcome-news-subtitle .byline,
.series-data,
.small-data,
.welcome-simulcasts .welcome-simulcasts-release,
.welcome-simulcasts .welcome-simulcasts-episode,
.showforumtopic-message-contents-header,
.showforumtopic-message-user-details,
ul.newsfeed h3 a,
ul.newsfeed .post-ago,
span.ordernum,
.paginator-lite-disabled,
.search-results .desc,
.search-results .type,
.filter-favorite-section,
.short-desc,
ul.topbar-nav-sub li.dim a:hover,
ul.topbar-nav-sub li.highlight a,
.white {
    color: rgb(181, 181, 181);
}

.widget-series-bubble-desc,
.topbar-nav-trail a,
.welcome-block h1 a,
.welcome-title-list h1 a,
.bb-quote,
.widget-media-bubble-details h3,
.widget-media-bubble-desc,
ul.newsfeed .news-date-label,
.paginator-lite-selected,
span.name {
    color: #fff;
}

#template_scroller,
#template_skin_leaderboard,
#template_container {
    padding: 0 !important;
    margin: 0 !important;
}

/* --------------- GENERAL END --------------- */
/* --------------- HEADER --------------- */
#header_beta {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    background-color: rgba(0, 0, 0, 0);
    box-shadow: none;
    color: #fff;
    z-index: 99;
}

#header_beta::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 100px;
    background-image: linear-gradient(to bottom, var(--background-color-general), rgba(0, 0, 0, 0));
    z-index: -1;
}

.header-menubar li,
li.games {
    border-radius: 2em;
    transition: 0.3s;
}

.header-menubar li a,
li.games #games_button {
    color: #fff !important;
}

.header-menubar li:hover,
li.games:hover,
.header-menubar li.state-selected {
    background-color: #F47521 !important;
}

.site-header > .content {
    max-width: 100% !important;
    padding-left: calc(15px + 1vw) !important;
    padding-right: calc(15px + 1vw) !important;
}

/* --------------- HEADER FIN --------------- */
/* --------------- SLIDER INICIAL --------------- */
#featured_carousel {
    position: relative;
    max-height: 60vh;
}

#featured_carousel::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: linear-gradient(to top, var(--background-color-general), rgba(0, 0, 0, 0));
    z-index: 2;
    pointer-events: none;
}

.welcome-featured-media .carousel-nav {
    display: none;
}

/* --------------- SLIDER INICIAL FIN --------------- */
/* --------------- CUERPO DE HOME --------------- */

#template_container.template-container {
    width: 100% !important;
}

#template_body {
    display: grid;
}

#marketing_banner,
#message_box,
.welcome-more-bottom {
    display: none !important;
}

#welcome_left {
    margin: 0;
    padding: 0;
    display: grid;
    grid-gap: 20px;
    grid-template-columns: 1fr 1fr;
    grid-template-areas: "banner banner" "nuevo nuevo" "series series";
    width: 100%;
}
/* --------------- CUERPO DE HOME FIN --------------- */
/* --------------- GRID DE SERIES SIMULCAST --------------- */
.welcome-featured-media {
    grid-area: banner;
    margin-bottom: -20vh;
}

#welcome_nowshowing {
    grid-area: series;
    display: block !important;
}

#welcome_nowshowing .welcome-simulcasts {
    height: auto !important;
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-gap: 30px 15px;
    overflow: unset;
}

#welcome_nowshowing .welcome-simulcasts::before,
#welcome_nowshowing .welcome-simulcasts::after,
.welcome-simulcasts li::before,
.welcome-simulcasts li::after {
    content: none !important;
}

.welcome-simulcasts li {
    display: grid;
    grid-template-columns: 1fr;
    grid-template-areas: "media" "titulo" "fecha" "capitulos";
    grid-gap: 5px;
    width: auto;
    margin-top: 0;
    margin-bottom: 0;
    height: auto;
    float: none;
    position: relative;
    margin-right: 0;
    transition: 0.3s;
}

.welcome-simulcasts li:hover {
    box-shadow: 0 17px 16px -2px !important;
    transform: scale(1.3);
    transition-delay: 0.5s;
    z-index: 10;
}

.welcome-simulcasts li:hover .mug {
    border-radius: 0;
    transition-delay: 0.5s;
}

.welcome-simulcasts li:hover .info {
    box-shadow: 0 17px 16px -2px !important;
    top: calc(100% - 2px);
    opacity: 1;
    transition-delay: 0.5s;
    z-index: 1;
}

.welcome-simulcasts .mug {
    float: none;
    width: auto;
    margin-right: 0;
    position: relative;
    grid-area: media;
    overflow: hidden;
    border-radius: 10px;
    transition: 0.3s;
}

.welcome-media-mug {
    width: 100%;
}

.welcome-simulcasts .welcome-simulcasts-collection {
    padding-left: 10px;
    padding-right: 10px;
    margin-top: 10px;
    display: block;
    width: auto;
    height: auto;
    position: unset;
    overflow: unset;
}

.welcome-simulcasts .welcome-simulcasts-collection .name {
    position: unset;
    width: auto;
    bottom: 0;
    max-height: unset;
}

.welcome-simulcasts-release {
    padding-left: 10px;
    padding-right: 10px;
}

.welcome-simulcasts li :not(.info) {
	background-color: var(--background-color-general);
	z-index: 5;	
}

.welcome-simulcasts li .info {
    opacity: 0;
    float: none;
    width: 100%;
    transition: 0.3s;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
}

.welcome-simulcasts li .info a {
    padding: 10px 15px;
    font-size: 16px;
}

/* --------------- GRID DE SERIES SIMULCAST FIN --------------- */
/* --------------- ACOMODO DE BLOQUES --------------- */
#welcome_left .welcome-block:nth-child(2) {
    grid-area: blog;
    display: none;
}

#welcome_left .welcome-block:nth-child(3) {
    display: none; 
}

#welcome_left .welcome-block:last-child {
    grid-area: nuevo;
}

.welcome-block {
    padding: 0 calc(15px + 2vw) !important;
    box-sizing: border-box;
    z-index: 2;
}

.welcome-block h1 {
    margin-bottom: 1rem;
    font-weight: 900;
}
/* --------------- ACOMODO DE BLOQUES FIN --------------- */

/* --------------- ACOMODO DE BLOQUES SIDEBAR HOME --------------- */

#welcome_right {
	float: none;
    display: grid;
    width: 100%;
    margin: 0;
}

#welcome_right .welcome-recent-episodes {
  display: none;
}

#welcome_right #sidebar,
#welcome_right .welcome-more {
	width: 100%;
	display: none;
}

#welcome_right #sidebar #marketing_box {
	display: none;
}

#welcome_right .landscape-grid {
	display: grid;
	grid-template-columns: repeat(5, 1fr);
	padding-left: calc(15px + 3vw);
	padding-right: calc(15px + 3vw);
}

#welcome_right .landscape-grid li .wrapper,
#welcome_right .landscape-grid li .wrapper a {
	margin: 0;
	padding: 0;
	width: auto;
}

#welcome_right .welcome-countdown-day {
	color: #fff;
}

#welcome_right .welcome-countdown-items {
	display: grid;
	grid-template-columns: repeat(6, 1fr);
	gap: 20px;
}

#welcome_right .welcome-countdown-items a {
	transition: 0.3s;
}

#welcome_right .welcome-countdown-items a:hover {
	background-color: var(--background-color-general);
	transform: scale(1.1);
	transition-delay: 0.3s;
}

#welcome_right .welcome-countdown-items a > img {
	width: 100%;
	border-radius: 10px;
}

#welcome_right .welcome-countdown-items a > .welcome-countdown-details {
	padding: 10px 5px;
}

#welcome_right .welcome-block:nth-last-child(2) {
  display: none;
}

/* --------------- ACOMODO DE BLOQUES SIDEBAR HOME FIN --------------- */

/* CAPITULOS RECIENTES */
.welcome-latest-videos::before,
.welcome-latest-videos::after {
    content: none;
}

.welcome-latest-videos {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    grid-gap: 30px 15px;
    width: auto;
}

.welcome-latest-videos li {
    float: none;
    width: auto;
    height: auto;
    padding: 0;
    transition: 0.3s;
}

.welcome-latest-videos li:last-child,
.welcome-latest-videos li:nth-last-child(2),
.welcome-latest-videos li:nth-last-child(3) {
    display: none;
}

.welcome-latest-videos li:hover {
    transform: scale(1.3);
    transition-delay: 0.5s;
    z-index: 10;
}

.welcome-latest-videos li a {
    display: grid;
    grid-template-rows: 4fr repeat(3, min-content);
    grid-template-areas: "vacio" "capitulo" "titulo" "serie";
    text-align: left;
    height: 100%;
}

.welcome-latest-videos li a .welcome-media-mug {
    grid-area: 1/1/-1/-1;
    overflow: hidden;
    position: relative;
    border-radius: 10px;
}

.welcome-latest-videos li a .welcome-media-mug img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.welcome-latest-videos li a .welcome-media-mug::after {
    content: "" !important;
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    bottom: 0 !important;
    background-image: linear-gradient(to top, #000, rgba(0, 0, 0, 0)) !important;
    z-index: 2 !important;
    pointer-events: none !important;
}

.welcome-latest-videos li .released {
    text-align: left;
}

.welcome-latest-videos li a .series,
.welcome-latest-videos li a .name,
.welcome-latest-videos li a .ordernum {
    grid-area: info;
    padding-left: 10px;
    padding-right: 10px;
    text-align: left;
    white-space: initial;
    z-index: 2;
    margin-bottom: 5px;
}

.welcome-latest-videos li a .series {
    grid-area: serie;
    font-size: 10px;
    font-weight: 400;
    padding-bottom: 10px;
}

.welcome-latest-videos li a .name {
    grid-area: titulo;
    font-size: 16px;
}

.welcome-latest-videos li a .ordernum {
    grid-area: capitulo;
    color: #F47521;
    display: block;
    font-size: 14px;
    font-weight: 900;
    font-style: normal;
}

/* CAPITULOS RECIENTES FIN */
.showmedia-trail,
.game-banner-wrapper {
    display: none;
}

#showmedia_video {
    padding-top: 52%;
    position: relative;
}

#showmedia_video_box_wide, #showmedia_video_box {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
}

#showmedia #sidebar > div:not(#showmedia_about_new) {
    display: none;
}

#main_content {
    width: 100%;
}

#main_content #tabs {
    display: none;
}

#main_content .showmedia-submenu {
    display: none;
}

#main_content .white-wrapper {
    background-color: var(--background-color-general);
}

#main_content .white-wrapper .collection-carousel {
    width: 100%;
}

.collection-carousel-contents {
    overflow: visible;
}

.new_layout #sidebar {
    position: absolute;
    top: 100px;
    left: 50px;
    display: none;
}

.new_layout #sidebar:hover {
    display: block;
}

#showmedia_video:hover + .game-banner-wrapper + style + #showmedia #sidebar {
    display: block;
}

#showmedia_about_new h3,
#showmedia_about_new #showmedia_about_info_details {
    display: none;
}

#showmedia_about_media,
#showmedia_about_info,
#showmedia_about_sampleSizeText {
    color: #fff;
}

#showmedia_about_episode_num a {
    color: #F47521;
}

.comments {
    color: #fff;
    width: 100%;
    margin: auto;
    max-width: 1240px;
    padding-left: 15px;
    padding-right: 15px;
}

.comments textarea {
    color: #fff;
    background-color: rgba(0, 0, 0, 0);
}


/* --------- SERIE SINLE ------------ */
#template_container,
#source_showview {
    width: 100% !important;
    max-width: 100% !important;
}

#source_showview #container {
    display: grid;
    gap: 30px;
    grid-template-columns: 1fr;
    grid-template-areas: "info" "capitulos";
}

#source_showview #sidebar {
    grid-area: info;
    display: flex;
    justify-content: center;
    width: 100%;
}

#source_showview #sidebar_elements {
    color: #fff;
    display: grid;
    grid-template-columns: min-content 5fr 5fr;
    grid-template-rows: min-content min-content min-content;
    gap: 15px;
    grid-template-areas: "imagen informacion informacion" "imagen rating compra" "imagen detalles detalles";
    align-items: center;
    justify-items: left;
    padding-left: calc(15px + 1vw);
    padding-right: calc(15px + 1vw);
}

#source_showview #sidebar_elements li {
    margin: 0;
}

#source_showview #sidebar_elements li:first-child {
    grid-area: imagen;
}

#source_showview #sidebar_elements li:nth-child(2) {
    grid-area: compra;
}

#source_showview #sidebar_elements li:nth-child(3) {
    grid-area: informacion;
}

#source_showview #sidebar_elements li:nth-child(4) {
    grid-area: rating;
}

#source_showview #sidebar_elements li:nth-child(5) {
    grid-area: detalles;
}

.season .portrait-grid {
    display: grid;
    grid-template-columns: repeat(6, 1fr);
    gap: 15px;
    padding-left: calc(15px + 1vw);
    padding-right: calc(15px + 1vw);
}

.season-dropdown {
    color: #F47521;
}

.portrait-grid::before,
.portrait-grid::after {
    content: none;
}

.portrait-grid li {
    padding: 0;
    margin: 0;
    transition: 0.3s;
}

.portrait-grid li:hover {
    transform: scale(1.3);
    z-index: 10;
}

.portrait-element,
.portrait-element *:not(.episode-progress) {
    padding: 0 !important;
    margin: 0 !important;
    width: 100% !important;
    height: auto !important;
}

.portrait-element img {
    border-radius: 10px;
    transition: 0.3s;
}

.portrait-grid li:hover img {
    border-radius: 0;
}

.portrait-element .series-title {
    padding: 10px 15px !important;
}

.season .portrait-element .series-title {
    padding: 0 !important;
}

.portrait-element .short-desc {
    padding: 5px 15px 20px;
}

/* ------- OTRAS SERIES ---------- */
.portrait-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 30px;
    padding-left: calc(15px + 2vw);
    padding-right: calc(15px + 2vw);
}

/* Series Grid */
#source_browse {
    display: grid;
    grid-template-columns: 1fr 6fr;
    padding-top: 100px;
}

#tabs,
#tabs .main-tabs {
    background: rgba(0, 0, 0, 0);
}

#tabs .sub-tabs-menu,
#tabs .genre-selectors ul,
#tabs .season-selectors ul {
    display: flex;
    flex-direction: column;
}

#tabs .sub-tabs-menu a:nth-child(4) {
    display: none;
}

#tabs .genre-selectors ul li,
#tabs .season-selectors ul li {
    width: auto;
}
#tabs .genre-selectors ul label,
#tabs .season-selectors ul label {
    color: #fff;
}

.genre-selectors,
.season-selectors {
    width: 100%;
}

.portrait-element .series-data {
    padding: 5px 15px !important;
}
}
