style.css:
@-moz-document domain("omegle.com") {
/* Omegle Dark Theme v0.1 by Lexan*/

/* Dark Scrollbar*/
::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #222 !important;
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: #444 !important;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838 !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
  }

/*Variables*/
:root {
        --bg-color: #0F0F0F;
        --bg2-color: #141414;
		--bg3-color: #222;
		--item-color: #333;
		--item-active-color: #777;
        --text-color: #fff;
		--text-darken-color: #ddd;
		--text-darken2-color: #aaa;
        --you: #3F90FF;
        --stranger: #FF3333;
        --question: #135
}

body {
    background: var(--bg2-color) !important;
}

/*Header*/
    #header{
        background: var(--bg3-color);
        box-shadow: none;
    }

    #logo img{
        content: url("https://www.omegle.com/static/dotcomlogomasked@2x.png") !important;
    }

    .goog-te-gadget-simple{
        background-color: var(--item-color) !important;
        border: none;
    }

    .goog-te-gadget-simple .goog-te-menu-value{
        color: var(--text-color)
    }

    #tagline img{
        display: none;
    }

/*Main Chat*/
    .logwrapper,#intro /*chat background*/{
        background: var(--bg2-color) !important;
        color: var(--text-darken-color);
        border: none;
        box-shadow: none;
    }

    .youmsg .msgsource /*You*/{
        color: var(--you)
    }

    .strangermsg .msgsource /*stranger*/{
        color: var(--stranger)
    }

    a:-webkit-any-link,.btnkbshortcut /*hyperlinks*/{
        color: var(--you)
    }
    .chatmsg,.chatmsgwrapper,.topicplaceholder,.topictageditor,.disconnectbtn,.disconnectbtnwrapper,.sendbtn,.sendbtnwrapper /*message box*/{
        background: var(--bg3-color) !important;
        color: var(--text-darken-color);
        border: none !important;
    }

    .statuslog /*secondary text*/{
        color: var(--text-darken2-color);
    }

    #startachat, #intoheader /*title text*/{
        color: var(--text-color)
    }

    .topictag,#monitoringnotice,#monitoringnotice,.questionHeading,#monitoringnotice h2 /*tags*/{
        background: var(--you) !important;
        box-shadow: none;
        border: none;
    }

    .questionText{
        background: var(--question) !important;
    }
}
