style.css:
@-moz-document domain("dashboard.heroku.com")
{
  body
  {
    background-color: #333;
    color: #999;
  }

  .main-content
  {
    background-color: #333;
  }

  .bg-white
  {
    background-color: #333;
  }

  .near-black
  {
    color: #999;
  }

  .bg-white:hover
  {
    background-color: #555;
    color: #999;
  }

  .b--light-silver
  {
    border-color: #999;
  }

  .glostick-nav
  {
    background-color: #333;
  }

  .build-stream .build-stream-output .build-stream-line
  {
    color: #fff;  
  }

  .build-stream 
  {
    background-color: #1f1f1f;  
  }

  .main-panel
  {
    background-color: #444;
  }

  .top-nav
  {
    border-bottom: 1px solid #999;
  }

  .app-footer
  {
    background-color: #444;
    border-top: 1px solid #999;
  }

  .context-switcher__button
  {
    color: #999;
  }

  .dark-gray
  {
    color: #999;
  }

  .link:hover
  {
    color: #B19CD9;
  }

  .nav.nav-tabs.sub-nav
  {
    background-color: #444;
    box-shadow: inset 0 -1px 0 #999;
  }

  #overview-page
  {
    background-color: #333;
  }

  #overview-page .app-overview-metrics .metrics__summary-row--favorite__link-wrapper, #overview-page .data-unavailable, .content-box, .metrics__chart-sorting .sortable-item.is-dragging, .metrics__labs-banner, .metrics__monitor-preview-chart__loading, .metrics__not-available, .pipeline-app .box, .purple-box
  {
    background-color: #444;
    box-shadow: 0 0 0 1px rgba(89,105,128,.1), 0 1px 3px 0 rgba(89,105,128,.1), 0 1px 2px 0 rgba(0,0,0,.05);
  }

  #overview-page .app-overview-metrics diagnostic-chart::after
  {
    background-image: -webkit-linear-gradient(left,#fff,rgba(68,68,68,0));
    background-image: linear-gradient(to right,#fff,rgba(68,68,68,0));
    background-image: linear-gradient(to right, rgb(68, 68, 68), rgba(255, 255, 255, 0));
  }

  code, pre
  {
    background-color: rgb(68, 68, 68);
    color: #888;
  }

  code, kbd
  {
    box-shadow: rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;
  }

  .deploy-section .pipeline-status::before, .pipeline-config .pipeline-status::before
  {
    background-color: rgb(51, 51, 51);
    background-image: linear-gradient(rgb(51, 51, 51), rgb(51, 51, 51));
    box-shadow: rgba(255, 255, 255, 0.05) 0px -1px 0px inset;
  }

  .deploy-section .panel-content .dyno-tier-picker-item-size-name, .deploy-section .panel-content .list-item-distinct__title, .deploy-section .panel-content .panel-section .panel-title, .deploy-section .panel-content h4, .panel-section .deploy-section .panel-content .panel-title, .panel-section .pipeline-config .panel-content .panel-title, .pipeline-config .panel-content .dyno-tier-picker-item-size-name, .pipeline-config .panel-content .list-item-distinct__title, .pipeline-config .panel-content .panel-section .panel-title, .pipeline-config .panel-content h4
  {
    color: #999;
  }

  .deploy-github .panel-title, .deploy-pipeline .panel-title
  {
    color: #999;
  }

  .list-group-item
  {
    background-color: #333;
    border-color: #999;
  }

  .quick-jump__filter.form-control
  {
    color: #999;
    background-color: #444;
    border: 1px solid #999;
  }

  .form-control, output
  {
    color: #999;
  }

  #pipeline-overview .pipeline-stages
  {
    background-color: #333;
  }

  .bg-white-40
  {
    background-color: rgba(68,68,68,0.4);
  }

  .bg-white-60
  {
    background-color: rgba(68,68,68,0.6);
  }

  .hk-button-sm--secondary
  {
    background-color: rgba(100,100,100,0.1);
  }

  .ci-view
  {
    background: rgb(51, 51, 51);
  }

  .ci-menu .ci-menu--context
  {
    background: rgb(51, 51, 51);
  }

  .ci-menu
  {
    background: rgb(51, 51, 51);
    border-right: 1px solid #999;
  }

  .nav.nav-tabs.sub-nav.pipeline-nav
  {
    box-shadow: inset 0 -1px 0 #999;
  }

  .nav.sub-nav a.active::after
  {
    background: #79589F;
  }

  .ci-index #tests-setup .dyno-tier-picker-item-size-name, .ci-index #tests-setup .list-item-distinct__title, .ci-index #tests-setup .panel-section .panel-title, .ci-index #tests-setup .title, .ci-index #tests-setup h4, .panel-section .ci-index #tests-setup .panel-title
  {
    color: #999;
  }

  [class*=protected-pipelines-pipeline-] .main-content
  {
    background: #333;
  }

  #pipeline-overview .stage::after
  {
    background-image: -webkit-linear-gradient(rgba(51,51,51,0),#333);
    background-image: linear-gradient(rgba(51,51,51,0),#333);
  }
}

@-moz-document domain("scheduler.heroku.com")
{
  body
  {
    background-color: #333;
    color: #999;
  }

  .item_actions
  {
    padding: 7px 15px;
    background-color: #444;
    border: 1px solid #999;
    border-radius: 0px 0px 4px 4px;
    position: relative;
    top: -1px;
  }

  .item_well
  {
    border: 1px solid #999;
    padding: 15px;
    border-radius: 4px 4px 0px 0px;
    background-color: #444;
  }
}
