style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/*DARK MODE*/

:root {
    --main: /*[[mainkey]]*/;
    --main2: /*[[main2key]]*/;
    --darkbg: /*[[darkbgkey]]*/;
    --darkbg2: /*[[darkbg2key]]*/;
    --darktext: /*[[darktextkey]]*/;
    --darktext2: /*[[darktext2key]]*/
}

body, .button, .battle-log {
    font-family: /*[[fontkey]]*/;
}

/****************************/
/****************************/
/*MAIN BACKGROUND*/
/****************************/
/****************************/

body {
    background: url("/*[[mainbgkey]]*/") !important;
    background-size: cover !important
}

/****************************/
/****************************/
/*SCROLLBAR*/
/****************************/
/****************************/

/********/
/*Size*/
/********/

::-webkit-scrollbar {
	width: 10px;
	height: 10px;
}

/********/
/*Thumb (the thing you grab)*/
/********/

/*vertical*/

::-webkit-scrollbar-thumb:vertical {
	box-shadow: inset 0px 0px 5px rgba(0, 0, 0, .3), inset 0px 0px 50px var(--main);
	border-radius: 50px 0px 0px 50px;
    border-right: 2px solid transparent
}

/*horizontal*/

::-webkit-scrollbar-thumb:horizontal {
	box-shadow: inset 0px 0px 7px rgba(0, 0, 0, .3), inset 0px 0px 50px var(--main);
	border-radius: 50px 50px 0px 0px;
    border-bottom: 2px solid transparent
}

/*hover*/

::-webkit-scrollbar-thumb:vertical:hover, ::-webkit-scrollbar-thumb:horizontal:hover {
  	box-shadow: inset 0px 0px 50px var(--main);
}

/********/
/*Track*/
/********/

/*vertical*/

::-webkit-scrollbar-track:vertical {
	box-shadow: inset 0px 0px 50px rgba(102, 102, 102, .3);
	border-radius: 50px 0px 0px 50px;
    border-right: 2px solid transparent
}

/*horizontal*/

::-webkit-scrollbar-track:horizontal {
	box-shadow: inset 0px 0px 50px rgba(102, 102, 102, .3);
	border-radius: 50px 50px 0px 0px;
    border-bottom: 2px solid transparent
}

/*hover*/

::-webkit-scrollbar-track:hover {
	box-shadow: inset 0px 0px 50px rgba(102, 102, 102, .4);
}

/********/
/*Corner*/
/********/

::-webkit-scrollbar-corner {
  	background: var(--main);
}

/****************************/
/****************************/
/*TEXTBOXES*/
/****************************/
/****************************/

.dark .textbox {
    background: var(--darkbg2);
    border: 1px solid var(--main);
    color: var(--darktext);
    box-shadow: none
}

.dark .textbox:hover {
    background: var(--darkbg2) !important;
    border: 1px solid var(--main);
    box-shadow: inset 0px 0px 3px var(--main)
}

.dark .textbox:focus {
    background: var(--darkbg2);
    border: 1px solid var(--main);
    box-shadow: inset 0px 0px 3px var(--main), 0px 0px 4px var(--main2), 0px 0px 4px var(--main2), 0px 0px 4px var(--main2);
}

/****************************/
/****************************/
/*DROP-DOWN BARS*/
/****************************/
/****************************/

.dark select {
    background: var(--darkbg2);
    border: 1px solid var(--main);
    border-radius: 4px;
    color: var(--darktext);
    transition: 0.3s;
    outline: 0
}

.dark select option {
    background: var(--darkbg2);
}

/****************************/
/****************************/
/*SLIDERS (FOR VOLUME AND EVS)*/
/****************************/
/****************************/

/********/
/*Track*/
/********/

.dark input[type="range"]::-webkit-slider-runnable-track, .dark input[type="range"]:hover::-webkit-slider-runnable-track {
    box-shadow: none;
    background: var(--darkbg2);
    border-radius: 5px;
    border: 1px solid var(--main);
}

/********/
/*Thumb*/
/********/

.dark input[type=range]::-webkit-slider-thumb, .dark input[type=range]:hover::-webkit-slider-thumb {
    background: var(--darkbg2);
    box-shadow: none;
    border: 1px solid var(--main)
}

/****************************/
/****************************/
/*MAIN MENU*/
/****************************/
/****************************/

/********/
/*Background*/
/********/

.dark .menugroup {
    background: var(--darkbg)
}

/********/
/*Buttons*/
/********/

.dark .menugroup .button {
    background: transparent !important;
    color: var(--darktext);
    border: 1px solid var(--main) !important;
    box-shadow: inset 0px 0px 4px var(--main) !important;
    text-shadow: none !important;
    transition: 0.3s
}

.dark .menugroup .button:hover, .dark .menugroup .button:active, .dark .menugroup .button:focus {
    background: var(--darkbg2) !important;
    border: 1px solid var(--main);
    box-shadow: inset 0px 0px 4px var(--main), 0px 0px 5px var(--main), 0px 0px 15px var(--main) !important;
    top: 0px !important
}

.dark .menugroup .button.disabled, .dark .menugroup .button.disabled:hover, .dark .menugroup .button.disabled:active, .dark .menugroup .button.disabled:focus {
    background: var(--darkbg2) !important;
    border: 1px solid var(--main) !important;
    color: var(--darktext) !important;
    box-shadow: inset 0px 0px 4px var(--main), 0px 0px 5px var(--main), 0px 0px 15px var(--main) !important;
    opacity: 0.6
}

/********/
/*Cancel And Reconnect And Add Game Buttons*/
/********/

.dark .menugroup .buttonbar button, .dark .menugroup .buttonbar button:hover {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    transition: 0.3s;
    top: 0px
}

.dark .menugroup .buttonbar button:active, .dark .menugroup .buttonbar button:focus {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2), 0px 0px 5px var(--main2);
    border: 1px solid var(--main);
    color: var(--darktext);
    transition: 0.1s;
    top: 0px
}

/********/
/*Text*/
/********/

.dark .menugroup label {
    color: var(--darktext);
    text-shadow: none
}

/********/
/*Format And Team Selectors*/
/********/

/*button stuff*/

.dark .select.formatselect, .dark .select.teamselect, .dark .select.formatselect:focus, .dark .select.teamselect:focus {
    background: var(--darkbg);
    box-shadow: inset 0px 0px 50px var(--main);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    transition: 0.3s
}

.dark .select.formatselect:hover, .dark .select.teamselect:hover {
    box-shadow: inset 0px 0px 100px var(--main);
    position: relative;
    top: 0px;
    border: 1px solid var(--main);
    background: var(--darkbg);
    color: var(--darktext)
}

.dark .select.formatselect:active, .dark .select.teamselect:active {
    box-shadow: inset 0px 0px 150px var(--main);
    position: relative;
    top: 0px;
    background: var(--darkbg);
    border: 1px solid var(--main)
}

/*arrow*/

.dark .select:after {
    color: var(--darktext) !important
}

/*disabled*/

.dark .select.formatselect:disabled, .dark .select.teamselect:disabled, .dark .select.formatselect:disabled:hover, .dark .select.teamselect:disabled:hover, .dark .select.formatselect:disabled:active, .dark .select.teamselect:disabled:active {
    background: var(--darkbg);
    box-shadow: inset 0px 0px 50px var(--main);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    opacity: 0.7;
    top: 0px
}

/*text for the above*/

.dark .teamselect.preselected strong {
    color: var(--darktext)
}

/********/
/*Spectator Toggle*/
/********/

.checkbox {
    background: transparent;
    transition: 0.2s
}

.dark .checkbox:hover {
    background: transparent !important;
    color: var(--darktext) !important;
    box-shadow: inset 0px 0px 10px var(--main), inset 0px 0px 50px var(--main)
}

/********/
/*Bottom Links*/
/********/

.mainmenufooter a, .mainmenufooter a small {
    color: var(--main) !important;
    text-shadow: 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black;
    font-weight: bold;
    font-family: 'tahoma'
}

.mainmenufooter small {
    color: white;
    text-shadow: 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black;
}

/****************************/
/****************************/
/*TOP BAR*/
/****************************/
/****************************/

/********/
/*Logo*/
/********/

.header .logo {
    opacity: 0%;
}

#header.header::after {
    display: block;
    content: "";
    width: 109px;
    height: 52px;
    float: left;
    position: relative;
    top: -2px;
    left: -125px;
    background: url('https://i.imgur.com/KuRjJEq.png'); /*←I made that :)*/
    background-size: 100%;
}

/********/
/*Background*/
/********/

.dark .header {
    background: var(--darkbg)
}

/********/
/*Tabs*/
/********/

/*some border radius stuff*/

.tabbar li:first-child a.button {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
}

.tabbar li:last-child a.button {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
}

/*normal state*/

.dark .tabbar a.button {
    background: var(--darkbg2);
    box-shadow: none;
    border-color: var(--main);
    color: var(--darktext);
    font-size: 10px;
    font-family: tahoma;
    text-shadow: none;
    transition: 0.3s;
    padding-bottom: 1.5px;
    padding-top: 1.5px;
    margin-right: 3px;
    top: 0px;
    z-index: 1
}

/*hover state*/

.dark .tabbar a.button:hover {
    background: var(--darkbg2);
    border-color: var(--main);
    box-shadow: inset 0px 0px 5px var(--main), inset 0px 0px 10px var(--main);
    opacity: 1;
    color: var(--darktext);
}

/*current tab*/

.dark .tabbar a.button.cur, .dark .tabbar a.button.cur:hover {
    background: var(--darkbg2) !important;
    box-shadow: none;
    border-color: var(--main) !important;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    color: var(--darktext) !important;
    font-size: 11px;
    font-family: verdana;
    padding-bottom: 5px;
    padding-top: 3px;
    opacity: 1;
    top: 0px;
}

/*unread state*/

.dark .tabbar a.button.subtle-notifying {
    color: turquoise
}

/*notifying state*/

.dark .tabbar a.button.notifying {
    color: black;
    background: skyblue;
    border: 1px solid #2c9cc1
}

.dark .tabbar a.button.notifying:hover{
    box-shadow: none !important;
    border-color: #e0f7ff;
    background: skyblue
}

/********/
/*Close Buttons*/
/********/

.tabbar .closebutton {
    right: 2px;
}

/*the parts below affect every close button*/

.dark .closebutton {
    color: var(--darktext2);
}

.closebutton:hover {
    color: red;
}

.closebutton:active {
    color: #c10000
}

/********/
/*Room List Button (the one that makes a popup showing the rooms when you click it)*/
/********/

.dark button[name="tablist"] {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main) !important;
    transition: 0.3s
}

.dark button[name="tablist"]:hover {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 30px var(--main);
}

.dark button[name="tablist"]:active, .dark button[name="tablist"]:focus {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main);
}

/********/
/*Bottom Border*/
/********/

.dark .maintabbarbottom {
    background: var(--darkbg2);
    border-color: var(--main);
}

/********/
/*Icon*/
/********/

i.fa.fa-user::before {
    display: none
}

.userbar .username::before {
    content: "";
    background: url("/*[[iconkey]]*/"); /*change this url for a different image*/
    background-size: cover;
    width: /*[[nameiconwidthkey]]*/; /*tweak these dimensions to fit different stuff*/
    height: /*[[nameiconheightkey]]*/;
    float: left;
    position: relative;
    top: -9px;
    left: 5px;
    /*[[nameiconsatukey]]*/
}

/********/
/*Username*/
/********/

.username {
    text-shadow: none !important
}

/********/
/*"Choose name" Button*/
/********/

.dark .userbar button {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main) !important;
    transition: 0.3s
}

.dark .userbar button:hover {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main);
    top: 0px;
}

.dark .userbar button:active, .dark .userbar button:focus {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main), 0px 0px 5px var(--main2);
    top: 0px;
    transition: 0.1s
}

.dark .userbar button:disabled {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main);
    opacity: 0.6
}

/********/
/*User Buttons*/
/********/

.dark .icon.button, .dark .icon.button:hover {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    transition: 0.3s;
    top: 0px
}

.dark .icon.button:active, .dark .icon.button:focus {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2), 0px 0px 5px var(--main2);
    border: 1px solid var(--main);
    color: var(--darktext);
    transition: 0.1s;
    top: 0px
}

/****************************/
/****************************/
/*MISCELLANEOUS STUFF FOR ROOMS*/
/****************************/
/****************************/

/********/
/*Background*/
/********/

.dark .ps-room.ps-room-light {
    background: var(--darkbg) !important;
    border-color: var(--main)
}

.dark .chat-log {
    background: transparent;
    color: var(--darktext)
}

/********/
/*Buttons*/
/********/

.dark div.pad button.button /*most buttons*/,
.dark div.pad a.button /*in the report page and maybe more*/,
.dark .teampane button.button /*in the teambuilder*/,
.dark div.pad p button /*in the ladder page and maybe more*/,
.dark div.pad .search button /*the search button in the ladder page and maybe more*/ {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main) !important;
    transition: 0.3s
}

.dark div.pad button.button:hover,
.dark div.pad a.button:hover,
.dark .teampane button.button:hover,
.dark div.pad p button:hover,
.dark div.pad .search button:hover {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main);
    top: 0px;
}

.dark div.pad button.button:active,
.dark div.pad button.button:focus,
.dark div.pad a.button:active,
.dark div.pad a.button:focus,
.dark .teampane button.button:active,
.dark .teampane button.button:focus,
.dark div.pad p button:active,
.dark div.pad p button:focus,
.dark div.pad .search button:active,
.dark div.pad .search button:focus {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main), 0px 0px 5px var(--main2);
    top: 0px;
    transition: 0.1s
}

.dark div.pad button.button.disabled,
.dark .teampane button.button.disabled {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main) !important;
    transition: 0.3s;
    opacity: 0.6
}

/********/
/*Room And Battle Links*/
/********/

/*im talking about the ones in rooms and battles tabs and the battle link(s) in the main menu*/

.dark .roomlist a.ilink {
    background: #0066ff1c !important;
    color: #06f !important;
    border: 1px solid #06f !important;
    transition: 0.3s
}

.dark .roomlist a.ilink:hover {
    background: #0066ff3b !important;
    color: #06f !important;
    border: 1px solid #06f !important
}

/********/
/*Text*/
/********/

.dark .ps-room.ps-room-light, .dark .ps-room.ps-room-light label {
    color: var(--darktext)
}

/*for when the remaining ev counter gets an illegal number*/

.statform .totalev b {
    color: #ff0707
}

/****************************/
/****************************/
/*CHATROOMS*/
/****************************/
/****************************/

/********/
/*Text*/
/********/

.dark .chat-log .chat em {
    color: var(--darktext)
}

.dark .chat small {
    color: var(--darktext2)
}

/********/
/*Message Backgrounds*/
/********/

/*user's text*/

.dark .chat.mine {
    background: rgba(242, 242, 242, .05)
}

/*highlighted text*/

.dark .highlighted {
    background: rgba(0, 102, 255, .35)
}

/*revealed text*/

.dark .revealed {
    background: rgba(0, 255, 145, .15)
}

/*spoilers*/

.dark .spoiler {
    background: grey;
    color: grey
}

.dark .spoiler:hover, .dark .spoiler:active, .dark .spoiler-shown {
    background: #3a3a3a;
    color: white
}

/********/
/*Buttons In Messages (Like The Undo Button When You Send A Friend Request)*/
/********/

.dark .chat .button {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 20px var(--main);
    border: 1px solid var(--main);
    transition: 0.3s
}

.dark .chat .button:hover {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main);
    top: 0px;
}

.dark .chat .button:active, .dark .chat .button:focus {
    background: var(--darkbg2);
    color: var(--darktext);
    box-shadow: inset 0px 0px 35px var(--main), 0px 0px 5px var(--main2);
    top: 0px;
    transition: 0.1s
}

/********/
/*Text Input Area*/
/********/

/*background and border*/

.chat-log-add {
    background: transparent;
    border-color: var(--main);
}

/*"Join chat" button*/

.dark .chat-log-add form button, .dark .battle-log-add form button, .dark .chat-log-add form button:hover, .dark .battle-log-add form button:hover {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    transition: 0.3s;
    top: 0px
}

.dark .chat-log-add form button:active, .dark .chat-log-add form button:focus, .dark .battle-log-add form button:active,.dark .battle-log-add form button:focus {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2), 0px 0px 5px var(--main2);
    border: 1px solid var(--main);
    color: var(--darktext);
    transition: 0.1s;
    top: 0px
}

/********/
/*Code*/
/********/

code {
    background: linear-gradient(grey, #2b2b2b) !important;
    border: 1px solid grey !important;
    color: #fff !important
}

/********/
/*Longer Code*/
/********/

.readmore.code {
    background: linear-gradient(#686868, #2b2b2b);
    color: #fff;
    border: 1px solid #686868
}

details.readmore summary:after {
    color: grey
}

details.readmore summary:hover:after {
    color: #dbdbdb
}

/********/
/*The Things That Appear When You /dt Something*/
/********/

.dark .utilichart .col {
    color: var(--darktext)
}

.dark .notice .col.statcol em {
    color: var(--darktext2)
}

.dark .notice .col.bstcol em {
    color: var(--darktext2)
}

.dark .notice font[color= "#686868"] {
    color: var(--darktext2)
}

/********/
/*Links*/
/********/

.dark a, a {
    color: #05f
}

.dark a:active, .dark a:visited:active, a:active, a:visited:active {
    color: red
}

.dark a:visited, a:visited {
    color: #a824a8
}

/*tournament links*/

.chat-log a.ilink, .chat-log a.ilink:active {
    color: #05f !important
}

/*links in tournament brackets*/

.tournament-bracket a.ilink, .tournament-bracket a.ilink:hover {
    fill: blue;
    color: blue
}

/*subtle links in infoboxes*/

.dark .infobox a.subtle {
    color: var(--darktext)
}

.infobox a.subtle:hover {
    color: #05f
}

/*links in broadcasts*/

.broadcast-blue a {
    color: #72b9ff !important;
    text-shadow: 0px 0px 5px #000
}

.broadcast-green a {
    color: #84ff84 !important;
    text-shadow: 0px 0px 5px #000
}

.broadcast-red a {
    color: #ff6c6c !important;
    text-shadow: 0px 0px 5px #000
}

/*links in announcements (/wall)*/

.message-announce a {
    color: #89c5ff !important;
    text-shadow: 0px 0px 5px black
}

/*room links in user popups*/

.ps-popup .userdetails .rooms a.ilink {
    color: #05f
}

.ps-popup .userdetails .rooms a.ilink:active, .ps-popup .userdetails .rooms a.ilink.yours:active {
    color: red
}

.ps-popup .userdetails .rooms a.ilink.yours {
    color: #a824a8
}

/********/
/*Broadcasts (the colored blocks with text in them; for example in the room intro of the tournaments room)*/
/********/

.broadcast-blue {
    background: #1e70c1;
    color: white;
}

.broadcast-green {
    background: #2cbb2c;
    color: white
}

.broadcast-red {
    background: #cc1f1f;
    color: white
}

/*buttons in blue broadcasts*/

.dark .broadcast-blue button[name="register"], .dark .broadcast-blue button[name="register"]:hover {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2);
    border: 1px solid var(--main);
    color: var(--darktext);
    text-shadow: none;
    transition: 0.3s;
    top: 0px
}

.dark .broadcast-blue button[name="register"]:active, .dark .broadcast-blue button[name="register"]:focus {
    background: var(--darkbg2);
    box-shadow: inset 0px 1px 3px var(--darktext2), 0px 0px 5px var(--main2);
    border: 1px solid var(--main);
    color: var(--darktext);
    transition: 0.2s;
    top: 0px
}

