@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/*LIGHT MODE*/
/*Important: This section won't work properly without the other section above*/

:root {
    --lightbg: /*[[lightbgkey]]*/;
    --lightbg2: /*[[lightbg2key]]*/;
    --lighttext: /*[[lighttextkey]]*/;
    --lighttext2: /*[[lighttext2key]]*/
}

/****************************/
/****************************/
/*TEXTBOXES*/
/****************************/
/****************************/

.textbox {
    background: var(--lightbg2);
    border: 1px solid var(--lighttext2);
    box-shadow: inset 0px 1px 4px var(--lighttext2);
    color: var(--lighttext);
}

.textbox:hover {
    background: var(--lightbg2);
    border: 1px solid var(--lighttext);
    box-shadow: inset 0px 1.5px 5px var(--lighttext2);
}

.textbox:focus {
    background: var(--lightbg2);
    border: 1px solid var(--lighttext);
    box-shadow: inset 0px 1.5px 5px var(--lighttext2), 0px 0px 4px var(--main2), 0px 0px 4px var(--main2);
}

/****************************/
/****************************/
/*DROP-DOWN BARS*/
/****************************/
/****************************/

select {
    background: var(--lightbg2);
    border: 1px solid var(--main);
    border-radius: 4px;
    color: var(--lighttext);
    transition: 0.3s;
    outline: 0
}

select option {
    background: var(--lightbg2);
}

/****************************/
/****************************/
/*SLIDERS (FOR VOLUME AND EVS)*/
/****************************/
/****************************/

/********/
/*Track*/
/********/

input[type="range"]::-webkit-slider-runnable-track, input[type="range"]:hover::-webkit-slider-runnable-track {
    box-shadow: none;
    background: var(--lightbg2);
    border-radius: 5px;
    border: 1px solid var(--main);
}

/********/
/*Thumb*/
/********/

input[type=range]::-webkit-slider-thumb, input[type=range]:hover::-webkit-slider-thumb {
    background: var(--lightbg2);
    box-shadow: none;
    border: 1px solid var(--main)
}

/****************************/
/****************************/
/*MAIN MENU*/
/****************************/
/****************************/

/********/
/*Background*/
/********/

.menugroup {
    background: var(--lightbg)
}

/********/
/*Buttons*/
/********/

.menugroup .button {
    background: transparent !important;
    color: var(--lighttext);
    border: 1px solid var(--main) !important;
    box-shadow: inset 0px 0px 4px var(--main) !important;
    text-shadow: none !important;
    transition: 0.3s
}

.menugroup .button:hover, .menugroup .button:active, .menugroup .button:focus {
    background: var(--lightbg2) !important;
    border: 1px solid var(--main);
    box-shadow: inset 0px 0px 4px var(--main), 0px 0px 5px var(--main), 0px 0px 15px var(--main) !important;
    top: 0px !important
}

.menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active, .menugroup .button.disabled:focus {
    background: var(--lightbg2) !important;
    border: 1px solid var(--main) !important;
    color: var(--lighttext) !important;
    box-shadow: inset 0px 0px 4px var(--main), 0px 0px 5px var(--main), 0px 0px 15px var(--main) !important;
    opacity: 0.6
}

/********/
/*Cancel And Reconnect And Add Game Buttons*/
/********/

.menugroup .buttonbar button {
    background: var(--lightbg2);
    box-shadow: 0px 1px var(--main);
    border: 1px solid var(--main);
    color: var(--lighttext);
    text-shadow: none;
}

.menugroup .buttonbar button:hover {
    background: var(--lightbg2);
    box-shadow: none;
    border: 1px solid var(--main);
    color: var(--lighttext);
    position: relative;
    top: 1px;
}

.menugroup .buttonbar button:active, .cancel.buttonbar button:focus {
    background: var(--lightbg2);
    box-shadow: inset 0px 2px 4px var(--lighttext2);
    border: 1px solid var(--main);
    color: var(--lighttext);
    position: relative;
    top: 2px;
}

/********/
/*Text*/
/********/

.menugroup label {
    color: var(--lighttext);
    text-shadow: none
}

/********/
/*Format And Team Selectors*/
/********/

/*button stuff*/

.select.teamselect, .select.formatselect, .select.teamselect:focus, .select.formatselect:focus {
    background: var(--lightbg2);
    box-shadow: 0px 2px var(--main);
    border: 1px solid var(--main);
    color: var(--lighttext);
    text-shadow: none;
    top: 0px
}

.select.teamselect:hover, .select.formatselect:hover {
    box-shadow: 0px 1px var(--main);
    position: relative;
    top: 1px;
    background: var(--lightbg2);
    border: 1px solid var(--main);
}

.select.teamselect:active, .select.formatselect:active {
    box-shadow: inset 0px 2px 4px var(--lighttext2);
    position: relative;
    top: 2px;
    border: 1px solid var(--main);
    background: var(--lightbg2);
}

.select.formatselect.preselected {
    background: var(--lightbg2);
    box-shadow: inset 0px 2px 4px var(--lighttext2);
    border: 1px solid var(--main);
    color: var(--lighttext);
    text-shadow: none;
    opacity: 0.7;
    position: relative;
    top: 2px
}

/*arrow*/

.select:after {
    color: var(--lighttext) !important
}

/*disabled*/

.teamselect:disabled, .formatselect:disabled, .teamselect:disabled:hover, .formatselect:disabled:hover, .teamselect:disabled:active, .formatselect:disabled:active {
    background: var(--lightbg2);
    box-shadow: inset 0px 2px 4px var(--lighttext2);
    border: 1px solid var(--main);
    color: var(--lighttext);
    text-shadow: none;
    position: relative;
    top: 2px;
    opacity: 0.7
}

/*text for the above*/

.teamselect.preselected strong {
    color: var(--lighttext)
}

/********/
/*Spectator Toggle*/
/********/

.checkbox:hover {
    background: transparent !important;
    color: var(--lighttext) !important;
    box-shadow: inset 0px 0px 80px var(--main)
}

/****************************/
/****************************/
/*TOP BAR*/
/****************************/
/****************************/

/********/
/*Background*/
/********/

.header {
    background: var(--lightbg)
}

/********/
/*Tabs*/
/********/

/*normal state*/

.tabbar a.button {
    transition: 0.3s;
    background: var(--lightbg2);
    box-shadow: none;
    border-color: var(--main);
    color: var(--lighttext);
