/****************************/
/*BATTLE AREA*/
/****************************/
/****************************/

/********/
/*Tooltips (The Things That Appear When You Hover Over Stuff)*/
/********/

/*normal state*/

#tooltipwrapper .tooltip {
    background: var(--lightbg2);
    border: 1px solid var(--main);
    color: var(--lighttext);
    border-radius: 5px;
}

/*locked state*/

#tooltipwrapper.tooltip-locked .tooltip {
    background: var(--lightbg2);
    border: 2px solid var(--main);
    color: var(--lighttext);
    border-radius: 5px;
    box-shadow: 0px 0px 13px black
}

/********/
/*"Resume" Button*/
/********/

.playbutton button {
    background: var(--lightbg2);
    box-shadow: none;
    border: none;
    color: var(--lighttext);
    text-shadow: none;
    position: relative
}

.playbutton button::before {
    content: "";
    position: absolute;
    top: 0;
    left: -2.5%;
    width: 105%;
    height: 8%;
    background: var(--main);
    transform: scaleX(0);
    transition: 0.4s ease-in;
    transform-origin: left
}

.playbutton button:hover::before {
    background: var(--main);
    transform: scaleX(1)
}

.playbutton button::after {
    content: "";
    position: absolute;
    top: 92%;
    left: -2.5%;
    width: 105%;
    height: 8%;
    background: var(--main);
    transform: scaleX(0);
    transition: 0.4s ease-in;
    transform-origin: left
}

.playbutton button:hover::after {
    background: var(--main);
    transform: scaleX(1)
}
}

